css:
@-moz-document domain("www.blast-info.fr") {
/* BODY */
body, .bg-white, .story-container {
    color: #f3f3f5 !important;
    background-color: #323232 !important;
}
section .main-container{ background-color: #282828 !important; }
.story-content a{ color: #ff5649 !important; }
.story-content a:hover{ color: #8ec6ff !important; }
.story-content .blockquote, .story-content blockquote{ border-color:#ffffff; }
.story-content blockquote p{ border-color:#ffffff; }
.list-hero>li { filter: invert(1); color: #000000; }

section .text-dark{ color:#f3f3f5 !important; }
.stories .author-meta{ background-color:#999999 !important; }
section .story-widget, .story-list-item, .stories-flash{ background-color:#666666 !important; }
.story-related-body li a{ color:#f3f3f5 !important; }
section.bg-yellow{ background-color: #986909!important; }
section.bg-yellow a:hover{ filter:invert(1); }
section *{ border-color:#ffffff !important; }
section .sticky-top *{ border-color:#666666 !important; }
section .sticky-top .navigation-items{ background-color:#999999 !important; }
.author-col{}
section .btn-outline{ color:#f3f3f5; background-color: #986909!important; }
section .btn-outline:hover{ opacity:1; }
section .table{ color: #f3f3f5 !important; }
section input.form-control{ background-color: #a6a6a6 !important; }
.subscription-option-price.selected {
    color: #fff;
    background-color: #0c67bd !important;
}
.btn-danger{ background-color: #e20404 !important; }
.btn-danger:hover{ filter: invert(1); }

/* TAGS */
.btag:not([class*=btag-politique]){ background-color: #ff5649; color: #ffffff; }
.btag[class*=btag-politique]{ background-color: #246dff !important; }
.btag[class*=btag-politique] a{ color: #cccccc !important; }
.btag[class*=btag-politique] a:hover{ color: #ffffff !important; }

/* HEADER */
.main-navbar .main-navbar-container, .navbar-search, .navbar-search input, .navbar-title{
    color: #f3f3f5 !important;
    background-color: #888888 !important;
}
.navbar-search>div:before { border: 0; }
.navbar-baseline{display:none !important;}

/* SIDE PANEL */
.sidebar-panel .sidebar-menu{
    background-color: #888888 !important;
}
.sidebar-panel .sidebar-menu *{
    color: #f3f3f5 !important;
}
.sidebar-panel .sidebar-menu h2{
    background-color: #ea5b50 !important;
}
.sidebar-panel .sidebar-menu .bg-yellow{
    background-color: #4db51e !important;
}

/* FOOTER */
.footer .footer-navigation{ background-color: #6b0082; }
.footer .footer-navigation .text-white{ color: #f3f3f5 !important; }
.footer .footer-navigation .footer-links a { color: #f3f3f5 !important; }
.footer .newsletter{ background-color: #093f88; }
.footer .newsletter input:hover {
    background-color: #a6a6a6 !important;
}
.footer .newsletter .bg-yellow{
    color: #000000 !important;
    background-color: #d230ab !important;
}
.footer .copyright{ background-color: #000000; color:#f3f3f5; }
.footer .copyright .text-dark{ color:#f3f3f5 !important; }


}
