/* i really want this to be global */

/* Chats */
.bn .bl { /* Pre-chat loading background */
  background: transparent;
}
.aM97s { /* Top level background of chats */
  background-color: transparent;
}
.WQKmIb .Bl2pUd { /* Background of chats (this superseeds the next) */
  background-color: transparent;
}
.bzJiD, .Kk7lMc-ae3xF { /* Background of chats */
  background-color: transparent;
}
.QQodff .FvYVyf { /* Message dates */
  color: #cde3ff;
  text-shadow: 0 1px 2px rgb(0 0 0 / 25%)
}

.vIO7af { /* Group name bar */
  background-color: rgba(255, 255, 255, 0.2);
}
.UDyRYe { /* Row containing group chat files, tasks etc bar */
  background-color: rgba(255, 255, 255, 0.2);
  border-bottom: solid 1px rgba(255, 255, 255, 0.4);
}
.c0lDWe { /* Group chat files, tasks etc bar */
  background-color: transparent;
}
.nk7hB, .wWf0Bc, .nk7hB .cAW0Vc { /* Group Name */
  color: rgba(255,255,255,1);
  text-shadow: 0 1px 2px rgb(0 0 0 / 65%)
}
.sNyyib .x1jjEb { /* Number of group members */
  color: rgba(255,255,255,1);
}
.zmXnlc .iNfgyc, .S6YNQd { /* Icons */
  fill: white;
}

/* Messages */
.McQwEc { /* Image part of messages (contains the text part too) */
  background-color: transparent;
}
.oGsu4 { /* Text part of messages */
  background-color: rgba(242,245,245,0.8);
  border-radius: 5px;
  /* max-width: 800px; */
}
.oGsu4:hover { /* Text part of messages */
  background-color: rgba(242,245,245,1);
}
.nF6pT.FwR7Pc .iKCcE, .VYsRpc:hover { /* Message text part on hover */
  background-color: transparent;
}
.XbbXmb.zzVqCe .nF6pT, .dsoUjb .jGyvbd .jO0Dzb { /* Grouped messages subsequent parts */
  background-color: transparent;
}

/* New message bar */
.XganBc{
  background-color: transparent;
}
.WQKmIb .XganBc {
    padding-left: 60px;
    padding-right: 30px;
}
.WQKmIb .XT3Vq {
  padding: 8px;
  background: rgba(241,243,244,0.749);
  border-radius: calc(48px/2);
}
.XT3Vq {
  margin-bottom: inherit;
}
.zmXnlc .WQKmIb .XganBc {
  padding-bottom: 20px;
}
