css:
@-moz-document domain("soopercool101.github.io") {
:root {
	--bg-dark: hsl(230, 17%, 18%);
	--bg-dark-1: hsl(230, 17%, 20%);
	--bg-dark-2: hsl(230, 17%, 23%);
	--bg-dark-2b: hsl(230, 17%, 28%);
	--bg-dark-3: hsl(230, 17%, 36%);
	--text-white: hsl(223, 2%, 83%);
	--text-white-2: hsl(0, 2%, 69%);
	--border-color: hsl(230, 8%, 27%);
	--border-color-2: hsl(230, 8%, 65%);
	--link-color: hsl(210, 42%, 67%);
	--link-color-2: hsl(210, 47%, 67%);
}

body, div.header { 
    background-color: var(--bg-dark);
	color: hsl(0, 2%, 81%);
	border-color: var(--border-color);
}

::-webkit-scrollbar { width: 14px; height: 12px;}
/* Selectable bar */
::-webkit-scrollbar-thumb { background: hsl(230, 4%, 39%) !important; border-radius: 10px;}
/* Background */
::-webkit-scrollbar-track-piece { background: hsl(230, 8%, 28%) !important; border-radius: 10px !important;}
/* Nothing visible? */
::-webkit-scrollbar-corner { background: hsl(230, 6%, 34%) !important;}
::-webkit-scrollbar-resizer { background: hsl(230, 8%, 32%)!important;}


/* Header bar fill */
.navpath ul, .sm-dox {
	background: var(--bg-dark-2);
	border-color: var(--border-color)
}

#titlearea {
	border-color: var(--bg-dark-3)
}
/* Header bar text */
.navpath li.navelem a, .sm-dox a, .sm-dox a:focus, .sm-dox a:hover, .sm-dox a:active {
	color: var(--text-white);
	text-shadow: none;
}

div.headertitle {
	background-color: var(--bg-dark);
}

h2.groupheader {
	border-color: var(--border-color-2);
}
#projectalign, #projectlogo {
	background-color: #0000 !important;
}

#MSearchBox, #MSearchField {
	background-color: var(--bg-dark-3);
}

.MSearchBoxActive #MSearchField {
	color: var(--text-white)
}
/* Image background so class diagram can be read on black bg */
div.center img {
	background-color: hsl(0, 0%, 47%);
}
/* Link color */
a {
	color: var(--link-color)
}
a.el:visited, a.code, a.code:visited, a.line, a.line:visited {
	color: hsl(208, 51%, 70%);
}

h2.groupheader {
	color: var(--text-white);
}

/* Method descriptor */
.mdescLeft, .mdescRight {
	color: var(--text-white-2)
}
/* Table separator */
.memSeparator {
	border-color: var(--border-color)
}
/* Method header */
.memtitle {
	background: var(--bg-dark-2);
	border: none;
}

/* Method header */
.memproto, dl.reflist dt {
	border: none;
	background-color: var(--bg-dark-2)
}

/* Method text shadow */
.memproto, dl.reflist dt {
	text-shadow: none;
}
/* Type doc fill */
.memdoc, dl.reflist dd {
	background-color: var(--bg-dark-1)
}

/* Table fill */
td {
	background-color: var(--bg-dark) !important;
	color: var(--text-white);
}

/* Member Data name */
.memname, td.mlabels-left, .memname td, td.mlabels-right {
	background-color: #0000 !important;
}
.memItemLeft, .memTemplItemLeft {
	color: var(--text-white)
}

/* Code fragment */
div.fragment, .memdoc, dl.reflist dd{ 
border-color: var(--border-color)}
.fragment {
	color: var(--text-white);
	background-color: var(--bg-dark-2) !important;
}

/* Code fragment line numbers divider */
span.lineno {
	border-color: hsl(209, 30%, 50%)
}
/* Code fragment line numbers bg */
span.lineno {
	background-color: var(--bg-dark-3);
}

/* Parameter name */
.paramname {
	color: hsl(0, 58%, 58%)
}
/* Type color i.e. int */
span.keywordtype {
	color: hsl(0, 50%, 54%)
}

/* Keyword color i.e. new */
span.keyword {
	color: hsl(120, 64%, 39%)
}

/* String color */
span.stringliteral {
	color: hsl(237, 82%, 75%)
}
}

@-moz-document domain("soopercool101.github.io") {
/* Search results */

#MSearchResultsWindow {
	background-color: var(--bg-dark-2);

}

span.SRScope, .SRSymbol, a.SRScope {
	font-family: Arial;
	font-size: 130%
}

/*  Result name (function or attribute) */
.SRSymbol{
	color:var(--link-color)
}

/* Result name (multiple namespace matches) */
a.SRScope {
	color:var(--link-color-2);
}

iframe#MSearchResults {
	width: 87ex;
	height: 35em;
}
}
