css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("pandora.com") {

#advertisement, #promotional_gallery, #footer {
    display: none !important;
}

body {
    background: #133B6B url(http://i30.tinypic.com/294nluh.jpg) repeat-x scroll 0 0 !important;
}

#container {
    background: transparent url(http://i29.tinypic.com/2r5un3k.jpg) no-repeat scroll 0 0 !important;
}

img[src="http://www.pandora.com/images/no_album_art.jpg"] {
    width: 0 !important;
    height: 0 !important;
    padding: 40px !important;
    background: url(http://i28.tinypic.com/zl5r6.jpg) no-repeat scroll 0 0 !important;
}

}

@-moz-document url-prefix("http://www.pandora.com/music") {

body {
    background: #F2F6F9 url(http://www.pandora.com/images/background_backstage_top.jpg) no-repeat scroll 0 0 !important;
}

#leaderboard_container, #medium_rectangle_container, #bottom_leaderboard_container, iframe {
    visibility: hidden !important;
}

#medium_rectangle_container {
    display: none !important;
}

}
