css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("himote.in") {

  body {
    background: #f9f6f0 !important;
    color: #222222 !important;
    font: normal normal normal .9em/1.66em Meiryo, sans-serif !important;
  }

  .partsHeading {
    background: #f9f6f0 !important;
    text-align: center !important;
    padding: .5em !important;
    color: #66aa11 !important;
    font-size: 1em !important;
  }
  .partsHeading p.link {
    top: 0 !important;
    right: 0 !important;
    padding: .5em !important;
  }

  /* {{{ */
  #topBanner                              { display: none !important; }
  #pr                                     { display: none !important; }
  #pr.homeNineTable                       { display: block !important; }
  #sideBanner                             { display: none !important; }
  iframe[src*='http://himote-chat.appspot.com/'] { display: none !important; }
  /* }}} */

  /* Link {{{ */
  a:link    { color: #66aa11 !important; text-decoration: none !important; }
  a:visited { color: #999690 !important; text-decoration: none !important; }
  a:hover   { color: #393630 !important; text-decoration: none !important; }
  a:active  { text-decoration: none !important; }
  /* }}} */

  /* Form {{{ */
  form input.input_text {
    padding: 2px !important;
    border: 1px solid #999690 !important;
  }
  form input.input_text:focus,
  form input.input_text:hover {
    border-color: #66aa11 !important;
  }
  form input.input_image { }
  form input.input_submit {
    padding: 2px !important;
    background-image: url("data:image/png;base64,") !important;
    background-position: left top !important;
    background-repeat: repeat-x !important;
    font-weight: bold !important;
    border-top: 3px double #cfccc6 !important;
    border-right: 3px double #999690 !important;
    border-bottom: 3px double #999690 !important;
    border-left: 3px double #cfccc6 !important;
  }
  form input.input_submit:focus,
  form input.input_submit:hover {
    color: #990000 !important;
    cursor: pointer !important;
  }
  form textarea {
    border: 1px solid #999690 !important;
  }
  form textarea:focus,
  form textarea:hover {
    border-color: #66aa11 !important;
  }
  /* }}} */

  /* Layout {{{ */
  #Container { width: 80em !important; margin: auto !important; } 
  #Header { position: relative !important; }
  #Top { }
  #LayoutA #Left   { float: left !important; width: 24em !important; padding: .5em !important; }
  #LayoutA #Center { float: right !important; width: 54em !important; padding: .5em !important; }
  #LayoutB #Left   { float: left !important; width: 19em !important; padding: .5em !important; }
  #LayoutB #Center { float: right !important; width: 59em !important; padding: .5em !important; }
  #LayoutC #Center { width: auto !important; padding: .5em !important; }
  #Footer { width: 100% !important; height: auto !important; }
  /* }}} */

  /* #Body {{{ */
  #Body { background: transparent !important; }
    /* #Body > #Container {{{ */
    #Container { background: transparent !important; }
      /* #Body > #Container > #Header {{{ */
      #Header { border-bottom: 1px solid #222222 !important; padding: 0 .5em !important; }
        /* #Body > #Container > #Header > globalNav {{{ */
        #globalNav, #globalNavBefore {
          width: 100% !important;
          position: relative !important;
          background: none !important;
        }
        #globalNav h1 { }
        #globalNav h1 a {
          margin: 0 0 0 0 !important;
          padding: 0 6px !important;
          display: block !important;
          float: left !important;
          width: auto !important;
          height: 1.5em !important;
          line-height: 1.5em !important;
          white-space: nowrap !important;
          background-image: url("data:image/png;base64,") !important;
          background-position: left top !important;
          background-repeat: no-repeat !important;
          height: 72px !important;
          width: 180px !important;
        }
        #globalNav ul {
          margin: 0 0 0 auto !important;
          width: 741px !important;
          height: 30px !important;
          background-image: url("data:image/png;base64,") !important;
          background-position: left top !important;
          background-repeat: no-repeat !important;
          position: relative !important;
        }
        #globalNav ul li {
          position: absolute !important;
          top: 0px !important;
        }
        #globalNav ul li#globalNav_1 { left: 0px !important; }
        #globalNav ul li#globalNav_2 { left: 83px !important; }
        #globalNav ul li#globalNav_3 { left: 165px !important; }
        #globalNav ul li#globalNav_4 { left: 247px !important; }
        #globalNav ul li#globalNav_5 { left: 329px !important; }
        #globalNav ul li#globalNav_6 { left: 411px !important; }
        #globalNav ul li#globalNav_7 { left: 493px !important; }
        #globalNav ul li#globalNav_8 { left: 575px !important; }
        #globalNav ul li#globalNav_9 { left: 657px !important; }
        #globalNav ul li#globalNav_1 a:hover, li#globalNav_1 a:active { background-position: 0 0 !important; }
        #globalNav ul li#globalNav_2 a:hover, li#globalNav_2 a:active { background-position: -83px 0 !important; }
        #globalNav ul li#globalNav_3 a:hover, li#globalNav_3 a:active { background-position: -165px 0 !important; }
        #globalNav ul li#globalNav_4 a:hover, li#globalNav_4 a:active { background-position: -247px 0 !important; }
        #globalNav ul li#globalNav_5 a:hover, li#globalNav_5 a:active { background-position: -329px 0 !important; }
        #globalNav ul li#globalNav_6 a:hover, li#globalNav_6 a:active { background-position: -411px 0 !important; }
        #globalNav ul li#globalNav_7 a:hover, li#globalNav_7 a:active { background-position: -493px 0 !important; }
        #globalNav ul li#globalNav_8 a:hover, li#globalNav_8 a:active { background-position: -575px 0 !important; }
        #globalNav ul li#globalNav_9 a:hover, li#globalNav_9 a:active { background-position: -657px 0 !important; }
        #globalNav ul a {
            display: block !important;
            text-indent: -9999px !important;
            height: 30px !important;
            width: 83px !important;
        }
        #globalNav ul a:hover, #globalNav ul a:active {
            background-image: url("data:image/png;base64,") !important;
        }
        /* }}} #Body > #Container > #Header > #globalNav */
      /* }}} #Body > #Container > #Header */
      /* #Body > #Container > .localNav {{{ */
      .localNav {
        width: 100% !important;
        position: relative !important;
        background: none !important;
        border: none !important;
      }
      .localNav a {
        margin: 0 0 0 0 !important;
        padding: 0 8px !important;
        display: block !important;
        float: left !important;
        width: auto !important;
        height: 2em !important;
        line-height: 2em !important;
        text-indent: 0px !important;
        white-space: nowrap !important;
      }
      .localNav a:hover, .localNav a:active { background: none !important; }
      .localNav ul {
        position: relative !important;
        left: 50% !important;
        float: left !important;
      }
      .localNav ul li {
        position: relative !important;
        left: -50% !important;
        float: left !important;
        width: auto !important;
      }
      /* }}} #Body > #Container > .localNav */

      /* #Body > #Container > #LayoutA {{{ */
      #LayoutA { }
      #LayoutA #Top { padding: .5em !important; }
      #LayoutA #Top .infoBox,
      #LayoutA #Top .descriptionBox { margin: auto !important; }
      #LayoutA #Top .dparts { border: 1px solid #999690 !important; }
      #LayoutA #Left { }
      #LayoutA #Left .dparts,
      #LayoutA #Left .parts { border: 1px solid #999690 !important; }
      #LayoutA #Center { }
      #LayoutA #Center .dparts,
      #LayoutA #Center .parts { border: 1px solid #999690 !important; }
      /* }}} #Body > #Container > #LayoutA */

      /* #Body > #Container > #LayoutB {{{ */
      #LayoutB { }
      #LayoutB #Left { }
      #LayoutB #Center { }
      #LayoutB #Center .dparts,
      #LayoutB #Center .parts { border: 1px solid #999690 !important; }
      /* }}} #Body > #Container > #LayoutB */

      /* #Body > #Container > #LayoutC {{{ */
      #LayoutC { }
      #LayoutC #Center { }
      #LayoutC #Center .dparts,
      #LayoutC #Center .parts { border: 1px solid #999690 !important; }
      /* }}} #Body > #Container > #LayoutC */

      /* #Body > #Container > #Footer {{{ */
      #Footer {
        border-top: 1px solid #222222 !important;
        padding: .5em !important;
        background: none !important;
        color: #222222 !important;
      }
      #Footer p { text-align: center !important; padding: 0 !important; }
      #Footer font { color: #222222 !important; }
      /* }}} #Body > #Container > #Footer */

    /* }}} #Body > #Container */

  /* }}} #Body */

}
