css:
@-moz-document url-prefix("https://eatingdisordercentral.com") {
html, body {
    color:#5b5b5b;
}
html > body {
    background-color:#0d0d0d;
}

#container {
    background-color:#0f0f0f;
}
#header {
    background-color:#0f0f0f;
}
#subheader {
    background-color:#0d0d0d;
}
#nav{
    background-color:#201f1f !important;
    
}

#forum #foro, #memberlist #miembros, #calendar #calendario, #search #busqueda, #help #ayuda {
    font-size: 13px;
    font-weight: 400;
    list-style: none;
    display: inline-block;
    background: #444;
    padding: 12px 25px 10px 25px;
    border-radius: 1px 1px 0px 0px;
}

#subtabs {
    width: 90%;
    min-width: 90%;
    max-width: 90%;
    margin: 0px auto;
    padding: 0px;
    background: #2f2f2f;
}

html, body, p, code:before, table {
    margin: 0;
    padding: 0;
    font-family: Tahoma, Verdana, Arial, Sans-Serif;
    font-size: 14px;
    color: #f0f2fc;
}
.lightblue {
    background-color:#1b1b1b !important;
}
.trow_sticky {
    background-color:#393939 !important;
}

.trow_sep {
    background-color:#393939 !important;
}

#forum #foro a, #memberlist #miembros a, #calendar #calendario a, #search #busqueda a, #help #ayuda a {
    color: #ffffff;
}

.trow1 {
    padding: 15px 10px;
    background: #101010;
}

.trow2 {
    padding: 15px 10px;
    background: #0a0a0a;
}
table {
    color: #fbfbfbc2;
    font-size: 16px;
}
.subs_button {
    margin: 0px;
    padding: 8px 8px 6px 8px;
    background: #49a5de;
    border-radius: 3px;
}
.postbit_container {
    background: #1e1e1e;
    padding: 15px;
    box-sizing: border-box;
}
.forum_desc {
    font-family: 'Hind',sans-serif;
    font-size: 16px;
    font-weight: 400 !important;
    color: #eee;
    text-align: left;
}
blockquote cite span {
    float: left;
    font-weight: bold;
    font-size: 15px;
    color: #e6e6e6;
    margin-right: 5px;
}

.userunique a:link, .userunique a:visited {
    color: #ded7d1;
    text-decoration: none;
    transition: all 0.3s ease;
}

blockquote cite {
    font-weight: bold;
    border-bottom: 1px solid rgba(0,0,0,0.055);
    font-style: normal;
    display: block;
    padding: 10px 10px 6px 10px;
    margin: -10px -10px 10px -10px;
    background: #191919 !important;
}
blockquote {
    border-top: 1px solid #666;
    border-right: 1px solid #666;
    border-bottom: 1px solid #666;
    border-left: 2px solid #666;
    margin: 0;
    background: #2b2b2b;
    padding: 10px;
}
textarea {
    font-family: 'Hind',sans-serif;
    font-size: 13px;
    background: #151515;
    color: #fffcfc;
    border: 1px solid #636363;
    padding: 2px;
    line-height: 1.4;
    outline: 0;
    border-radius: 2px;
}

input {
    -webkit-writing-mode: horizontal-tb !important;
    text-rendering: auto;
    color: #151515;
    letter-spacing: normal;
    word-spacing: normal;
    text-transform: none;
    text-indent: 0px;
    text-shadow: none;
    display: inline-block;
    text-align: start;
    appearance: auto;
    cursor: text;
    margin: 0em;
    font: 400 13.3333px Arial;
    padding: 1px 2px;
    border-width: 2px;
    border-style: inset;
    
}

   .tfoot {
    border-top: 1px solid #2b2b2b;
    padding: 6px;
    background: #0e0e0e;
    color: #464646;
}


.pagination .pagination_current {
    background: #686868;
    color: #f4f4f4 !important;
    border: none;
    font-weight: 600;
    border-radius: 2px;
}

.fd_multipage a:link, .fd_multipage a:visited {
    position: relative;
    top: -1px;
    opacity: 0.3;
    color: #fdf9f9;
    background: rgba(0,0,0,0.05);
    font-size: 11px;
    font-weight: 400;
    text-decoration: none;
    padding: 1px 5px 0px 4px;
    margin: 0px;
    border-radius: 2px;
    transition: all 0.3s ease;
}

.postbit_right {
    width: 100%;
    padding: 8px;
    box-sizing: border-box;
    display: table-cell;
    vertical-align: top;
    color: #f9f9f9;
}
.p_container {
    width: 100%;
    display: table;
    table-layout: fixed;
    border-collapse: collapse;
    border-spacing: 0;
    margin-bottom: 15px;
    background: #111;
}
.forum_on {
    background-color:#868686 !important;
}
.forum_off {
    background-color:#4f4f4f;
}

input {
    -webkit-writing-mode: horizontal-tb !important;
    text-rendering: auto;
    color: #bdbdbd;
    letter-spacing: normal;
    word-spacing: normal;
    text-transform: none;
    text-indent: 0px;
    text-shadow: none;
    display: inline-block;
    text-align: start;
    appearance: auto;
    cursor: text;
    margin: 0em;
    font: 400 13.3333px Arial;
    padding: 1px 2px;
    border-width: 2px;
    border-style: inset;
}

original_title#foro {
    color: #f66;
}

.menu ul li {
    font-size: 13px;
    font-weight: 400;
    list-style: none;
    display: inline-block;
    background:#202020;
    padding: 12px 25px 10px 25px;
    margin-right: 2px;
    border-radius: 1px 1px 0px 0px;
}

a:link, a:visited {
    color: #738898;
    text-decoration: none;
}
.menu{
    background-color:#000;
}

.navigation a:link, .navigation a:visited {
    color: #fbfbfb;
    text-decoration: none;
    transition: all 0.3s ease;
}
.wrapper{
    background-color:#000;
    
}
.forum_title { color: #fff !important; font-family: Arial, Helvetica, sans-serif; }




#tabs {
    background:#222 !important;
}
.thead {
    background:#1a1a1a !important;
}
.pms {
  background:#ee2c5d !important;
}

.newth_button a:hover, .newth_button a:active {
    background:#5c9f46 !important;
}
.newth_button a:link, .newth_button a:visited {
    background:#4eb79c !important;
}
}

