@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix(http://images.google.), 
url-prefix(http://www.google.com/imghp?)
{




/* TOP MENU. GOOGLE SERVICES WITH ICONS */


/* Translate */

#gbar .gb1[href^="http://translate.google."]:hover, 
#gbi .gb2[href^="http://translate.google."]:hover
{
	background: 
		url(data:image/png;base64,) 
		no-repeat 50% 50% !important;
}


/* Directory */

#gbar .gb1[href*="/dirhp?"]:hover, 
#gbi .gb2[href*="/dirhp?"]:hover, 
#gbar .gb1[href*="cat=gwd/Top"]:hover, 
#gbi .gb2[href*="cat=gwd/Top"]:hover	/* Directory */
{
	background: 
		url(data:image/png;base64,) 
		no-repeat 50% 50% !important;
}



/* More */

#gbar .gb1[href*="/options/"]:hover, 
#gbi .gb2[href*="/options/"]:hover
{
	background: 
		url(data:image/png;base64,) 
		no-repeat 50% 50% !important;
}




/* TOP MENU. GOOGLE ACCOUNT WITH ICONS */


/* Web History */

#guser a[href*="/history/?"]:hover
{
	background: 
		url(data:image/png;base64,) 
		no-repeat 50% 50% !important;
}


/* Manage Account */

#guser a[href*="/accounts/ManageAccount"]:hover
{
	background: 
		url(data:image/png;base64,) 
		no-repeat 50% 50% !important;
}



/* Preferences */

#guser a.gb3[href="/preferences?hl=en"]:hover
{
	background: 
		url(data:image/png;base64,) 
		no-repeat 50% 50% !important;
}



/* Sign Out */

#guser a[href*="/accounts/Logout"]:hover
{
	background: 
		url(data:image/png;base64,) 
		no-repeat 50% 50% !important;
}


/* Sign In */

#guser a[href*="/accounts/Login"]:hover
{
	padding-left: 79px !important;
	right: 2px !important;
	background: 
		url(data:image/png;base64,) 
		no-repeat 0% 50% !important;
}



} /* END OF MOZ-DOCUMENT */
