css:
@-moz-document domain(podio.com) {
/* BRAND BY HAND ADD-ON STYLING: NAVIGATION */
 .inner a.logo {
    background-image: url(https://brandbyhand.dk/wp-content/uploads/2021/02/sandy-bbh-logo_aftryk.png) !important;
    background-size: 22px;
    background-position: 8px 8px !important;
    width: 40px !important;
}
nav .space-switcher-wrapper.highlight, #header-global .nav-box>.inner, #header-global .nav-box>.inner .user-functions, #header-global, #header-global .podio-search-box, .left-nav, nav .space-switcher .space-switcher-sidebar, nav .left-nav .org-navigation>li:last-child .header {
    background: #4C5C64 !important;
}
nav .left-nav .org-navigation>li:last-child .header {
    border-bottom: solid;
}
nav .space-switcher-wrapper.highlight {
    margin-right: 15px;
}
#header-global .nav-box>.inner .user-navigation>li.navigation-link.calendar, #header-global .nav-box>.inner .user-navigation>li.navigation-link.contacts, #header-global .nav-box>.inner ul.user-functions li.podio-chat.navigation-link {
    display: none;
}
li.navigation-link.inbox .PodioUI__Notifications {
    right: 10px !important;
}
li.navigation-link.inbox.empty .Icon svg {
    fill: #fefefe !important;
}
.left-nav .org-navigation>li .header div {
    color: #fefefe;
}
li.navigation-link.inbox.empty .Icon svg {
    fill: #000000 !important;
}
li.navigation-link.inbox, li.navigation-link.inbox > div > div:first-of-type:hover {
    background: #FFCD0B !important;
}
/* BRAND BY HAND ADD-ON STYLING: BODY */
 .global-container .page-width {
    max-width: 100%;
}
.inner.grid-spacer.right li.gridster-block {
    width: 48.5%;
}
.inner.grid-spacer.right .gridster[data-col="2"] {
    left: 50%;
}
div.tasks#wrapper .single-task:hover .image-block .bd .task-summary a {
    color: #72808A;
}
.button-new.green, .flatbutton.green, .spaceswitcher-wrapper .spaceswitcher-button {
    background-color: #FFCD0B !important;
    color: #121212 !important;
    border-color: #FFCD0B !important;
}
.button-new.silver, .podio-dropdown-selector-wrapper.disabled .podio-dropdown-selector {
    background-color: #F7F7F1 !important;
}
.app-view, .page-width {
    max-width: 100%;
}
.dashboard .gridster-block ul.content li.highlight-red {
    border-left-color: #FFCD0B;
}
/* BRAND BY HAND ADD-ON STYLING: TEXT */
 h1, h2, h3, h4, h5, h6 {
    color: #4C5C64 !important;
}
a:link, a:active, a:visited, .header-title > a:active, .header-title > a:link, .header-title > a:visited, .activity-header .selector li.active {
    color: #4C5C64 !important;
}
ul.org-spaces a:link, ul.org-spaces a:visited, ul.org-spaces a:active {
    color: #fefefe !important;
}
/* BRAND BY HAND ADD-ON STYLING: TASKS */
 div.tasks#wrapper .grid-column-1-4, div#wrapper.tasks .grid-column-3-4 {
    width: 100%;
}
div.tasks#wrapper .grid-column-1-4 .right .task-content > div:first-of-type, .task-group-selector, div.tasks#wrapper .task-list h5 {
    display: none;
}
div.tasks#wrapper {
    max-width: 1400px;
    margin: 0px auto;
}
.single-task .due-date-wrapper span.due-date:before {
    content:"Deadline:";
}
div.tasks#wrapper .single-task div.date-with-checkbox {
    width: 250px;
}
div.tasks#wrapper .single-task div.date-with-checkbox span.due-date, div.tasks#wrapper .single-task div.task-summary a.task-link, div.tasks#wrapper .task-detail .task-via {
    color: #121212;
    font-weight: 600;
}
div#new_task_container {
    box-shadow: 0px 3px 20px 0px rgb(0 0 0 / 20%);
    margin-bottom: 35px;
    padding: 10px 20px;
}
div#new_task_container .input-text-composer {
    border: none;
}
div.tasks#wrapper .single-task {
    background: #F7F7F1;
    padding: 14px 20px;
    margin: 10px 0px;
    border-radius: 10px;
    border: none !important;
}
div.tasks#wrapper .sub-navigation .tabs ul>li h5 {
    color: #BAA995;
}
div.tasks#wrapper li.sidebar-menu-button.selected h5 {
    color: #4C5C64 !important;
}
div.tasks#wrapper .single-task:hover {
    background: #eeeee8;
    transition: .4s;
}
.podio-dropdown-selector-wrapper .podio-dropdown-selector, form.new_task textarea#task_description, ul.token-input-list {
    border-color: #735F48 !important;
}
div#new_task_container textarea#task_description {
    min-height: 100px;
}
ul.token-input-list li.token-input-input-token, div.tasks#wrapper .podio-dropdown-selector, ul.token-input-list li.token-input-input-token label {
    padding: 8px !important;
}
.button-new.silver {
    padding: 3px 10px !important;
}
form.new_task input#task_due_time {
    background: none !important;
    padding: 22px 25px;
}
.icon-16-grey-white-address-book {
    background: var(--mastersprite16) 0 -240px no-repeat !important;
}
div.tasks#wrapper .task-detail {
    background: #eeeee8 !important;
    position: relative;
    top: -16px;
}
div.tasks#wrapper .comment-box .comment-composer {
    background: #F7F7F1;
}
div.tasks#wrapper .single-task.expanded {
    background: #eeeee8;
}
.task-detail .comment-box .add-comment {
    color: #735F48;
}
div.tasks#wrapper .task-detail .edit-task-description, form.new_task textarea#task_description, div.tasks#wrapper .task-detail .edit-task-description-inline {
    background-image: url(https://brandbyhand.dk/wp-content/uploads/2021/02/blues-bbh-logo_aftryk.png);
    background-size: 14px;
}
.attach-file-button {
    background-color: #fefefe !important;
    border: none !important;
    color: #424242 !important;
    text-shadow: none !important;
}
/* BRAND BY HAND ADD-ON STYLING: HOME PAGE */
 input[type=submit].flatbutton {
    padding-top: 2px !important;
}
/*Brand by Hand styling END*/

/* Podianga theme style - reference to Eduardo Figueiredo for Grupo Anga. */
 @font-face {
    font-family:'';
}
:root {
    --font-stack-sans: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, "Helvetica Neue", Arial, "Noto Sans", sans-serif, "Apple Color Emoji", "Segoe UI Emoji", "Segoe UI Symbol", "Noto Color Emoji";
    --card-shadow: 0 12px 24px -12px rgba(69, 79, 90, .05), 0 1px 1px 0 rgba(50, 50, 50, .05);
    --card-shadow-hover: 0 12px 24px -12px rgba(69, 79, 90, .10), 0 1px 1px 0 rgba(50, 50, 50, .1);
    --theme-radius: 8px;
    --btn-radius: 6px;
    --pill-radius: 16px;
    --gray-100: #F9F8F7;
    --gray-200: #F3F2F1;
    --gray-300: #E7E5E4;
    --gray-400: #CBD1D8;
    --gray-500: #9FA7B2;
    --gray-600: #585E6F;
    --gray-700: #464958;
    --gray-750: #3E404E;
    /*Space Switcher BG*/
    --gray-800: #363644;
    --gray-900: #1E1C26;
    --green-light: #0BD58F;
    --green: #00C782;
    --green-dark: #00B275;
    --podio-blue-rgb: 74, 135, 182;
    --header-bg: var(--gray-700);
    --header-bg-hover: var(--gray-800);
    --header-sprite: url("https://res.cloudinary.com/zero8/image/upload/Podianga/sprite-nav_xaalgv.svg");
    --mastersprite16: url("https://res.cloudinary.com/zero8/image/upload/Podianga/mastersprite-01_mrgqdc.svg");
    --mastersprite16-02: url("https://res.cloudinary.com/zero8/image/upload/v1580773522/Podianga/mastersprite-02_hkizbf.svg");
    --mastersprite-notification: url("https://res.cloudinary.com/zero8/image/upload/Podianga/mastersprite-notification_yeyamp.svg");
}
html {
    color: #454D58;
    font-family: var(--font-stack-sans);
}
body {
    color: #454D58;
    background: var(--gray-300);
    font-size: 1.4rem;
}
blockquote {
    margin: 0;
}
.markdown table {
    border-radius: var(--theme-radius);
    border-collapse: collapse;
    overflow: hidden;
    font: 1.175rem Consolas, "Liberation Mono", Menlo, Courier, monospace;
}
.markdown table th {
    background: rgba(0, 0, 0, .1);
    padding: 8px;
    font-size: 1.275rem;
}
.markdown table thead tr:hover {
    background: rgba(0, 0, 0, .05);
}
.markdown table td {
    padding: 7px 8px;
    border-top: 2px solid #fff;
}
.markdown table tr {
    background: rgba(0, 0, 0, .04);
}
.markdown table tr:hover {
    background: hsla(35, 100%, 95%, 1);
}
.markdown table th:first-child, .markdown table td:first-child {
    padding-left: 10px;
}
.markdown table th:last-child, .markdown table td:last-child {
    padding-right: 10px;
}
.markdown code {
    padding: .175em .2rem;
    font: 1.15rem Consolas, "Liberation Mono", Menlo, Courier, monospace;
    background-color: rgba(0, 0, 0, .05);
    border-radius: var(--pill-radius);
}
.markdown hr {
    margin: 1.15em 0;
    border-top: 1px dashed var(--gray-400);
    border-bottom: none;
    border-left: none;
}
.header-sub-title, .header-title, .sub-navigation .tabs ul>li h5 {
    font-family: var(--font-stack-sans);
    font-weight: 600;
}
.app-view {
    height: calc(100% - 90px);
    padding: 12px 24px 0;
}
.app-sidebar {
    background-color: var(--gray-100);
    border-bottom-left-radius: var(--theme-radius);
    border-top-left-radius: var(--theme-radius);
}
.app-view__content {
    border-bottom-right-radius: var(--theme-radius);
    border-top-right-radius: var(--theme-radius);
    box-shadow: 0 12px 24px -12px rgba(69, 79, 90, .05), 0 1px 1px 0 rgba(50, 50, 50, .05), -12px 0 12px rgba(50, 50, 50, .01);
}
.app-view__sidebar {
    border-bottom-left-radius: var(--theme-radius);
    border-top-left-radius: var(--theme-radius);
    box-shadow: 0 12px 24px -12px rgba(69, 79, 90, .05), 0 1px 1px 0 rgba(50, 50, 50, .05);
}
.grid-tile {
    border-radius: var(--theme-radius);
    box-shadow: 0 12px 24px -12px rgba(69, 79, 90, .05), 0 1px 1px 0 rgba(50, 50, 50, .05);
}
.app-view-options-balloon-content-component {
    margin: -12px;
}
.app-view-options-balloon-content-component .app-view-options-balloon-content-component__content {
    padding: 8px;
}
.app-view-options-balloon-content-component .app-view-options-balloon-content-component__content>li {
    padding: 2px 6px;
}
/*  HEADER  */
 #header-global, #header-global .podio-search-box {
    background: var(--header-bg);
    border-bottom: 1px solid var(--gray-700);
}
.space-switcher-wrapper.highlight {
    background: var(--gray-750);
}
.space-switcher-wrapper {
    width: 242px;
}
.space-switcher-wrapper:hover {
    background-color: var(--header-bg-hover);
}
.space-switcher .search-result-container {
    background: var(--gray-200);
}
.space-switcher .search-result-container ul {
    max-height: 86vh;
}
#header-global .nav-box>.inner .logo:focus, #header-global .nav-box>.inner .logo:hover, #header-global .nav-box>.inner .navigation-link>a:focus, #header-global .nav-box>.inner .navigation-link>a:hover {
    background-color: var(--header-bg-hover) !important;
}
#header-global .nav-box>.inner .user-navigation>li>a {
    border-right: 1px solid var(--gray-600);
}
#header-global .nav-box>.inner .user-functions {
    background: linear-gradient(to right, rgba(80, 146, 189, 0), var(--header-bg) 10px);
}
.icon-nav {
    background-image: var(--header-sprite);
}
.icon-16-nav-blue-connections {
    background-image: var(--header-sprite);
    background-position:0 -258px;
    width: 20px;
}
.icon-16-nav-white-connections {
    background-image: var(--header-sprite);
    background-position: 0 -274px;
    width: 20px;
}
.inner .logo {
    background-image: url("data:image/svg+xml,%3Csvg width='118' height='13' viewBox='0 0 118 13' fill='none' xmlns='http://www.w3.org/2000/svg'%3E%3Cpath d='M2.6634 10.7885C1.53911 9.66424 0.976959 8.22356 0.976959 6.4665C0.976959 4.70944 1.53911 3.27158 2.6634 2.15294C3.79334 1.03429 5.186 0.474972 6.84137 0.474972C8.59843 0.474972 10.0504 1.13599 11.1973 2.45802L10.4515 2.99192C10.0335 2.49475 9.50803 2.10209 8.87526 1.81395C8.24814 1.52582 7.57018 1.38175 6.84137 1.38175C5.46283 1.38175 4.31312 1.85633 3.39221 2.80548C2.47696 3.75463 2.01933 4.97497 2.01933 6.4665C2.01933 7.94672 2.47978 9.16706 3.40069 10.1275C4.32724 11.088 5.47413 11.5682 6.84137 11.5682C7.54758 11.5682 8.1973 11.4298 8.79052 11.1529C9.38939 10.8761 9.87244 10.5456 10.2397 10.1614V7.39023H5.86679V6.5004H11.2312V10.5428C10.0843 11.8253 8.62103 12.4693 6.84137 12.475C5.186 12.475 3.79334 11.9128 2.6634 10.7885Z' fill='white'/%3E%3Cpath d='M15.1366 12.2462V0.669887H19.4587C20.4756 0.669887 21.3174 0.977797 21.9841 1.59362C22.6507 2.20379 22.9841 3.02299 22.9841 4.05124C22.9841 5.05689 22.6762 5.85633 22.0603 6.44955C21.4502 7.03712 20.6875 7.35068 19.7722 7.39023L23.1197 12.2462H21.9248L18.679 7.45802H16.1281V12.2462H15.1366ZM16.1281 6.55124H19.3739C20.1366 6.55124 20.7553 6.31678 21.2298 5.84785C21.7044 5.37893 21.9417 4.78006 21.9417 4.05124C21.9417 3.32243 21.7044 2.72638 21.2298 2.26311C20.7553 1.79983 20.1366 1.56819 19.3739 1.56819H16.1281V6.55124Z' fill='white'/%3E%3Cpath d='M26.8302 7.80548V0.669887H27.8387V7.78853C27.8387 8.96932 28.1409 9.89305 28.7454 10.5597C29.3556 11.2207 30.22 11.5512 31.3387 11.5512C32.4629 11.5512 33.3274 11.2207 33.9319 10.5597C34.542 9.89305 34.8471 8.96932 34.8471 7.78853V0.669887H35.8556V7.80548C35.8556 9.26311 35.4714 10.4044 34.7031 11.2292C33.9347 12.0484 32.8132 12.458 31.3387 12.458C29.881 12.458 28.7652 12.0456 27.9912 11.2207C27.2172 10.3902 26.8302 9.25181 26.8302 7.80548Z' fill='white'/%3E%3Cpath d='M40.117 12.2462V0.669887H44.4559C45.5294 0.669887 