    box-shadow: 0 1px 5px rgba(0, 0, 0, .10);
}
.badge-component>section .contacts-field-value-component .contacts-field-name {
    margin-left: 1.5rem;
}
.badge-component>section .contacts-field-value-component .contacts-field-extra-contacts {
    background-color: white;
    color: var(--gray-600);
    font-weight: 700;
    padding: 0 6px;
    margin-left: 1.5rem;
    border-radius: var(--btn-radius);
    box-shadow: 0 1px 3px rgba(0, 0, 0, .1);
    font-size: .9em;
    line-height: 26px;
}
.badge-component>footer {
    border-top: 1px solid transparent;
    transition: unset;
    background-color: var(--gray-100);
    border-radius: 0 0 var(--btn-radius) var(--btn-radius);
}
.category-field-value-component:after {
    width: 8px;
    background: linear-gradient(to right, rgba(255, 255, 255, 0) 0, var(--gray-200) 100%);
}
.large-text-field-value-component {
    background: white;
}
/*Progress bar*/
 .progress-field-value-component {
    border-radius: calc(var(--btn-radius)/1.5);
    background: var(--gray-300);
}
.progress-field-value-component__progress {
    background: var(--gray-600);
    border-radius: calc(var(--btn-radius)/1.5);
    opacity: .8;
}
.progress-field-value-component__progress[data-progress="100"] {
    background: var(--green);
}
.card-component>main .progress-field-value-component {
    margin: 0 13px;
    border: none;
}
/*  Table  */
 .items-table .field-type-state-item {
    padding: 1px 8px;
    border-radius: var(--pill-radius);
}
.items-table table tr.even:hover>td, .items-table table tr.odd:hover>td, .items-table table tr.ui-selected>td, .items-table table tr.ui-selecting>td, .items-table table tr:hover>td {
    background-color: var(--gray-100);
}
/*  Baloon  */
 .simple-balloon, .balloon-inner {
    box-shadow: 0 12px 28px -4px rgba(0, 0, 0, 0.2) !important;
    background: #fff;
    border: 2px solid var(--gray-400) !important;
    border-radius: var(--theme-radius) !important;
    min-width: 160px;
}
.simple-balloon .balloon-inner {
    box-shadow: none !important;
    background: #fff;
    border: none !important;
    min-width: unset !important;
}
.balloon.settings-balloon>.arrow {
    border-bottom-color: var(--gray-400);
}
.balloon.below .arrow-border {
    top: 3px;
}
.balloon.settings-balloon .balloon-inner .buttons {
    background-color: var(--gray-200);
    border-radius: 0 0 var(--btn-radius) var(--btn-radius);
}
.simple-balloon.arrow-left.gravity-n:after {
    border-width: 9px;
    border-bottom-color: var(--gray-100);
    left: 32px;
    margin-left: -10px;
}
.simple-balloon.arrow-left.gravity-n:before {
    border-width: 12px;
    margin-left: -14px;
}
.simple-balloon .balloon-content {
    padding: 12px !important;
}
.simple-balloon .balloon-content li:hover {
    background: var(--gray-200);
    border-radius: var(--btn-radius);
}
.app-filters-popover .main {
    margin: -12px;
    height: 100%;
}
.app-header-layout-switcher-options ul.app-settings-dropdown-list li .icon-checked {
    position: absolute;
    right: 12px;
}
ul.app-settings-dropdown-list li {
    margin-left: -4px;
    margin-right: -4px;
    height: 22px;
    line-height: 20px;
    padding: 2px 2px 2px 6px;
}
/*  Notification  */
 .PodioUI__Notifications {
    top: 58px !Important;
    right: -53px !important;
    border-top: 1px solid rgb(204, 204, 204) !Important;
    border-radius: var(--theme-radius) !Important;
    box-shadow: 0px 16px 80px rgba(0, 0, 0, .3) !Important;
}
.PodioUI__Notifications > div:first-child div div:first-child {
    border-radius: var(--pill-radius) 0 0 var(--pill-radius);
}
.PodioUI__Notifications > div:first-child div div:last-child {
    border-radius: 0 var(--pill-radius) var(--pill-radius) 0;
}
.PodioUI__Notifications > div:first-child > div:first-child span:first-child {
    padding: 3px 8px !Important;
    color: #fff !important;
    margin-right: 6px !Important;
    background: var(--green) !Important;
    border-radius: var(--pill-radius) !Important;
}
/*  Modal  */
 body .modal>.content {
    box-shadow: 0 24px 32px 0 rgba(0, 0, 0, .2);
}
body .modal>.content.flat .modal-wrapper {
    border-radius: var(--theme-radius);
}
body .modal>.content.flat .modal-wrapper .modal-header {
    border-radius: var(--theme-radius) var(--theme-radius) 0 0;
    border-bottom: 3px;
    background: var(--gray-600);
}
body .modal>.content.flat .modal-wrapper .modal-header>h2 {
    font-family: var(--font-stack-sans);
    font-weight: 500;
    color: var(--gray-400);
}
body .modal>.content.flat .modal-wrapper .modal-content, body .modal>.content.flat .modal-wrapper .modal-footer {
    border-radius: var(--theme-radius);
}
body>div.modal>.content>.modal-wrapper .modal-confirm-buttons {
    display: flex;
    flex-direction: row-reverse;
}
body>div.modal>.content>.modal-wrapper .modal-confirm-buttons button:first-child {
    margin-right: 0;
}
/*  Fix  */
