@-moz-document domain(monkeytype.com) {

    #bottom {
        display: none;
    }

    #top .logo .bottom {
        display: none;
    }

    div.top {
        display: none;
        position: absolute;
        left: 10px;
        top: 9px;
    }

    div.config {
        filter: blur(4px);
        position: absolute;
        bottom: 60px;
        left: 50%;
        transform: translate(-50%);
    }

    div.config:hover {
        filter: none;
        position: absolute;
        bottom: 60px;
        left: 50%;
        transform: translate(-50%);
    }

    #menu {
        filter: blur(4px);
        position: absolute;
        bottom: 10px;
        left: 50%;
        transform: translate(-50%);
    }

    #menu:hover {
        filter: none;
        position: absolute;
        bottom: 10px;
        left: 50%;
        transform: translate(-50%);
    }

    /* i really want this to be global */
}
