@-moz-document domain("wolai.com") {
/*-------@文字批注----*/
:root {
	--wolaistyleplus-text-bg-border-radius:20px;
	/*备注背景圆角半径*/
	/*白色主题下的一些变量定义 文字颜色可自己修改*/
	--wolaistyleplus-text-comments-notes-content: '注';
	--wolaistyleplus-text-comments-notes-content2: '注释';
	/*文字批注 文字标显示的内容“注”*/
	--wolaistyleplus-text-comments-notes-bgcolor: rgba(244, 163, 95, .6);
	/*文字批注 注释 背景色*/
	/*----------------------------------------------------------------------------*/
	--wolaistyleplus-text-comments-highlight-content: '亮';
	--wolaistyleplus-text-comments-highlight-content2: '高亮';
	/*文字批注 文字标显示的内容“亮”*/
	--wolaistyleplus-text-comments-highlight-bgcolor: rgba(246, 204, 51, .6);
	/*文字批注 高亮 背景色*/
	/*----------------------------------------------------------------------------*/
	--wolaistyleplus-text-comments-revised-content: '改';
	--wolaistyleplus-text-comments-revised-content2: '修改';
	/*文字批注 文字标显示的内容“改”*/
	--wolaistyleplus-text-comments-revised-bgcolor: rgba(187, 198, 28, .6);
	/*文字批注 修改 背景色*/
	/*----------------------------------------------------------------------------*/
	--wolaistyleplus-text-comments-delete-content: '删';
	--wolaistyleplus-text-comments-delete-content2: '删除';
	/*文字批注 文字标显示的内容“删”*/
	--wolaistyleplus-text-comments-delete-bgcolor: rgba(137, 211, 249, .6);
	/*文字批注 删除 背景色*/
	/*----------------------------------------------------------------------------*/
	--wolaistyleplus-text-comments-supplement-content: '补';
	--wolaistyleplus-text-comments-supplement-content2: '补充';
	/*文字批注 文字标显示的内容“补”*/
	--wolaistyleplus-text-comments-supplement-bgcolor: rgba(151, 243, 217, .6);
	/*文字批注 补充 背景色*/
	/*----------------------------------------------------------------------------*/
	--wolaistyleplus-text-comments-comments-content: '重';
	--wolaistyleplus-text-comments-comments-content2: '重点';
	/*文字批注 文字标显示的内容“评”*/
	--wolaistyleplus-text-comments-comments-bgcolor: rgba(203, 153, 252, .6);
	/*文字批注 评语 背景色*/
	/*----------------------------------------------------------------------------*/
	--wolaistyleplus-text-comments-appreciation-content: '析';
	--wolaistyleplus-text-comments-appreciation-content2: '赏析';
	/*文字批注 文字标显示的内容“析”*/
	--wolaistyleplus-text-comments-appreciation-bgcolor: rgba(255, 207, 209, .6);
	/*文字批注 赏析 背景色*/
	/*----------------------------------------------------------------------------*/
	--wolaistyleplus-text-comments-doubt-content: '问';
	--wolaistyleplus-text-comments-doubt-content2: '疑问';
	/*文字批注 文字标显示的内容“问”*/
	--wolaistyleplus-text-comments-doubt-bgcolor: rgba(247, 138, 133, .6);
	/*文字批注 疑问 背景色*/
	/*----------------------------------------------------------------------------*/
	--wolaistyleplus-text-comments-dot-color: red;
	/*文字批注 圆点颜色*/
	--wolaistyleplus-text-comments-dot-bgcolor: rgba(200, 200, 200, 0.2);
	/*文字批注 圆点背景色*/
	/*----------------------------------------------------------------------------*/
	--wolaistyleplus-text-comments-wave-color: red;
	/*文字批注 波浪颜色*/
	--wolaistyleplus-text-comments-wave-bgcolor: rgba(200, 200, 200, 0.2);
	/*文字批注 波浪背景色*/
	/*----------------------------------------------------------------------------*/
	--wolaistyleplus-text-comments-triangle-color: red;
	/*文字批注 三角颜色*/
	--wolaistyleplus-text-comments-triangle-bgcolor: rgba(200, 200, 200, 0.2);
	/*文字批注 三角背景色*/
	--wolaistyleplus-text-comments-text-color: rgba(1, 1, 1, .83);
	/*文字批注 文字颜色*/
	--wolaistyleplus-text-comments-textlogo-color: rgba(1, 1, 1, .83);
	/*文字批注 文字标记颜色*/
	--wolaistyleplus-text-comments-textlogo-bgcolor: rgba(255, 255, 255, 0);
	/*文字批注 文字标记背景颜色*/
	--wolaistyleplus-text-comments-textlogo-leftline-color: rgb(250, 100, 0);;
	/*文字批注 文字标记左侧竖线颜色*/
	/*----------------------------------------------------------------------------*/
}
:root[theme='dark'] {
	--wolaistyleplus-text-bg-border-radius:20px;
	/*备注背景圆角半径*/
	--wolaistyleplus-text-comments-highlight-bgcolor: rgba(246, 204, 51, .6);
	/*文字批注 高亮 背景色*/
	/*----------------------------------------------------------------------------*/
	--wolaistyleplus-text-comments-notes-bgcolor: rgba(244, 163, 95, .6);
	/*文字批注 注释 背景色*/
	/*----------------------------------------------------------------------------*/
	--wolaistyleplus-text-comments-revised-bgcolor: rgba(187, 198, 28, .6);
	/*文字批注 修改 背景色*/
	/*----------------------------------------------------------------------------*/
	--wolaistyleplus-text-comments-delete-bgcolor: rgba(137, 211, 249, .6);
	/*文字批注 删除 背景色*/
	/*----------------------------------------------------------------------------*/
	--wolaistyleplus-text-comments-supplement-bgcolor: rgba(151, 243, 217, .6);
	/*文字批注 补充 背景色*/
	/*----------------------------------------------------------------------------*/
	--wolaistyleplus-text-comments-comments-bgcolor: rgba(203, 153, 252, .6);
	/*文字批注 评语 背景色*/
	/*----------------------------------------------------------------------------*/
	--wolaistyleplus-text-comments-appreciation-bgcolor: rgba(255, 207, 209, .6);
	/*文字批注 赏析 背景色*/
	/*----------------------------------------------------------------------------*/
	--wolaistyleplus-text-comments-doubt-bgcolor: rgba(247, 138, 133, .6);
	/*文字批注 疑问 背景色*/
	/*----------------------------------------------------------------------------*/
	--wolaistyleplus-text-comments-dot-color: red;
	/*文字批注 圆点 颜色*/
	--wolaistyleplus-text-comments-dot-bgcolor: rgba(200, 200, 200, 0.2);
	/*文字批注 圆点 背景色*/
	/*----------------------------------------------------------------------------*/
	--wolaistyleplus-text-comments-wave-color: red;
	/*文字批注 波浪 颜色*/
	--wolaistyleplus-text-comments-wave-bgcolor: rgba(200, 200, 200, 0.2);
	/*文字批注 波浪 背景色*/
	/*----------------------------------------------------------------------------*/
	--wolaistyleplus-text-comments-triangle-color: red;
	/*文字批注 三角 颜色*/
	--wolaistyleplus-text-comments-triangle-bgcolor: rgba(200, 200, 200, 0.2);
	/*文字批注 三角 背景色*/
	--wolaistyleplus-text-comments-text-color: rgba(255, 255, 255, .89);
	/*文字批注 文字颜色*/
	--wolaistyleplus-text-comments-textlogo-color: rgba(255, 255, 255, .7);
	/*文字批注 文字标记颜色*/
	--wolaistyleplus-text-comments-textlogo-bgcolor: rgba(255, 255, 255, 0);
	/*文字批注 文字标记背景颜色*/
	--wolaistyleplus-text-comments-textlogo-leftline-color: rgb(250, 100, 0);
	/*文字批注 文字标记左侧竖线颜色*/
	/*----------------------------------------------------------------------------*/
}
/*------------以下代码非专业同学不要改动！不要改动！不要改动！-----------*/
div [style*="background-color:rgba(237, 233, 235, 0.09)"],
div [style*="background-color:rgba(237, 233, 235, 0.5)"],
div [style*="background-color:rgba(247, 214, 183, 0.5)"] {
	/*三角符号，圆点符号的一些公共样式*/
	font-family: 'Source Code Pro', 'Consolas', 'Monaco', 'monospace' !important;
	border-radius: var(--wolaistyleplus-text-bg-border-radius);
	padding: 2px 8px 3px 8px;
	-webkit-text-emphasis-position: under right;
	text-emphasis-position: under right;
	letter-spacing: 0px !important;
	line-height: 29px;
}
div [style*="background-color:rgba(237, 233, 235, 0.09)"] {
	/*三角符号线 暗色主题白灰背景按钮*/
	-webkit-text-emphasis: filled triangle;
	text-emphasis: filled triangle;
	background-color: var(--wolaistyleplus-text-comments-triangle-bgcolor) !important;
	text-emphasis-color: var(--wolaistyleplus-text-comments-triangle-color) !important;
	-webkit-text-emphasis-color: var(--wolaistyleplus-text-comments-triangle-color) !important;
}
div [style*="background-color:rgba(237, 233, 235, 0.5)"] {
	/*三角符号线 白色主题白灰背景按钮*/
	-webkit-text-emphasis: filled triangle;
	text-emphasis: filled triangle;
	background-color: var(--wolaistyleplus-text-comments-triangle-bgcolor) !important;
	text-emphasis-color: var(--wolaistyleplus-text-comments-triangle-color) !important;
	-webkit-text-emphasis-color: var(--wolaistyleplus-text-comments-triangle-color) !important;
}
div [style*="background-color:rgba(247, 214, 183, 0.5)"] {
	/*圆点符号 幼杏背景按钮 */
	-webkit-text-emphasis: filled circle;
	text-emphasis: filled circle;
	background-color: var(--wolaistyleplus-text-comments-dot-bgcolor) !important;
	text-emphasis-color: var(--wolaistyleplus-text-comments-dot-color) !important;
	-webkit-text-emphasis-color: var(--wolaistyleplus-text-comments-dot-color) !important;
}
div [style*="background-color:rgba(217, 211, 215, 0.2)"],
div [style*="background-color:rgba(217, 211, 215, 0.5)"] {
	/*波浪线符号 暗银背景按钮 */
	font-family: 'Source Code Pro', 'Consolas', 'Monaco', 'monospace' !important;
	background-color: var(--wolaistyleplus-text-comments-wave-bgcolor) !important;
	border-radius: var(--wolaistyleplus-text-bg-border-radius);
	padding: 2px 8px 3px 8px;
	text-decoration: underline wavy var(--wolaistyleplus-text-comments-wave-color);
	line-height: 29px;
}
div [style*="background-color:rgba(169, 222, 249, 0.5)"],
div [style*="background-color:rgba(205, 243, 220, 0.5)"],
div [style*="background-color:rgba(255, 193, 153, 0.5)"],
div [style*="background-color:rgba(252, 246, 189, 0.5)"],
div [style*="background-color:rgba(239, 218, 251, 0.5)"],
div [style*="background-color:rgba(253, 198, 200, 0.5)"],
div [style*="background-color:rgba(189, 201, 255, 0.5)"],
div [style*="background-color:rgba(234, 202, 220, 0.5)"] {
	/*八种批注的公共样式*/
	border-radius: var(--wolaistyleplus-text-bg-border-radius);
	padding: 1px 6px 2px 8px !important;
	margin: 0px 2px !important;
	letter-spacing: 0px !important;
	color: var(--wolaistyleplus-text-comments-text-color) !important;
	line-height: 29px;
}

div [style*="background-color:rgba(169, 222, 249, 0.5)"]:after,
div [style*="background-color:rgba(205, 243, 220, 0.5)"]:after,
div [style*="background-color:rgba(255, 193, 153, 0.5)"]:after,
div [style*="background-color:rgba(252, 246, 189, 0.5)"]:after,
div [style*="background-color:rgba(239, 218, 251, 0.5)"]:after,
div [style*="background-color:rgba(253, 198, 200, 0.5)"]:after,
div [style*="background-color:rgba(189, 201, 255, 0.5)"]:after,
div [style*="background-color:rgba(234, 202, 220, 0.5)"]:after {
	/*八种批注文字标的公共样式*/
	font-size: 0.8em;
	font-weight: 600;
	border-radius: 0px;
	border-left: 2px solid var(--wolaistyleplus-text-comments-textlogo-leftline-color);
	margin: 0px -2px 4px 5px;
	padding: 1px 3px;
	color: var(--wolaistyleplus-text-comments-textlogo-color)!important;
	background-color: var(--wolaistyleplus-page-textlogo-bgcolor) !important;
}
/*----------------------------------------------------------------------------*/
div [style*="background-color:rgba(169, 222, 249, 0.5)"] {
	/*删除 天藍背景按钮*/
	background-color: var(--wolaistyleplus-text-comments-delete-bgcolor) !important;
}
div [style*="background-color:rgba(169, 222, 249, 0.5)"]:after {
	/*删除 天藍背景按钮*/
