	color: var(--wolaistyleplus-text-comments-notes-bgcolor);
}
/*----------------------------------------------------------------------------*/
div [style*="background-color:rgba(252, 246, 189, 0.5)"] {
	/*高亮 文字标 淡黄背景按钮*/
	background-color: var(--wolaistyleplus-text-comments-highlight-bgcolor) !important;
}
div [style*="background-color:rgba(252, 246, 189, 0.5)"]:after {
	/*高亮 文字标  淡黄背景按钮*/
	content: var(--wolaistyleplus-text-comments-highlight-content);
	/*亮*/
	color: var(--wolaistyleplus-text-comments-highlight-bgcolor);
}
/*----------------------------------------------------------------------------*/
div [style*="background-color:rgba(239, 218, 251, 0.5)"] {
	/*评语 文字标  轻紫背景按钮*/
	background-color: var(--wolaistyleplus-text-comments-comments-bgcolor) !important;
}
div [style*="background-color:rgba(239, 218, 251, 0.5)"]:after {
	/*评语 文字标  轻紫背景按钮*/
	content: var(--wolaistyleplus-text-comments-comments-content);
	/*评*/
	color: var(--wolaistyleplus-text-comments-comments-bgcolor);
}
/*----------------------------------------------------------------------------*/
div [style*="background-color:rgba(253, 198, 200, 0.5)"] {
	/*疑问 将红背景按钮*/
	background-color: var(--wolaistyleplus-text-comments-doubt-bgcolor) !important;
}
div [style*="background-color:rgba(253, 198, 200, 0.5)"]:after {
	/*疑问 文字标  将红背景按钮*/
	content: var(--wolaistyleplus-text-comments-doubt-content);
	/*问*/
	color: var(--wolaistyleplus-text-comments-doubt-bgcolor);
}
/*----------------------------------------------------------------------------*/
div [style*="background-color:rgba(189, 201, 255, 0.5)"] {
	/*补充 文字标  雾藍背景按钮*/
	background-color: var(--wolaistyleplus-text-comments-supplement-bgcolor) !important;
}
div [style*="background-color:rgba(189, 201, 255, 0.5)"]:after {
	/*补充 文字标  雾藍背景按钮*/
	content: var(--wolaistyleplus-text-comments-supplement-content);
	/*补*/
	color: var(--wolaistyleplus-text-comments-supplement-bgcolor);
}
/*----------------------------------------------------------------------------*/
div [style*="background-color:rgba(234, 202, 220, 0.5)"] {
	/*赏析 文字标  熏粉背景按钮*/
	background-color: var(--wolaistyleplus-text-comments-appreciation-bgcolor) !important;
}
div [style*="background-color:rgba(234, 202, 220, 0.5)"]:after {
	/*赏析 文字标  熏粉背景按钮*/
	content: var(--wolaistyleplus-text-comments-appreciation-content);
	/*析*/
	color: var(--wolaistyleplus-text-comments-appreciation-bgcolor);
}
/*----------------------------------------------------------------------------*/
