css:
@-moz-document domain("studysmarter.de"), domain("app.studysmarter.de") {
body, html, .app {
    color: #919191de !important;
    background: #000 !important;
}

.auth-container{
    background-image: linear-gradient(277.28deg,#09201c,#02151f) !important;
}

.toolbar, .toolbar .page-title, .widget-wrapper, .menu-button {
    background: #19273c !important;
}
.main-content, .toolbar {
    border-bottom: 1px solid #333333 !important;
}
.toolbar, .mobile-toggle-icon {
    color: #ababab !important;
}
.invite-button {
    border: 1px solid #1478c8 !important;
    background: #000 !important;
}
.content-wrapper {
    background-color: #171717fa !important;
}


.create-button {
    background: #00253e !important;
    color: #3971ba !important;
}
.create-button, .plus {
    background: #000000cf !important;
}

.slide-header, .slideset-card-content{
    border-radius: 12px;
}

.question-answer, .single-flashcard{
    border-radius: 12px;
}



.card, .tab-group, .slides-card, .drag-and-drop-block, .placeholder-illustration-block, .flashcard-tab-wrapper, .mat-drawer-container, .mat-sidenav-container, .sidenav-container, .dropdown-select-trigger, .subject-inner, .slideset-list, .slideset-card, .slideset-card-content, .slides-card .global-card-layout .ng-star-inserted, .mat-dialog-container, .ng-tns-c56-49, .ng-trigger, .ng-trigger-dialogContainer, .slides-card, .global-card-layout, .progress-bar-main, .infinite-scroll-block, .upload-wrapper, .all-flashcards, .placeholder-illustration, .all-flashcards-wrapper, .mat-selectdropdown-select-trigger, .ng-tns-c175-60, .no-studygroup, .studygroup-controls-wrapper, .slideset-list, .left, .canvasResponsive, .chartjs-render-monitor, .chartjs-size-monitor-shrink, .time-chart-wrapper, audio, canvas, progress, video, .canvasResponsive, .chartjs-render-monitor, .mat-table, .cdk-table, .mat-elevation-z8, .feed-right-wrapper, .badge-feed-left-wrapper, .article-feed-wrapper, .time-chart-feed-wrapper, .main-container {
    background-color: #00253e !important;
    color: #888 !important;
/*     border-radius: 15px !important; */
}
.card:hover, .content, .cancelText:hover, .table-row:hover, .main-container {
    background-color: #001b2d !important;
    color: #888 !important;
}
.card, .title-wrapper, .left, .title, .benchmark, .benchmark-text, .add-subtopic, .subtopic-card, .subtopic-benchmarks, p, .slide-titlebar, .subject-name, .dropdown-select-trigger, .mat-header-cell, .cdk-header-cell, .cdk-column-subject, .mat-column-subject{
    color: #a6a6a6 !important;
}

.subtopic-expander, .subtopic-expander.expanded, .subtopic-card, .title-input, .color-wrapper, .datepicker, .share-toggle, .searchbar-wrapper, .joinable-subject-card-container, .joinable-subject-card-content-container, .content-container, .counts-container, .count-wrapper, .joinable-subject-card-container, .nav-tabs-item.active,  .flex-wrapper, .subjects-wrapper,  .document-overview-wrapper, .create-button, .card, .single-flashcard, .create-flashcard, .question, .question-answer-block.state-default, .filter-select, .document-item, .setting-button, .show-more-text, .title-wrapper > .left{
    background: #243c51 !important;
    color: #178fef !important;
    border: 1px #0000008c !important;
}

.filter-menu-item:hover, .mat-option.mat-active, .mat-option.mat-selected:not(.mat-option-multiple):not(.mat-option-disabled), .mat-option:focus:not(.mat-option-disabled), .mat-option:hover:not(.mat-option-disabled), .filter-block-item:hover, .show-more-text:hover{
    background: #001d36 !important;
    color: #178fef !important;
    border: 1px #000000b5 !important;
}

.subject-list, .slides-card-wrapper, .slides-card, .border-top, .border-top, .subjects-wrapper .subject-inner, .time-chart-feed-wrapper{
    border: none !important;
}
.mat-datepicker-content, .filter-block-item, .invite-button {
    background-color: #243c51 !important;
    color: #cdcdcdde !important;
    border: 1px #000000b5 !important;
}

.ng-star-inserted, .feed-right-wrapper, .article-feed-wrapper, .form-input__container, .ng-tns-c81-121 {
    border: 1px #06141d !important;
}


.details-more-icon-svg{
    fill: #888;
}
.mat-calendar-table-header, .mat-calendar-body-label, .mat-calendar-body-cell-content, .mat-date-range-input-separator, .title, .benchmark-text, .add-button, .ng-star-inserted, .mat-input-element, .mat-form-field-autofill-control, .ng-tns-c81-14, .cdk-text-field-autofill-monitored, .primary-title-feed-page{
    color: #bebebe !important;
}
.mat-calendar-body-disabled > .mat-calendar-body-cell-content:not(.mat-calendar-body-selected):not(.mat-calendar-body-comparison-identical), .mat-form-field-disabled .mat-date-range-input-separator {
    color: #b5b5b561 !important;
}


.mat-button.mat-accent.mat-button-disabled, .mat-button.mat-button-disabled.mat-button-disabled, .mat-button.mat-primary.mat-button-disabled, .mat-button.mat-warn.mat-button-disabled, .mat-icon-button.mat-accent.mat-button-disabled, .mat-icon-button.mat-button-disabled.mat-button-disabled, .mat-icon-button.mat-primary.mat-button-disabled, .mat-icon-button.mat-warn.mat-button-disabled, .mat-stroked-button.mat-accent.mat-button-disabled, .mat-stroked-button.mat-button-disabled.mat-button-disabled, .mat-stroked-button.mat-primary.mat-button-disabled, .mat-stroked-button.mat-warn.mat-button-disabled, .mat-datepicker-content .mat-calendar-next-button, .mat-datepicker-content .mat-calendar-previous-button, .mat-datepicker-toggle {
    color: #ffffff7a !important;
}
}
