css:
@-moz-document url-prefix("https://www.netflix.com/browse"), url-prefix("https://www.netflix.com"), domain("netflix.com") {
.notifications .notification:hover{
background:#333!important;
}
img.title-card.multi-landing-stack.multi-landing-stack-img {
    width: auto;
    height: auto;
}
ul.notifications-container{
background-color: rgba(157, 158, 216, 0)!important;
}
.main-header .has-billboard .search-focused .menu-navigation{
border-radius: 10px!important;
}
.searchBox::hover{
    border: white solid!important;
    border-radius: 10px!important;
}
a.current.active{
    border: red solid 4px!important;
    border-radius: 10px!important;
    width: auto!important;
    padding-left: 5px!important;
    padding-right: 5px!important;
    padding-top: 2px!important;
    padding-bottom: 2px!important;
    background-color: red!important;
}
a.element.text.notification-link {
    padding-left: 5px!important;
    margin-left: 5px!important;
    margin: 10px!important;
}
a.element.image.notification-link {
    margin: 20px!important;
    padding: 0px!important;
    height:75px!important;

}
.sub-menu.theme-lakira {
    background-color: transparent!important;
    border-radius: 20px!important;
    border: gray solid!important;
}

ul.notifications-container {
    border: black solid!important;
    border-radius: 10px!important;
}

.topbar {
    background-color:transparent!important;
}

.multi-landing-stack.multi-landing-stack-1 {
    background-color: transparent!important;
}

.multi-landing-stack.multi-landing-stack-2 {
    background-color: transparent!important;
}
img.title-card{
border-radius:15px!important;
}
.episodeArt.video-artwork{
border-radius:20px!important;
}
.episodeLockup .episodeArt{
background-color:transparent!important;
}
.numberVignette,.bob-overlay.bob-overlay-hidden,.bob-overlay{
border-radius:20px!important;
}
.account-drop-down.sub-menu.theme-lakira{
background-color:transparent!important;
}
.akira-header .account-drop-down .profiles .profile-icon, .pinning-header .account-drop-down .profiles .profile-icon{
border-radius:10px!important;
background-color:gray!important;
}
.bob-overlay.bob-overlay-hidden,.sub-menu.theme-lakira a{
border: solid 5px gray!important;
background-color:white!important;
color:black!important;
border-radius:20px!important;
}
html body div div div div div div div div div div div div div div div span div div,.jawBoneContainer,.bob-card-adult-video-merch .bob-background,img.bob-title-art,.jawBoneContainer .background,.nfp.nf-player-container.notranslate.inactive.NFPlayer.VideoMerchPlayer,.bob-card .bob-card-adult-tall-panel-video-merch{
background-color:transparent!important;
border-radius:20px!important;
}
.jawBoneContainer .background{
background:transparent!important;
background-color:transparent!important;
border-radius:15px!important;
}
.pinning-header-container{
background:transparent!important;
}
.mini-modal-container, .previewModal--metadatAndControls-container, .detail-modal-container, .episodeSelector, .previewModal--detailsMetadata{
background-color:rgb(102, 102, 102)!important;
}
.previewModal--player_container.detail-modal .previewModal--player-titleTreatmentWrapper{
background:linear-gradient(to top,rgb(102, 102, 102),transparent 50%)!important;
}
.episodeSelector .episodeSelector-container .episode-item.current{
background-color:rgba(102, 102, 102, 0.65)!important;
}
.ltr-16khy1u{
background-color:rgba(102, 102, 102, 0.65)!important;
}
.previewModal--tags .previewModal--tags-label{
color:black;
}
.lolomoRow .lolomoRow_title_card .ltr-0{
background:gray!important;
}
body, html{
background:gray!important;
}
.previewModal--backDrop{
background:linear-gradient(to top,gray,black 50%)!important;
}
.titleCard--container .titleCard--metadataWrapper{
background-color:rgba(168, 168, 168, 0.88)!important;
}
.about-wrapper, .moreLikeThis--wrapper{
background-color:rgba(102, 102, 102, 0.65);
}
.ltr-111bn9j{
background-color:rgba(168, 168, 168, 0.88)!important;
}
.ltr-1lwi1i3{
background-color:rgba(168, 168, 168, 0.76)!important;
}
.ltr-bbkt7g:hover:hover{
background-color:rgba(168, 168, 168, 0.88)!important;
}
.previewModal--player_container{
background-color:transparent!important;
}
.main-header.has-billboard.menu-navigation,.pinning-header .main-header.menu-navigation:not(.has-billboard):not(.solid){
border: solid 0px black!important;
border-radius:10px!important;
max-width:2209px!important;
background:rgb(102, 102, 102)!important;
min-width:800px!important;
margin-right:3px!important;
padding-left:40px!important;
margin-top:3px!important;
margin-left:4px!important;
}
.main-header.has-billboard.menu-navigation{
width:auto!important;
}
.akira-header .account-dropdown-button .profile-icon, .pinning-header .account-dropdown-button .profile-icon{
border: solid 0px black!important;
border-radius:7px!important;
}
.billboard-row .billboard .maturity-rating{
border: solid 0px black!important;
border-radius:10px!important;
background-color:rgba(112, 112, 112, 0.51)!important;
margin-right:5px!important;
}
.oxart-image, .boxart-image-in-padded-container,.boxart-image, .boxart-image-in-padded-container,.oxart-size-16x9 ,.boxart-containe,.fallback-text-container,.bob-play-hitzone,.title-card-focus-ring,div.bob-overlay.bob-overlay-hidden,.bob-overlay{
 border: solid 0px black!important;
 border-radius:10px!important;
 background-color:transparent!important;
}


/*custom profile icon / you will need to manuly put the class one in your profile*/
.one{
background-image:url("data:image/jpeg;base64,")!important;
}
.image-text-notification{
background:rgba(142, 142, 142, 0.68)!important;
}
body{
background:gray
}
.billboard-row .billboard .hero-image-wrapper .hero-vignette{
background-image: linear-gradient(to bottom,rgba(20,20,20,0) 0,rgba(142, 142, 142, 0.39) 15%,rgba(142, 142, 142, 0.57) 29%,rgba(142, 142, 142, 0.68) 44%,gray 68%,gray 100%)!important;
}
.originals-panels-row .rowContent .slider-item{
background:transparent!important;
}
}
