css:
@-moz-document domain("www.youtube.com")
{

/*Hides short description*/
#watch-video-details-inner-less {
display: none !important;}

/*Forces long description to show*/
#watch-video-details-inner-more {
display: inline !important;}

#watch-video-details-inner .description {
/*Limits description width*/
max-width: 24em !important; 
/*Limits description height*/
max-height: 25em !important; 
/*Show scrollbar if over limit*/
overflow: auto !important; 
/*Centers the description*/
margin-left: 7px !important; 
margin-right: 7px !important; 
margin-top: -7px !important;}

/*Moves Category*/
#watch-category {
margin-left: 7px !important; 
margin-top: 4px !important;}

/*Moves Tags*/
#watch-video-tags-div {
margin-left: 7px !important;
margin-bottom: -4px !important;}

/*Hides the 'More Info'/'Less Info' Link*/
#watch-video-details-toggle
{display: none !important;}

}
