/* 主题色 */
:root {
	--THEME_LIGHT_BG_COLOR: var(--white);
	--THEME_NIGHT_BG_COLOR: var(--gray-12);
	--THEME_LIGHT_SEPARATOR_COLOR: #F0F0F0;
	--THEME_NIGHT_SEPARATOR_COLOR: #F0F0F0;
	--THEME_LIGHT_TEXT_COLOR: var(--gray-12);
	--THEME_NIGHT_TEXT_COLOR: var(--gray-8);
	--THEME_LIGHT_TEXT_ACTIVATED_COLOR: var(--gray-13);
	--THEME_NIGHT_TEXT_ACTIVATED_COLOR: var(--gray-7);
	--THEME_LIGHT_TEXT_UNFOCUSED_COLOR: var(--gray-7);
	--THEME_NIGHT_TEXT_UNFOCUSED_COLOR: #121212;
	--THEME_LIGHT_TEXT_FOCUSED_COLOR: #d9d9d9;
	--THEME_LIGHT_TEXT_DISABLED_COLOR: #121212;
	--THEME_NIGHT_TEXT_DISABLED_COLOR: #121212;
}

/* 中性色 Neutral Colors */
:root {
	--white: #ffffff;
	
	--gray-1: #ffffff;
	--gray-2: #fafafa;
	--gray-3: #f5f5f5;
	--gray-4: #f0f0f0;
	--gray-5: #d9d9d9;
	--gray-6: #bfbfbf;
	--gray-7: #8c8c8c;
	--gray-8: #595959;
	--gray-9: #434343;
	--gray-10: #262626;
	--gray-11: #1f1f1f;
	--gray-12: #141414;
	--gray-13: #000000;
	
	--black: #000000;
	
	
	
}



:root {
	--night-bd-color: #444;
	--night-text-color: #c6c6c6;
	--night-focus-color: #333;
	--night-bg-color: #262626;
}
:root {
	--sr-annote-color-0: #b4d9fb;
	--sr-annote-color-1: #ffeb3b;
	--sr-annote-color-2: #80deea;
	--sr-annote-color-3: #85d1f6;
	--sr-annote-color-4: #8cd842;
	--sr-annote-color-5: #ffb7da;
}
:root {
	--nav-night-bg-color: #202020;
	--logo-night-bg-color: #252525;
	--night-bg-color: #202020;
	--EDITOR_BG_COLOR_DARK: #1E1E1E;
}
:root {
	--danger: #de1c31;
	--default: inherit;
	--success: #20894d;
	--warning: #ff9900;
/* 	--primary: #309FB1; */
/* 	--primary: #348ACD; */
	--primary: #3D86BA;
/* 	--primary: #2775b6; */
	/*--info: #03a9f4;*/
	--info: #2775b6;
	--bgrey: rgb(96, 125, 139);
	--common-border: #ddd;
	--comman-bg: #fff;
	--common-font-color: #000;
	--common-font-inverse-color: #fff;
	--table-border: #ddd;
	--highlight: var(--danger);
	--placeholder: #bbb;
	--star-refer-text: #c9c9c9;
	--roam-right-bg: #fff;
	--node-crumbs-text: #ccc;
	--node-crumbs-link: #666;
	--node-crumbs-splitor: #bbb;
	--node-btn-default: #999;
	--node-btn-hover: var(--info);
	--node-btn-wrap-text: #d3d1d1;
	--node-btn-wrap-hover: var(--primary);
	--node-btn-wrap-active: var(--info);
	--node-text-link: var(--primary);
	--node-text-link-hover: var(--info);
	--node-text-finished: #ccc;
	--node-text-focus-bg: transparent;
	--node-child-border: #ddd;
	--node-child-border-focus: #ccc;
	--node-quote: #aaa;
