	--search-node-bg: #f9f9f9;
	--search-node-border: #f4f4f4;
	--suggest-bg: #fff;
	--suggest-shadow: 0px 0px 4px 1px #dddddd;
	--suggest-item-odd-bg: rgba(0, 0, 0, 0.03);
	--suggest-item-hover-bg: #607d8b42;
	--suggest-item-hover: inherit;
	--suggest-help: #aaa;
	--mdimg-border: #eee;
	--btn-default: #fff;
	--btn-default-bg: var(--bgrey);
	--btn-default-hover-bg: rgba(96, 125, 139, 0.85);
	--popup-menu-input-border: #ddd;
	--popup-menu-divider: rgba(0, 0, 0, 0.1);
	--md-underline: #999;
	--help-tip-border: #00000017;
	--help-tip-bg: #fff;
	--flexmap-bracket-border: #75757d;
