#detail-wrap .nmarkdown span[linkto][color='yellow'],
#detail-wrap .nmarkdown span[linkto][color='y'],
#detail-wrap .nmarkdown .katex[color='yellow'],
#detail-wrap .nmarkdown .katex[color='y'],
#detail-wrap .nmarkdown .refer-text[color='yellow'],
#detail-wrap .nmarkdown .refer-text[color='y'] {
