}

@-moz-document domain("roamedit.com") {
/**
 * 布局
 */

/* 整体修正 */
html, body {
	font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, Cantarell, 'Open Sans', 'Helvetica Neue', sans-serif;
}

kbd {
	display: inline-block;
	padding: 3px 5px;
	font: 11px SFMono-Regular,Consolas,Liberation Mono,Menlo,monospace !important;
	line-height: 10px;
	color: var(--gray-7);
	vertical-align: middle;
	background-color: var(--gray-4);
	border-bottom-color: var(--gray-5);
	border: 1px solid var(--gray-5);
	border-radius: 6px;
	box-shadow: inset 0 -1px 0 var(--gray-5);
}

/* 总框架 */

body#jShape {
	background-color: #EAEAEA;
}

body#jShape.theme-night {
	background-color: var(--THEME_NIGHT_BG_COLOR);
	color: var(--THEME_NIGHT_TEXT_COLOR);
}

#jShape, #detail-wrap {
	height: 100vh;
	overflow: hidden;
}



/* 导航栏 */
.navbar {
	height: 50px;
	box-shadow: rgba(0, 0, 0, 0.1) 0px 1px 3px 0px, rgba(0, 0, 0, 0.06) 0px 1px 2px 0px;
}
body#jShape.theme-night #detail-wrap .navbar {
	background-color: var(--gray-11);
}

#navbar-list {
	display: flex;
	align-items: center;
}

.navbar #navbar-list .navbar-header {
	height: 100%;
	padding: 13px;
	background: var(--THEME_LIGHT_BG_COLOR);
}

.navbar #navbar-list .navbar-header .navbar-brand {
	font-family: Cambria, Cochin, Georgia, Times, 'Times New Roman', serif;
	font-size: 24px;
	line-height: 24px;
	font-weight: bold;
	letter-spacing: .03em;
	color: var(--THEME_LIGHT_TEXT_COLOR);
}

body#jShape.theme-night .navbar #navbar-list .navbar-header .navbar-brand {
	color: var(--THEME_NIGHT_TEXT_COLOR);
}

.navbar #navbar-list .navbar-header .navbar-brand:hover,
body#jShape.theme-night .navbar #navbar-list .navbar-header .navbar-brand:hover {
	color: var(--primary);
	transition: color 500ms ease;
}

.navbar #navbar-list .navbar-header .navbar-brand span {
	font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, Cantarell, 'Open Sans', 'Helvetica Neue', sans-serif;
	color: var(--primary);
	letter-spacing: .05em;
	font-size: 13px!important;
}

#navbar-list .tab-wrap .tab-header .tab-item {
	margin-left: 10px;
	border: 1px solid var(--gray-4) !important;
	border-radius: 4px;
	color: var(--THEME_LIGHT_TEXT_COLOR);
	background: var(--gray-1);
	opacity: 1;
	transition: all .24s ease;
}

body#jShape.theme-night #navbar-list .tab-wrap .tab-header .tab-item {
	border: 1px solid var(--gray-10) !important;
	color: var(--THEME_NIGHT_TEXT_COLOR);
	background: var(--gray-10);
}

#navbar-list .tab-wrap .tab-header .tab-item:hover {
	color: var(--THEME_LIGHT_TEXT_ACTIVATED_COLOR) !important;
	background-color: var(--gray-4) !important;
	box-shadow: none !important;
}

body#jShape.theme-night #navbar-list .tab-wrap .tab-header .tab-item:hover {
	background-color: var(--gray-9) !important;
	color: var(--THEME_NIGHT_TEXT_ACTIVATED_COLOR) !important;
}

#navbar-list .tab-wrap .tab-header .tab-item span {
	border-radius: 0 !important;
}

#navbar-list .tab-wrap .tab-header .tab-item span[linkto],
#navbar-list .tab-wrap .tab-header .tab-item a,
#navbar-list .tab-wrap .tab-header .tab-item i {
	margin-left: 0px;
	border-bottom: none;
	border-radius: 0;
	text-decoration: none;
	background: transparent;
	color: var(--THEME_LIGHT_TEXT_COLOR);
	transition: all .24s ease;
}

body#jShape.theme-night #navbar-list .tab-wrap .tab-header .tab-item span[linkto],
body#jShape.theme-night #navbar-list .tab-wrap .tab-header .tab-item a,
body#jShape.theme-night #navbar-list .tab-wrap .tab-header .tab-item i {
	color: var(--THEME_NIGHT_TEXT_COLOR);
}

#navbar-list .tab-wrap .tab-header .tab-item:hover span[linkto],
#navbar-list .tab-wrap .tab-header .tab-item:hover a,
#navbar-list .tab-wrap .tab-header .tab-item:hover i {
	color: var(--THEME_LIGHT_TEXT_ACTIVATED_COLOR) !important;
}

body#jShape.theme-night #navbar-list .tab-wrap .tab-header .tab-item:hover span[linkto],
body#jShape.theme-night #navbar-list .tab-wrap .tab-header .tab-item:hover a,
body#jShape.theme-night #navbar-list .tab-wrap .tab-header .tab-item:hover i {
	color: var(--THEME_NIGHT_TEXT_ACTIVATED_COLOR) !important;
}



/* 选项菜单 */
body#jShape .op-menu {
	width: 220px;
	min-width: 160px;
	padding: 0;
	margin: 0;
	font-size: 14px;
	text-align: left;
	list-style: none;
	background-color: #fff;
	background-clip: padding-box;
	border-radius: 0 0 4px 4px;
	box-shadow: rgba(0, 0, 0, 0.1) 0px 1px 3px 0px, rgba(0, 0, 0, 0.06) 0px 1px 2px 0px;
	max-height: 500px;
	overflow: auto;
}

body#jShape.theme-night .op-menu {
	background-color: var(--gray-10);
}

body#jShape .op-menu .op-menu-ul[name=system] li {
	padding: 0 !important;
	border-bottom: none;
	transition: all .24s ease;
}

.op-menu .op-menu-ul li > a {
	display: flex;
	flex: 0 1 auto;
	flex-flow: row wrap;
	justify-content: flex-start;
	align-items: center;
	height: 32px;
	margin: 0;
	padding: 5px 10px;
	cursor: pointer;
}

.op-menu .op-menu-ul li:hover {
	background: var(--gray-4);
}

body#jShape.theme-night .op-menu .op-menu-ul li:hover {
	background: var(--gray-9);
}

.op-menu .op-menu-ul li:hover a {
	color: var(--THEME_LIGHT_TEXT_ACTIVATED_COLOR);
	background: var(--gray-4);
	text-decoration: none;
}

body#jShape.theme-night .op-menu .op-menu-ul li:hover a {
	color: var(--THEME_NIGHT_TEXT_ACTIVATED_COLOR);
	background: var(--gray-9);
	text-decoration: none;
}

.op-menu .op-menu-ul li a i {
	width: 22px;
	height: 22px;
	line-height: 22px;
	text-align: center;
}

.op-menu .op-menu-ul li > a > span {
	height: 22px;
	margin: 0 0 0 5px;
	line-height: 22px;
	text-align: center;
	overflow-x: hidden;
	text-overflow: ellipsis;
	white-space: nowrap;
}

.op-menu .op-menu-ul li > a > code {
	display: inline-block;
	padding: 1px 3px;
	font: 11px SFMono-Regular, Consolas, Liberation Mono, Menlo, monospace !important;
	line-height: 10px;
	letter-spacing: .2em;
	color: var(--gray-7);
	vertical-align: middle;
	background-color: var(--gray-2);
	border-color: var(--gray-5);
	border: 1px solid var(--gray-5);
	border-bottom: 2px solid var(--gray-5);
	border-radius: 4px 4px 2px 2px;
	box-shadow: inset 0 -1px 0 var(--gray-5);
}

body#jShape.theme-night .op-menu .op-menu-ul li > a > code {
	color: var(--gray-7);
	background-color: var(--gray-9);
	border-color: var(--gray-8);
	box-shadow: inset 0 -1px 0 var(--gray-8);
}

#navbar-list .tab-wrap .tab-header .tab-item[key="undo"] {
	display: none!important;
}
#navbar-list .tab-wrap .tab-header .tab-item[key="redo"] {
	display: none!important;
}

#navbar-list .tab-wrap .tab-header {
	margin-right: 10px
}
.navbar .label.label-success {
	background: #f3f3f300;
	color: #797979;
	border-radius: 8px
}
.tab-wrap .tab-header .tab-item {
	color: #797979;
	background: #f3f3f300;
	margin-left: 10px;
	border-radius: 6px
}
.tab-wrap .tab-header .tab-item:hover {
	color: #fff!important;
	background-color: #64aff0 !important;
	opacity: 0.99
}
.tab-wrap .tab-header .tab-item span {
	margin-left: 0px
}
.tab-wrap .tab-header .tab-item {
	color: rgb(121, 121, 121);
	margin-left: 10px!important;
	border: 1px solid #bbbbbb42!important
}

#navbar-list .tab-wrap .tab-header .tab-item {
	height: 35px;
	flex-basis: 35px
}

/* 主区域框架 */
#detail-wrap > .home {
	height: 100vh;
	margin-top: 50px;
}

#detail-wrap .block-header {
	padding: 0;
}

#roam-wrap {
	margin: 0;
}

/* 左侧栏 */
#leftsidebar {
	height: calc(100vh - 50px);
	margin-top: -4px;
	padding: 0;
	overflow: visible;
	box-shadow: rgba(0, 0, 0, 0.05) 0px 0px 0px 1px;
}

#leftsidebar .sidebar .menu {
	position: relative;
	overflow: visible
}

#leftsidebar .slimScrollDiv {
	height: calc(100vh - 50px) !important;
}

.sidebar .menu .list {
	height: calc(100vh - 50px) !important;
}

body#jShape.theme-night .toggled, .ml-menu {
	background-color: var(--gray-11);
	border-left: 3px solid;
	border-bottom: none;
	border-top: none;
	border-color: var(--primary);
}



.sidebar .menu .list .header {
	position: relative;
	background: var(--THEME_LIGHT_BG_COLOR);
	margin-bottom: 0;
	border-bottom: 1px solid var(--THEME_LIGHT_SEPARATOR_COLOR);
}

.menu .list li .addon-btn {
	opacity: 0;
	flex-basis: 25px;
	height: 25px;
	font-size: 18px;
	flex-grow: 0;
	flex-shrink: 0;
	display: flex;
	justify-content: center;
	align-items: center;
	cursor: pointer;
	margin-right: 5px;
	border-radius: 3px;
}
.menu .list li .addon-btn:hover {
	background-color: rgba(255,255,255,0.2);
}

.menu .list li.header #db-current-name {
	font-weight: normal;
	letter-spacing: .03em;
	color: var(--THEME_LIGHT_TEXT_UNFOCUSED_COLOR);
}

.menu .list li.header .addon-btn {
	position: absolute;
	top: 0;
	right: 0;
	height: 100%;
	margin: 0;
	color: var(--THEME_LIGHT_TEXT_UNFOCUSED_COLOR);
	opacity: 1;
}
.menu .list li.header .addon-btn:hover {
	color: var(--primary);
}

#jShape .nav-item-graph > a[route] > span {
	transform: translateX(0);
}

.sidebar .menu .list a {
	position: relative;
	display: block;
	height: 48px;
	padding: 10px 1em;
	border: none;
	border-radius: 0;
	margin: 0;
	line-height: 100%;
	letter-spacing: .05em;
	color: var(--gray-10);
	box-shadow: none;
	transition: color .24s ease, background .24s ease;
}
.sidebar .menu .list a:hover, .sidebar .menu .list a:active, .sidebar .menu .list a:focus {
	background-color: var(--gray-3);
	border: none;
	color: var(--primary);
}

.sidebar .menu .list a i {
	float: left;
	height: 28px;
	width: 28px;
	font-size: 16px;
	line-height: 28px;
}

.sidebar .menu .list a span {
	float: left;
	margin: 0 0 0 12px !important;
	height: 28px;
	font-size: 16px;
	font-weight: 600;
	line-height: 28px;
}

.menu .list li:not(.header) .addon-btn {
	position: absolute;
	top: 10px;
	right: 6px;
	height: 28px;
	width: 28px;
	border-radius: 100%;
	line-height: 28px;
	background: var(--white);
	color: var(--primary);
	box-shadow: rgba(0, 0, 0, 0.05) 0px 0px 0px 1px;
}
.theme-orange .sidebar .menu .list .toggled, .theme-orange .sidebar .menu .list .ml-menu {
	background-color: var(--white);
	border: none;
}

#star-list {
	padding: 1em 0 0 0;
}

/* 编辑区域 */
#roam-main-wrap {
	padding-left: 0 !important;
	padding-right: 0 !important;
	display: flex;
	flex-wrap: nowrap;
	height: 100%;
	background: var(--THEME_LIGHT_BG_COLOR);
}

body#jShape.theme-night #roam-main-wrap {
	background: var(--THEME_NIGHT_BG_COLOR);
