css:
@-moz-document url("https://www.itemfix.com/"), url("http://www.itemfix.com/"), url-prefix("https://www.itemfix.com/list") {
div.itemfix-grid-main-inner > div:nth-child(2) > h3:nth-child(2) > a:nth-child(2), div.itemfix-grid-main-inner > div:nth-child(2) > h3:nth-child(1) > a:nth-child(2) {
    color: #e6e3d7;
}
div.col-sm-8.pad-none.itemfix-grid-right-con div.itemfix-grids-views-con span a {
    color: #bebcbc;
}
.content-categories-tags-outer > h2:nth-child(1), div.itemfix-grid-main-inner > div:nth-child(2) > p:nth-child(4), div.itemfix-grid-main-inner > div:nth-child(2) > p:nth-child(3), 
div.col-12 > a:nth-child(1) > div:nth-child(2) > h4:nth-child(1) {
    color: #a5a0a0;
}
td {
    color: #a5a0a0;
}
#q  {
    background-color: #303030;
    color: white;
}
#order_by, .table > tbody:nth-child(1) > tr:nth-child(4) > td:nth-child(2) > input, .table > tbody:nth-child(1) > tr:nth-child(5) > td:nth-child(2) > input {
    filter: invert(81%);
}
.pagination > li > a {
    background-color: #242424 !important;
}
.fa-angle-right {
    color: #6f6f6f; 
}
[href="javascript://"] > strong {
    color: white !important;
}
}
@-moz-document url-prefix("https://www.itemfix.com/"), url-prefix("http://www.itemfix.com/") {
html, header, footer, [action="list"]>input, .categories-dropdown-con {
    background-color: #323030;
}
.logo-outer>a>img, footer>div:nth-child(1)>div>div>a>img {
    filter: invert(1) hue-rotate(185deg)
}
.navigation-list>div>ul>li>a, h1, div.col-md-3:nth-child(2) > h2:nth-child(1), div.col-md-3:nth-child(2) > h2:nth-child(4), div.col-md-3:nth-child(2) > form:nth-child(5) > h2:nth-child(2),
.categories-dropdown-con > ul > li > a, .filters > span:nth-child(1) {
    color: #a5a0a0;
}
[src="https://u.itemfix.com/images/img_dummy_video.png"] {
    filter: invert(92%);
}
div.col-12 > a > div:nth-child(2) > h4, div.col-12 > a:nth-child(1) > div:nth-child(2) > h4:nth-child(1) {
    color: #a5a0a0;
}
div.col-12 > a:hover > div:nth-child(2) > h4, div.col-12 > a:nth-child(1):hover > div:nth-child(2) > h4:nth-child(1) {
    color: #b09467;
}
}
@-moz-document url-prefix("https://www.itemfix.com/v?"), url-prefix("http://www.itemfix.com/v?") {
h1, .itemfix-grids-views-con > span:nth-child(1) > a:nth-child(3) {
    color: #fff;
}
.itemfix-grid-right-con > p:nth-child(3)
{
    color: white;
}
div.col-md-3:nth-child(2) > h2:nth-child(3), .content-right-outer > h2 {
    color: #a5a0a0;
}
.item-grid-video-links > ul:nth-child(1) > li:nth-child(1) > a:hover, .item-grid-video-links > ul:nth-child(1) > li:nth-child(2) > a:nth-child(1):hover {
    background-color: #e7e7e7;
    color: #151515;
}
.item-grid-video-links > ul:nth-child(1) > li:nth-child(1) > a, .item-grid-video-links > ul:nth-child(1) > li:nth-child(2) > a:nth-child(1) {
transition: background-color .2s;
}
}
@-moz-document url("https://www.itemfix.com/ll") {
#content_section > div:nth-child(1) > div:nth-child(1) > table:nth-child(1) > tbody:nth-child(1) {
    color: #c2bfb9;
}
}
@-moz-document url-prefix("https://www.itemfix.com/c/"), url-prefix("http://www.itemfix.com/c/") {
.staff-main-heading-con > h2:nth-child(1),
.staff-popular-right-con > span:nth-child(1),
.content-right-outer > h2:nth-child(2),
div.itemfix-grid-main-inner > div:nth-child(2) > h3:nth-child(2) > a:nth-child(2)
{
   color: #c2bfb9; 
}
div.col-sm-6 h4, div.itemfix-grid-main-inner > div:nth-child(2) > div:nth-child(3) > span:nth-child(1) > a:nth-child(3), div.itemfix-grid-main-inner > div:nth-child(2) > p:nth-child(4) {
    color: #b8b7b3; 
}
.staff-pic-con {
    display: none;
}
.staff-member-subscribe-con > h1:nth-child(1) {
    font-family: "Trattatello", fantasy, monospace;
    font-size: 50px;
    text-shadow: -2px 2px 6px #251e00c7;
}
.staff-member-right-con > p:nth-child(4) {
    font-size: 18px;
    color: #8c8c8c; 
}
}
