css:
@-moz-document domain("burgame.top")
{
  html
  {
    height: 100%;
  }

  body
  {
    background: url(https://creative-moscow.ru/assets/images/background.jpg) no-repeat #1d1e22;
    background-size: cover;
    background-attachment: fixed;
    min-height: 97%;
  }

  .discord-btn, .headerbar, #page-footer, #phpbb_announcement > div > div > strong, #ajaxchat .forabg
  {
    display: none;
  }

  .chat-message
  {
    font-size: 15px;
  }

  .chat-message iframe, .chat-message img
  {
    max-width: 100%;
  }

  .inputbox, .button, a.button1, input.button1, input.button3, a.button2, input.button2, .pagination li a, abbc3_buttons_row
  {
    background: #171B24;
  }

  .inputbox, #ajaxchat #abbc3_buttons
  {
    border-radius: 10px;
  }

  #ajaxchat .abbc3_buttons_row
  {
    width: inherit !important;
    background: none;
    border: none;
  }

  #abbc3_buttons select, .button
  {
    background-color: #242A36 !important;
    border: none;
    color: #626262 !important;
  }

  #abbc3_buttons select
  {
    width: 100px;
  }

  .spoilwrapper, .spoilbtn, .spoilcontent, .ModTable, .rowuser
  {
    background: #171B24 !important;
    color: white !important;
    border: none !important;
  }

  .spoilbtn
  {
    padding-top: 5px;
    padding-bottom: 5px;
  }
}
