/* ==UserStyle==
@name         YouTube: Fade-In Video Thumbnails
@namespace    de.sidneys.userstyles
@version      1.0.1
@description  This user style lets YouTube video thumbnails blend in smoothy as soon as they have been downloaded. This reduces perceived UI jankiness – and helps avoiding eye strain in context of infinitely scrolling list elements.
@author       sidneys
@homepageURL  https://userstyles.org/styles/203601
@supportURL   https://userstyles.org/styles/203601
@license      MIT
@preprocessor default
==/UserStyle== */
@-moz-document domain("youtube.com")
{
    /* Thumbnail background
       ==================================== */

    ytd-thumbnail #thumbnail.ytd-thumbnail
    {
        background-color: hsla(0deg, 0%, 100%, 3%);
    }

    /* Thumbnail image
       ==================================== */

    .ytd-thumbnail > img.yt-img-shadow
    {
        transition: filter 600ms ease-in-out 200ms;
        filter: opacity(0%);
    }

    /* Thumbnail image, loaded
       ==================================== */

    .ytd-thumbnail > img.yt-img-shadow[src^="http"]
    {
        filter: opacity(100%);
    }
}
