@keyframes rainbow
  {
    0% {background-color: pink;
    border-color: pink;
  }

  14%
  {
    background-color: peachpuff;
    border-color: peachpuff;
  }

  28%
  {
    background-color: oldlace;
    border-color: oldlace;
  }

  42%
  {
    background-color: honeydew;
    border-color: honeydew;
  }

  56%
  {
