css:
@-moz-document regexp("https?://www.youtube.com?/(?!my_videos|ad_companion|subscribe_embed|account|yt|red).*")
{
  html:not(#⁠),
body > div:not(.toastify):not(#toast),
body > table,
#guide-content,
[role="dialog"],
#body-container > div,
[aria-expanded="true"]:not(.ytp-button),
[class*="tooltip"]:not(.ytp-tooltip-duration):not(.ytp-tooltip-bg):not(.ytp-tooltip-text-wrapper):not([class*="arrow"]):not(button),
[role="menu"],
.ytp-popup,
.videoAdUiPreSkipButton,
.videoAdUiAdInfoPopup
  {
    background: url(https://www.wallpapertip.com/wmimgs/10-101512_violet-evergarden-wallpaper-hd.jpg)fixed no-repeat #000!important;
    background-size: cover!important;
  }
/*search bar*/
  #masthead-search-terms,
form.channels-search:not(#​)
  {
    border: 1px solid #FFF!important;
  }

  #masthead-search-terms.gsfe_a
  {
    border: 1px solid #A0A0A0!important;
  }

  #masthead-search-terms.gsfe_b
  {
    border: 1px solid #4D90FE!important;
  }

  :not(textarea):not(input):not([contenteditable])
  {
    text-shadow: 0 0.05em rgba(0, 0, 0, .5), 0 -0.05em rgba(0, 0, 0, .5), 0.05em 0 rgba(0, 0, 0, .5), -0.05em 0 rgba(0, 0, 0, .5)!important;
  }

  :not([class*="video-extras-sparkbar"]):not([class^="ytp-"]):not(.sidebar):not(.video-time):not([class^="html5-"]):not(.yt-uix-button-primary):not(label):not(.toggle):not(.branding-context-container-inner):not(.iv-drawer):not(#img):not(.ytd-thumbnail):not(#dismissable):not(.ytd-multi-page-menu-renderer):not(ytd-compact-link-renderer):not(ytd-notification-renderer):not(.ytd-multi-page-menu-renderer):not([menu-style="multi-page-menu-style-type-conversations"]):not(.yt-multi-page-menu-section-renderer):not(#creation-box):not(.toastify):not(#placeholder-area):not(#simple-box):not(.ytd-subscribe-button-renderer):not(.ytd-playlist-panel-supported-items):not(#content):not([role="menuitem"]):not(#container):not([role="presentation"]):not([role="listbox"]):not(#search-form):not(#search):not(#toast):not(.style-default-active):not(.ytd-toggle-button-renderer):not(.yt-icon):not(.yt-icon-button):not(#creator-heart-button)
  {
    background-color: rgba(0, 0, 0, 0)!important;
    color: #FFFFFF!important;
  }

  .load-more-button
  {
    background: transparent!important;
  }

  [role="alert"],
.content-region,
.yt-uix-checkbox-on-off .checked:before
  {
    display: none!important;
  }

  :not(#​) :link
  {
    color: #99CCFF!important;
  }

  :not(#​) :visited
  {
    color: #CC99FF!important;
  }

  :not(#​) select
  {
    background: black!important;
  }

  [href="/"][id*="logo"],
:not(.ytp-volume-slider-handle):not(.yt-uix-playlistlike):before,
.yt-uix-button-icon-material-upload,
.yt-uix-button-icon-bell,
.yt-uix-button-icon-wrapper > .yt-sprite,
.autoplay-info-icon,
.search-button > .yt-uix-button-content,
#appbar-guide-button .yt-uix-button-icon-wrapper,
.yt-uix-button-shelf-slider-pager
  {
    -webkit-filter: invert(1)hue-rotate(180deg);
    filter: invert(1)hue-rotate(180deg);
  }

  .yt-uix-form-input-select,
[class$="container"],
#yt-comments-paginator
  {
    background-image: none!important;
  }

  :not(h2):not(button):not(.comment-simplebox-renderer-collapsed-content),
#footer-container button,
.yt-uix-button-shelf-slider-pager
  {
    border: none!important;
  }

  textarea,
[contenteditable="true"],
[type="text"]:not(#masthead-search-term),
[role="menu"]:not(.guide-user-links):not(.ytp-panel-menu),
#footer-container .yt-uix-button-default,
.yt-uix-clickcard-card-visible,
#watch-appbar-playlist,
.comment-simplebox-frame,
body > table
  {
    outline: none!important;
  }

  #masthead-search-terms
  {
    box-shadow: none!important;
  }

  .ytd-multi-page-menu-renderer .ytd-multi-page-menu-renderer
  {
    background-color: rgb(96, 109, 176)!important;
  }

  [menu-style="multi-page-menu-style-type-conversations"]
  {
    background-color: #606db0!important;
  }

  #creation-box
  {
    padding: 1rem;
    box-shadow: 0 15px 30px 0 rgba(0,0,0,0.11),0 5px 15px 0 rgba(0,0,0,0.08);
    border-radius: 0.5rem;
  }

  #placeholder-area
  {
    padding: 1rem;
    box-shadow: 0 15px 30px 0 rgba(0,0,0,0.11),0 5px 15px 0 rgba(0,0,0,0.08);
    border-radius: 0.5rem;
  }

  #simple-box
  {
    padding: 1rem;
    box-shadow: 0 15px 30px 0 rgba(0,0,0,0.11),0 5px 15px 0 rgba(0,0,0,0.08);
    border-radius: 0.5rem;
  }

  [role="button"].ytd-subscribe-button-renderer
  {
    color: white!important;
    background-color: rgba(0, 0, 0, 0)!important;
    padding: 1rem;
    box-shadow: 0 15px 30px 0 rgba(0,0,0,0.11),0 5px 15px 0 rgba(0,0,0,0.08);
    border-radius: 0.5rem;
  }

  [role="button"].ytd-button-renderer
  {
    padding: 1rem;
    box-shadow: 0 15px 30px 0 rgba(0,0,0,0.11),0 5px 15px 0 rgba(0,0,0,0.08);
    border-radius: 0.5rem;
    background-color: #00000030!important;
  }

  .ytd-item-section-renderer
  {
    box-shadow: 0 15px 30px 0 rgba(0,0,0,0.11),0 5px 15px 0 rgba(0,0,0,0.08);
    border-radius: 0.5rem;
  }

  #content.ytd-playlist-panel-supported-items
  {
    border-top-style: solid!important;
    border-top-width: 1px!important;
    border-top-color: black!important;
    border-bottom-style: solid!important;
    border-bottom-width: 1px!important;
    border-bottom-color: black!important;
    box-shadow: inset 0 5px 5px 0 rgba(0,0,0,0.11),inset 0 5px 5px 0 rgba(0,0,0,0.08);
    margin-top: 1px;
    background-color: #00000030!important;
  }

  #content.ytd-playlist-panel-supported-items:hover
  {
    background-color: rgba(0, 0, 255, .1)!important;
  }

  #playlist
  {
    padding: 1rem;
    box-shadow: 0 15px 30px 0 rgba(0,0,0,0.11),0 5px 15px 0 rgba(0,0,0,0.08);
    border-radius: 0.5rem;
  }

  .ytd-watch-next-secondary-results-renderer
  {
    padding: 1rem;
    box-shadow: 0 15px 30px 0 rgba(0,0,0,0.11),0 5px 15px 0 rgba(0,0,0,0.08);
    border-radius: 0.5rem;
  }

  .ytd-grid-renderer
  {
    border-radius: 0.5rem;
    box-shadow: inset 0 0px 0px 0 rgba(0, 0, 0, .3),0 5px 15px 0 rgba(0, 0, 0, .3);
  }

  [role="listbox"]
  {
    background-color: rgba(0, 0, 0, .87)!important;
  }

  [role="menuitem"]:hover
  {
    background-color: rgba(0, 0, 255, .42)!important;
  }

  #container.ytd-masthead
  {
    background-color: rgba(0, 0, 0, .48)!important;
  }

  #search-input
  {
    background-color: rgba(0, 0, 0, .48)!important;
    outline: none!important;
  }

  #search
  {
    outline: none!important;
    color: #6774ba!important;
  }

  .fl:before
  {
    content: ".: Theme By Tanuki (AdiTio) :.";
  }

  .yt-icon
  {
    -webkit-filter: drop-shadow( 1px 1px 3px rgba(0, 0, 0, .7));
    filter: drop-shadow( 1px 1px 3px rgba(0, 0, 0, .7));
  }
}

@-moz-document regexp("https://apis.google.com/u/0/_/hovercard.*")
{
  *
  {
    background-color: #000!important;
    color: #fff!important;
    border-color: #000!important;
  }
}
