/* ==UserStyle==
@name         Racedepartment UserCSS style
@namespace    github.com/openstyles/stylus
@version      1.0.0
@license      https://creativecommons.org/licenses/by-nc-sa/4.0/
@preprocessor default
==/UserStyle== */

@-moz-document domain("racedepartment.com") {

.xb-toggle-default .p-header-inner,
.xb-toggle-default .p-nav-inner,
.xb-toggle-default .p-body-inner,
.xb-toggle-default .p-footer-inner {
    max-width: inherit;
    max-width: 1440px;
    max-width: 75%;
}
.p-body-main--withSidebar.p-body-main--withSideNav .p-body-content,
.p-body-main--withSidebar .p-body-content {
    max-width: inherit;
}
.p-body-sidebar {
    display: none;
}
.p-body-left, 
ins.adsbygoogle {
    display: none;
}
.p-footer-copyrightRow {
    display: none;
}
.node-body .node-main {
    width: inherit;
}
.node-extra {
    margin-right: 20px;
}
.uix_page--fixed.sidebarNav--active .p-body-inner {
    left: inherit;
}
.uix_welcomeSection {
    display: none;
}
.style-dark .p-body-main::selection {
    background-color:  #8ebf42;
    color: #3b3b3d;
}
.samCodeUnit,
.gridContainerAmsTileView {
    display: none;
}

.template-xfrm_category_view.template-xfrm_overview .p-body-sidebarCol,
.template-xfrm_overview .p-sidebarWrapper,
.template-forum_view .p-body-sidebarCol,
.template-forum_view .p-sidebarWrapper,
.template-category_view .p-sidebarWrapper,
.template-thread_view .p-sidebarWrapper,
.template-thread_view .p-body-sideNavCol, 
.template-thread_view .p-body-sidebarCol,
.template-category_view .p-body-sideNavCol,
.template-category_view .p-body-sidebarCol,
.template-xfrm_category_view .p-body-sidebarCol,
.template-xfrm_category_view .p-sidebarWrapper,
.template-xfrm_category_view .p-body-sidebar,
.template-xfrm_thread_view_type_resource .p-body-sideNavCol,
.template-xfrm_thread_view_type_resource .p-body-sidebarCol, 
.template-xfrm_thread_view_type_resource .p-sidebarWrapper {
    display: none;
    width: 0;
}


}
