/*
	==== MyAnimeList.net - Night Theme PROTOTYPE ====
	Once based on MyAnimeList.net Dark Theme (styleID:5263) by Mathes (UserID:6092)
	But a lot has changed since then ;)

	==== Bookmarks ====
	[Z-RplImg] Bookmark for replacement images and icons.
	[Z-ColFix] Bookmark for my colour fixes.
	[Z-PbrRpl] Bookmark for "progress" bars replacement colours.

	==== Full Change Log ====
	0.0.0 - Constant work in progress!
	==== End Change Log ====
*/
@-moz-document regexp(".*myanimelist\\.net/(?!animelist|mangalist).*"){
  /* Global Defaults */
  body, #myanimelist, #content, #fancy_bg {
    color:					#DDD !important;
    background:				#111 !important;
    margin:					0 auto !important;
  }
  #content, #contentHome, .container, #rightcontentunder {
    border:					0 !important;
    background:				transparent !important;
  }

  /* Headers */
  #header, #headerSmall {
    display:				none !important;
  }
  .inboxsent_header {
    border-color:			#444 !important;
    background-color:		#458 !important;
    color:					#030303 !important;
  }
  .normal_header {
    border-color:			#333 !important;
    color:					#458 !important;
  }

  /* * Navigator * */
  /* Main */
  #nav li a, #nav a:visited {
    color:					#DDD !important;
    background:				#458 !important;
  }
  #nav li a:hover, #nav a:active, #nav li.hover a {
    color:					#DDD !important;
    background:				#333 !important;
  }
  #nav li .new a, #nav li .new a:visited, #nav li .new a:active, #nav li .new a:hover, .count {
    font-weight:			bold !important;
    color:					#F33 !important;
  }

  /* Sub */
  #nav ul a, #nav ul a:visited {
    color:					#DDD !important;
    background:				#333 !important;
  }
  #nav ul a:hover, #nav ul a:active {
    background:				#458 !important;
  }
  #menu_left, #menu, #menu_right, #menu_left #nav>li>ul>li>span a:hover {
    background:				#458 !important;
  }

  /* Input Text Fields */
  .inputtext, .textarea {
    border-color:			#333 !important;
    background-color:		#1F1F1F !important;
    color:					#AAA !important;
  }
  .inputtext:hover, .textarea:hover  {
    background-color:		#090909 !important;
  }

  .inputButton, .button {
    border-top-color:		#666 !important;
    border-left-color:		#666 !important;
    border-bottom-color:	#222 !important;
    border-right-color:		#222 !important;
    background-color:		#236 !important;
  }

  h1 {
    border-top:				0 !important;
    border-left:			0 !important;
    border-right:			0 !important;
    border-bottom:			solid 1px #458 !important;
    background:				#222 !important;
    color:					#458 !important;
  }

  h2 {
    color:					#458 !important;
    border-bottom:			solid 1px #333 !important;
  }

  .mini_header {
    background-color:		#222 !important;
    color:					#458 !important;
    border-bottom:			solid #458 1px !important;
  }

  /* Links and Texts */
  a,a:visited {
    color:					#458 !important;
  }
  a:focus {
    outline:				none !important;
  }
  a:hover {
    color:					#67A !important;
    text-decoration:		none !important;
  }
  .lightLink, .lightLink:link, .lightLink:visited, .lightLink:active {
    color:					#666 !important;
  }
  .dark_text, .dark_text:hover,.dark_text:visited , .dark_text_normal, .dark_text_normal:hover,.dark_text_normal:visited  {
    color:					#236 !important;
  }
  .bgColor1 .dark_text, .bgColor1 .dark_text:hover, .bgColor1 .dark_text:visited, .bgColor1 .dark_text_normal,
  .bgColor1 .dark_text_normal:hover, .bgColor1 .dark_text_normal:visited  {
    color:					#458 !important;
  }
  .colored_text {
    color:					#333 !important;
  }

  /* Horizontal Navs */
  #horiznav_nav {
    border-color:			#458 !important;
  }
  #horiznav_nav li {
    background-color:		#111 !important;
  }
  #horiznav_nav ul li a {
    color:					#458 !important;
    background-color:		#222 !important;
  }
  #horiznav_nav ul li a:hover {
    background-color:		#458 !important;
    color:					#AAA !important;
  }
  #horiznav_nav ul li .horiznav_active {
    background-color:		#458 !important;
    color:					#AAA !important;
  }

  /* Results */
  .goodresult {
    color:					#AAA !important;
    background-color:		#121 !important;
    border-color:			#4A4 !important;
  }
  .goodresult a:link,.goodresult a:active,.goodresult a:visited {
    color:					#347 !important;
  }
  .badresult {
    color:					#AAA !important;
    background-color:		#211 !important;
    border-color:			#A44 !important;
  }
  .badresult a:link,.badresult a:active,.badresult a:visited  {
    color:					#347 !important;
  }

  /* Profile */
  .profile_leftcell {
    border:					0 !important;
  }
  #profileRows a {
    color:					#458 !important;
    font-weight:			bold !important;
    border-color:			#333 !important;
  }
  #profileRows a:hover {
    color:					#AAA !important;
    background-color:		#458 !important;
    border-color:			#333 !important;
  }

  /* Friends */
  .friendHolder {
    background-color:		#222 !important;
  }
  .bgLightBlue {
    background-color:		#223 !important;
  }

  /* Useless borders... */
  .borderClass {
    border:					0 !important;
  }
  .borderDark, .borderLight, div[style="border: 1px solid #bebebe; padding: 2px; margin: 4px 2px 2px 2px;"], div[style="border: 1px solid #ebebeb; padding: 1px; margin: 4px 2px 2px 2px;"] {
    border-color:			#333 !important;
    overflow:				auto !important;
  }
  div[style="margin: 0pt 10px 10px 0pt; float: left; display: none;"] {
    border:					1px solid #333 !important;
  }

  /* Messages */
  .row_highlighted {
    background-color:		#222 !important;
    border-color:			#458 !important;
  }
  .row_not_highlighted {
    background-color:		#111 !important;
    border-color:			#333 !important;
  }	

  /* Info bubble */
  .hoverinfo {
    color:					#DDD !important;
    background-image:		none !important;
    background-color:		#111 !important;
    border-color:			#458 !important;
    border-style:			solid !important;
    border-width:			1px !important;
    z-index:				9999 !important;
  }

  .bgbdrContainer {
    background-color:		#111 !important;
    border:					1px solid #333 !important;
  }

  .bgColor1 {
    background-color:		#1A1A1A !important;
  }
  .bgColor2, .bgColor, .bgNone {
    background-color:		#222 !important;
  }

  div.picSurround img {
    background-color:		#111 !important;
    border:					1px solid #333 !important;
  }

  .button_form {
    background-color:		#1A1A1A !important;
    border:					none !important;
  }

  .button_form:hover {
    background-color:		#222 !important;
    border:					none !important;
  }

  #dialog {
    border:					10px solid #111 !important;
  }
  #dialog td {
    border:					1px solid #333 !important;
  }

  #copyright {
    /*display:				none !important;*/
    color:					#666 !important;
  }
  #copyright:after {
    content:				' Night Theme by Zealotus.' !important;
  }

  #panel_left {
    border-right:			0 !important;
  }

  .vote_container {
    background:				#CBB !important;
  }
  .message_separator {
    background:				#522 !important;
  }
  .forum_boardrow1 {
    background:				#111 !important;
    border:					#333 !important;
  }
  .forum_boardrow2 {
    background:				#222 !important;
    border:					#333 !important;
  }
  .forum_boardrowspacer {
    background:				transparent !important;
  }
  .forum_category {
    background:				#458 !important;
  }
  .forum_category a {
    color:					#AAA !important;
  }
  #forum_options_container ul li {
    border-color:			#458 !important;
  }
  .quotetext, .codetext {
    background:				#222 !important;
    border:					1px solid #333 !important;
  }
  .sig {
    border-color:			#333 !important;
  }

  /* Shared Anime/Manga page fix */
  td[style='background-color: rgb(246, 246, 246);'] {
    background-color:		#222 !important;
  }
  span[style='color: rgb(0, 0, 255);'] {
    color:					#04F !important;
  }
  span[style='color: rgb(255, 0, 0);'] {
    color:					#E10 !important;
  }
  span[style=""] {
    color:					#2D2 !important;
  }

  /* ==== Feature Bookmarks ==== */

  /* ==== [Z-RplImg] START ==== */
  /* The icons */
  img[src*="images/good-off.gif"]		{ background: transparent url(data:image/png;base64,) no-repeat center center !important; }
  img[src*="images/good-on.gif"]		{ background: transparent url(data:image/png;base64,) no-repeat center center !important; }
  img[src*="images/bad-off.gif"]		{ background: transparent url(data:image/png;base64,) no-repeat center center !important; }
  img[src*="images/bad-on.gif"]		{ background: transparent url(data:image/png;base64,) no-repeat center center !important; }
  img[src*="images/cancel-fsg.gif"]	{ background: transparent url(data:image/png;base64,) no-repeat center center !important; }
  img[src*="images/watch_n.gif"]		{ background: transparent url(data:image/png;base64,) no-repeat center center !important; }
  img[src*="images/watch_y.gif"]		{ background: transparent url(data:image/png;base64,) no-repeat center center !important; } 
  img[src*="images/rss_small.gif"]	{ background: transparent url(data:image/png;base64,) no-repeat center center !important; }
  img[src*="images/reply.gif"]		{ background: transparent url(data:image/png;base64,) no-repeat center center !important; }

  /* Needed code for the replacement icins to work */
  img[src*="images/good-off.gif"], img[src*="images/good-on.gif"], img[src*="images/bad-off.gif"],
  img[src*="images/bad-on.gif"], img[src*="images/cancel-fsg.gif"] {
    height:					0 !important;
    width:					0 !important;
    margin:					0 auto !important;
    padding:				13px 13px 0 0 !important;
  }
  img[src*="images/watch_n.gif"], img[src*="images/watch_y.gif"] {
    height:					0 !important;
    width:					0 !important;
    margin:					0 auto !important;
    padding:				15px 15px 0 0 !important;
  }
  img[src*="images/reply.gif"], img[src*="images/rss_small.gif"] {
    height:					0 !important;
    width:					0 !important;
    margin:					0 auto !important;
    padding:				11px 11px 0 0 !important;
  }
  img[src*="images/ignorethread.gif"] {
    background:				transparent url(data:image/png;base64,) no-repeat center center !important;
    height:					0 !important;
    width:					0 !important;
    margin:					0 auto !important;
    padding:				12px 12px 0 0 !important;
  }
  /* Main Header */
  #header_surround {
    height:					49px !important;
    background:				transparent !important;
  }
  #header_left {
    height:					49px !important;
    background:				#111 url('http://i206.photobucket.com/albums/bb123/WhiteNemesis00/MAL/Night%20Theme/myanimelist-header-leftb6c.png') no-repeat top left !important;
  }
  #header_right {
    height:					49px !important;
    background:				#111 url('http://i206.photobucket.com/albums/bb123/WhiteNemesis00/MAL/Night%20Theme/myanimelist-header-rightb6c.png') no-repeat top left !important;
  }
  /* Re-enable "Add to List" icon */
  .button_add {
    height:					0 !important;
    width:					0 !important;
    border:					0 !important;
    font-size:				0 !important;
    margin:					0 auto !important;
    background:				transparent url(data:image/png;base64,) no-repeat center center !important;
    position:				relative !important;
    top:				2px !important;
    padding:				13px 13px 0 0 !important;
  }
  /* Re-enable "Edit List Entry" icon */
  .button_edit {
    height:					0 !important;
    width:					0 !important;
    border:					0 !important;
    font-size:				0 !important;
    margin:					0 auto !important;
    background:				transparent url(data:image/png;base64,) no-repeat center center !important;
    position:				relative !important;
    top:				2px !important;
    padding:				13px 13px 0 0 !important;
  }
  /* Maintenance image */
  img[src*="maintenance.png"] {
    background:				transparent url('http://i206.photobucket.com/albums/bb123/WhiteNemesis00/MAL/Night%20Theme/maintenance.png') no-repeat center center !important;
    height:					0 !important;
    width:					0 !important;
    margin:					0 auto !important;
    padding:				125px 250px !important;
  }
  /* ==== [Z-RplImg] END ==== */

  /* These are some colour fixes to make text in these colours readable. */
  /* ==== [Z-ColFix] START ==== */
  span[style="color: green;"]			{ color: #2D2 !important; }
  span[style="color: blue;"]			{ color: #04F !important; }
  span[style="color: red;"]			{ color: #E10 !important; }
  span[style="color: black;"]			{ color: #DDD !important; }
  span[style="color: white;"]			{ color: #111 !important; }
  span[style="color: darkblue;"]		{ color: #12B !important; }
  span[style="color: navy;"]			{ color: #12B !important; }
  /* ==== [Z-ColFix] END ==== */

  /* Replacement colours for the "progress" bars on profile pages. */
  /* ==== [Z-PbrRpl] START ==== */
  div[style*="background-color:#eaf0fa"]	{ background-color:#191b23 !important; }
  div[style*="background-color:#dbe5f6"]	{ background-color:#1b1f29 !important; }
  div[style*="background-color:#cddbf2"]	{ background-color:#1e222f !important; }
  div[style*="background-color:#bdceec"]	{ background-color:#202635 !important; }
  div[style*="background-color:#b0c4e8"]	{ background-color:#23293b !important; }
  div[style*="background-color:#a3bae3"]	{ background-color:#252c41 !important; }
  div[style*="background-color:#96b0de"]	{ background-color:#283047 !important; }
  div[style*="background-color:#8aa7d8"]	{ background-color:#2b334d !important; }
  div[style*="background-color:#7c9acf"]	{ background-color:#2d3652 !important; }
  div[style*="background-color:#7191c8"]	{ background-color:#303a58 !important; }
  div[style*="background-color:#6687c0"]	{ background-color:#323d5e !important; }
  div[style*="background-color:#5b7db8"]	{ background-color:#354165 !important; }
  div[style*="background-color:#5073af"]	{ background-color:#37446a !important; }
  div[style*="background-color:#476aa6"]	{ background-color:#3a4770 !important; }
  div[style*="background-color:#3e619d"]	{ background-color:#3c4b76 !important; }
  div[style*="background-color:#365a97"]	{ background-color:#3f4e7c !important; }
  div[style*="background-color:#2f528f"]	{ background-color:#415282 !important; }
  div[style*="background-color:#274a86"]	{ background-color:#445588 !important; }
  /* ==== [Z-PbrRpl] END ==== */
}

	/* ==== AD-BLOCK ==== */
@-moz-document domain(myanimelist.net) {
#leftcontent { height: 550px !important; overflow: hidden !important; }
.ad_300x250_center, .ad_border, .ad_728x90_center, #ad_nav_hide, #surround_nav_ad, #ad_panel_hide, #ad_panel, .adCenter728, .adCenter300, .adText, .adBlockNav, .adBlock, #copyright, .adCenter600, .adHome230, .adRight728, .ad300, .adHome300, #roadblock, #homeAds {
	display:	none !important;
}
}
