css:
@-moz-document url-prefix("https://www.seriouseats.com/") {
@media print {
  body > header,
  footer,
  div.leaderboard--header,
  div.post-footer,
  nav,
  #figure-wrapper--article__container_1-0,
  #article__header--project_1-0,
  #sources_1-0,
  #disqus-block_1-0,
  .post-footer,
  .mntl-sc-block-adslot,
  #mntl-leaderboard-spacer_1-0,
  #article-meta__feedback-container_1-0,
  #relish-save_1-0,
  #relish-shop_1-0,
  #feedback-block_1-0,
  #mntl-sc-block_3-0-20,
  #mntl-sc-block_3-0-21,
  #project-meta__comments-container_1-0 {
    display: none;
  }

  #main {
    margin-top: 0;
  }

  .text-passage ol,
  .text-passage ul,
  ul .text-passage {
    margin-bottom: 0.25rem;
  }
  .simple-list__item,
  .mntl-sc-block-group--LI .mntl-sc-block-html,
  .text-passage {
    font-size: 0.9rem;
  }
  .text-passage ol,
  .text-passage ul,
  ul .text-passage {
    margin-bottom: 0.5rem;
  }
}
}
