css:
@-moz-document domain("vk.com") {
body {
    --nav-bar-color: #3a4259;
    --nav-bar-icon-color: #656a87;
    --nav-bar-border-color: #464655;
    --top-search-color: #59678e;
    
    --background_content: #222230;
    --background_light: #3b3a4c;
    --background_lighter: #494b5c;
    --text_primary: #cfcfcf;
    --black: #cfcfcf;
    --gray_900: #bababf;
    --white: #3b3a4c;
    --bright_text: #e1e1e6;
    --separator_common: #7a7a95;
    --separator_alternate: #686873;
    --steel_gray_10: #3b3a4c;
    --steel_gray_80: #686873;
    --steel_gray_120: #7a7a95;
    --steel_gray_140: #7a7a95;
    --content_tint_background: #edeef01a;
    --text_link: #79b2e4;
    --text_name: #7faed8;
    --blue_600: #79b2e4;
    --background_page: #222230;
    --accent: #5f9ec4;
    --im_forward_line_tint: #5c84a8;
    --im_reply_separator: #5c84a8;
    
    --counter_secondary_background: #4d5467;
    
    --button-background-color: #638cb5;
    --button-secondary-background-color: var(--light_blue_300);
    --button-secondary-background-color-hover: var(--light_blue_400);
    --button-secondary-background-color-active: var(--light_blue_500);
    
    --page-block-shadow: 0 1px 0 0 var(--separator_alternate), 0 0 0 1px var(--separator_common);

}
body {
    color: var(--text_primary) !important;
    background: var(--background_page) !important;
}
::selection {
    background: #6a6a7a !important;
    color: #e2e2e2 !important;
}
.left_count_wrap {
    color: var(--text_primary) !important;
}



/* Top Bar */

[dir] #page_header_cont, #dev_top_nav_wrap {
    background-color: #3a4259 !important;
    border-bottom: 1px solid #464655 !important;
}
#ts_input {
    background-color: #4d5467 !important;
    border: 1px solid #49506392;
}
[dir="ltr"] body.dev input.dev_top_input {
    padding: 8px 6px 6px 29px !important;
    background-position: 8px 8px !important;
    background-color: #4d5467 !important;
    filter: grayscale(0.7) !important;
    color: #fff;
}
[dir] .TopNavBtn#top_notify_btn .TopNavBtn__icon {
    background: url("data:image/svg+xml,%3Csvg height='20' viewBox='0 0 20 20' width='20' xmlns='http://www.w3.org/2000/svg'%3E%3Cpath d='m8 18c-1.35416667 0-2.5-.7-2.5-2h5c0 1.3-1.14583333 2-2.5 2zm5.9956933-7c0 1.5 2.0043067 1.5 2.0043067 2.5 0 .5 0 1.5-2.1044016 1.5h-11.91128855c-1.98430985 0-1.98430985-1-1.98430985-1.5 0-1 1.98430985-1 2-2.5v-3c0-4 2.09724901-6.5 5-6.9v-.1c0-.6.5-1 1-1s1 .4 1 1v.1c2.902751.4 4.9956933 2.9 4.9956933 6.9z' fill='%235a6985' fill-rule='evenodd' transform='translate(2 1)'/%3E%3C/svg%3E") center center / 23px !important;
}
[dir] .TopNavBtn.TopNavBtn__audio .TopNavBtn__icon {
    background: url("data:image/svg+xml,%3Csvg height='20' viewBox='0 0 20 20' width='20' xmlns='http://www.w3.org/2000/svg'%3E%3Cpath d='m14 11.1552322v-5.6552322l-7 2v7.7999878s-.00076021-.000114-.00225099-.0003451c-.00469478 1.1500293-1.0157329 2.2529207-2.45934692 2.5862052-1.63053306.3764383-3.19546426-.3714797-3.49537168-1.6705214-.29990741-1.2990417.7787778-2.6572866 2.40931086-3.0337248.53731735-.1240495 1.06751079-.1260016 1.54765873-.0263695v-9.1596547c0-.55074053.42843442-1.11242325.95693506-1.25655979l9.08612984-2.47803542c.5257221-.14337873.9569351.18658106.9569351.73867844v12.30032707c-.000747-.000116-.0014973-.000235-.0022509-.0003569-.0046879 1.1500335-1.015728 2.2529313-2.459347 2.586217-1.6305331.3764383-3.1954643-.3714797-3.4953717-1.6705214s.7787778-2.6572866 2.4093109-3.0337248c.5373173-.1240495 1.0675108-.1260016 1.5476587-.0263695z' fill='%235a6985' fill-rule='evenodd' transform='translate(1 1)'/%3E%3C/svg%3E") center center / 24px !important;
}
.TopNavBtn:hover .TopNavBtn__icon {
    filter: brightness(1.85);
}
[dir] #top_notify_count {
    border: 2px solid #565252 !important;
}
.top_notify_header {
    color: var(--text_primary) !important;
}



/* Notification Center */

#top_notify_wrap {
    border: 1px solid var(--separator_common) !important;
}
.top_notify_show_all {
    border-top: 1px solid var(--separator_common) !important;
}
[dir] .feedback_row, [dir] .feedback_sticky_row {
    border-top: 1px solid var(--separator_alternate) !important;
}
.feedback_row_clickable:not(.feedback_row_touched):hover {
    background-color: transparent !important;
    border-bottom: 1px solid #292d36 !important;
}
.top_notify_cont .feedback_row:not(.dld).hover,
.top_notify_cont .feedback_row:not(.dld):hover,
.top_notify_show_all:hover {
    background-color: #edeef01a !important;
}
.mention_tt_actions {
    background-color: var(--background_content);
    border-top: 1px solid var(--background_content);
}
.feedback_header .author,
.feedback_header .mem_link,
.feedback_header .group_link,
.ts_contact_name {
    color: var(--text_link);
}
#top_notify_wrap {
    border: 1px solid var(--separator_alternate) !important;
}
.ts_cont_wrap {
    background: var(--background_light) !important;
    border: 1px solid var(--separator_common) !important;
}
a.ts_contact.active, a.ts_search_link.active {
    background-color: #edeef01a !important;
}

.top_notify_cont,
.top_notify_cont .feedback_header {
    color: var(--text_primary) !important;
}
.LeftMenu__icon {
    color: #79b2e4 !important;
}


/* IM Page */

.ui_rmenu_slider._ui_rmenu_slider {
    width: 2.5px !important;
}
.ui_rmenu_item_sel, [dir="ltr"] .ui_rmenu_item_sel:hover {
    border-left: 2px solid #5f9ec4;
}
[dir="ltr"] .im-page_classic.im-page .im-page--header,
[dir="rtl"] .im-page_classic.im-page .im-page--header {
    border-left: solid 1px var(--background_page) !important;
    border-right: solid 1px var(--background_page) !important;
}
.im-page--dialogs-search .ui_search_input_block {
    background: var(--background_highlighted) !important;
}
#im_dialogs_search {
    background-color: transparent !important;
}
#im_dialogs {
    background: none !important;
}
.im-page_classic .im-page--chat-header::after {
    display: block;
    content: '';
    position: absolute;
    top: -15px;
    right: -3px;
    width: 5px;
    height: 13px;
    background: var(--background_page);
}
.im-page--title-main-inner {
    color: #e3e3e8 !important;
}
.nim-dialog {
    background: rgba(147, 173, 200, 0.05) !important;
}
.nim-dialog.nim-dialog_unread {
    background: rgba(147, 173, 200, 0.17) !important;
}
.nim-dialog, .nim-dialog:hover {
    border-top: 1px solid #7a7a95 !important;
}
.nim-dialog_unread-out .nim-dialog--inner-text {
    color: #dcdcdc !important;
}
[dir] .nim-dialog .nim-dialog--content {
    border-top: 1px solid transparent !important;
}
.nim-dialog .nim-dialog--text-preview, .nim-dialog .nim-dialog--preview {
    color: var(--text_secondary) !important;
}
.nim-dialog:not(.nim-dialog_deleted).nim-dialog.nim-dialog_classic.nim-dialog_unread,
.nim-dialog:not(.nim-dialog_deleted).nim-dialog.nim-dialog_hovered,
.nim-dialog:not(.nim-dialog_deleted).nim-dialog:hover,
.nim-dialog:not(.nim-dialog_deleted).nim-dialog_hovered + .im-page--messages-search,
.nim-dialog:not(.nim-dialog_deleted).nim-dialog_hovered + .nim-dialog,
.nim-dialog:not(.nim-dialog_deleted).nim-dialog_selected + .im-page--messages-search,
.nim-dialog:not(.nim-dialog_deleted).nim-dialog_selected + .nim-dialog,
.nim-dialog:not(.nim-dialog_deleted).nim-dialog_unread.nim-dialog_classic + .im-page--messages-search,
.nim-dialog:not(.nim-dialog_deleted).nim-dialog_unread.nim-dialog_classic + .nim-dialog,
.nim-dialog:not(.nim-dialog_deleted):hover + .im-page--messages-search,
.nim-dialog:not(.nim-dialog_deleted):hover + .nim-dialog {
    border-top: 1px solid #7a7a95 !important;
}


/* Dialog Page */

[dir] .im-page .im-page--top-date-bar_visible .im-page--history-new-bar_days.im-page--date-bar-transition > span {
    border-color: #3f3f4e !important;
}
.im-page .im-page--top-date-bar {
    color: var(--text_secondary) !important;
}
[dir] .im-page .im-page--top-date-bar {
    background: #3b3e4c !important;
    border: 1px solid #3f3f4e !important;
}
[dir] .CallSnippet {
    background: var(--background_highlighted) !important;
    border: 1px solid var(--separator_alternate) !important;
    border-radius: 5px !important;
}
.CallSnippet__title {
    color: var(--text_name) !important;
}
[dir] .CallSnippet__users:empty {
    background: url("data:image/svg+xml;charset=utf-8,%3Csvg%20width%3D%2224%22%20height%3D%2224%22%20xmlns%3D%22http%3A%2F%2Fwww.w3.org%2F2000%2Fsvg%22%3E%3Cpath%20d%3D%22M13.889%2014.893c1.355-1.352%203.383-1.087%205.289.512%201.366%201.146%201.183%204.14-1.724%204.53-2.92.393-5.983-1.01-9.192-4.21C5.054%2012.527%203.751%209.772%204.04%206.56c.261-2.913%203.36-3.122%204.544-1.718%201.54%201.826%202.009%203.784.515%205.273-.69.689-.974.855-.802%201.32.592%201.606%203.174%203.856%204.27%204.258.51.187.668-.147%201.323-.8z%22%20fill%3D%22%23689AC7%22%2F%3E%3C%2Fsvg%3E") !important;
}
[dir="ltr"] .mail_link {
    border-left: 2px solid var(--im_forward_line_tint) !important;
}
.im-replied::before {
    opacity: .68 !important;
}
.im-replied:hover::before {
    opacity: .8 !important;
}
.page_doc_row .page_doc_title {
    color: var(--text_link) !important;
}
.online::after, .nim-peer.online::after {
    border-color: #30303a !important;
}
.online.mobile::after {
    background-color: #30303a !important;
}
[dir] .im-mess .im-mess--check {
    filter: saturate(1.15) brightness(1.05) hue-rotate(-5deg) contrast(1.25);
}
.im-mess:hover:not(.im-mess_selected) .im-mess--check, .history_a11y .im-mess.im-mess_selected .im-mess--check {
    opacity: 0.73 !important;
}
.history_a11y .im-mess.im-mess_selected, .history_a11y .im-mess.im-mess_selected:hover .im-mess--check {
    opacity: 0.96 !important;
}
[dir] .im-mess.im-mess_unread, [dir] .im-mess.im-mess_selected:not(.im-mess_is_editing), [dir] .im-mess.im-mess_selected:not(.im-mess_is_editing):hover {
    background: var(--background_light) !important;
}
[dir] .im-mess.im-mess_unread + .im-mess::before,
[dir] .im-mess.im-mess_selected + .im-mess::before {
    background: var(--background_light) !important;
}



/* Emoji Popup */

[dir] .emoji_tabs {
    background: var(--background_light) !important;
}
.emoji_tab {
    background: var(--background_light) !important;
}
[dir] .emoji_tab_sel, [dir] .emoji_tab_sel:hover {
    background: var(--background_lighter) !important;
    box-shadow: inset 0px 0px 2px 1px rgba(0, 0, 0, 0.2), inset 0px 0px 0px 1px rgba(30, 20, 30, 0.12);
}
[dir] .emoji_tabs_l_s, [dir] .emoji_tabs_r_s {
    background: var(--background_light) !important;
}
[dir] .emoji_shop:hover {
    background: var(--background_lighter) !important;
}
[dir] .emoji_shop_icon_badge {
    background: var(--orange_fire);
    color: var(--text_primary) !important;
}
[dir] .emoji_shop_icon_badge, [dir] .emoji_shop_icon_badge:hover {
    border: 1px solid #999da3 !important;
}
[dir] .emoji_tt_wrap.tt_down::before {
    border-top-color: var(--separator_common) !important;
}
[dir] .emoji_tt_wrap.tt_down::after {
    border-top-color: var(--background_light) !important;
}
[dir] .emoji_shop:hover + [dir] .emoji_tt_wrap.tt_down::after {
    border-top-color: var(--background_lighter) !important;
}
[dir="ltr"] .ui_scroll_default_theme.ui_scroll_emoji_theme > .ui_scroll_overflow > .ui_scroll_shadow_top, [dir="ltr"] .ui_scroll_default_theme.ui_scroll_emoji_theme > .ui_scroll_overflow > .ui_scroll_shadow_bottom {
    background: linear-gradient(to bottom, var(--background_light) 0%, var(--background_light) 30%, transparent 90%) !important;
}
[dir] .emoji_tabs_l_sc {
    background: linear-gradient(to right, var(--background_light) 0%, var(--background_light) 10%, transparent 100%) !important;
}
[dir] .emoji_tabs_r_sc {
    background: linear-gradient(to left, var(--background_light) 0%, var(--background_light) 10%, transparent 100%) !important;
    width: 10px !important;
}
.emoji_stickers_pack_title {
    color: var(--text_secondary) !important;
    filter: brightness(1.03);
}



/* Stickers Explore Window */

[dir] .im_stickers_promo_banner, [dir] .im_stickers_bl_wrap {
    background: var(--background_content) !important;
}
.im_sticker_bl_name {
    color: var(--text_primary) !important;
}
.im_sticker_bl_desc {
    color: var(--text_secondary) !important;
}



/* Sticker Pack Window */

[dir] .StickerPackPreview__header {
    background: var(--background_light) !important;
    border-bottom: 1px var(--separator_alternate) solid !important;
}
.StickerPackPreview__title {
    color: var(--text_primary) !important;
}
.StickerPackPreview__author {
    color: var(--text_secondary) !important;
}
.Button--primary {
    color: var(--bright_text) !important;
}
[dir] .StickerPackPreview__footer {
    background: var(--background_light) !important;
    border-top: 1px var(--separator_alternate) solid !important;
}
.StickerPackPreview__footerNote {
    color: var(--text_secondary) !important;
}
.StickerImage__image {
    filter: drop-shadow(0px 0px 5px #b8b8d7);
}
.StickerPackPreview__footerContainer {
    background: #545366 !important;
}



/* Edit Profile Button */

.flat_button.secondary, .button_gray button {
    color: #3d3d3d !important;
}
.flat_button.secondary:hover, .button_gray button:hover {
    color: #2a2a2a !important;
}
.flat_button.secondary:active, .button_gray button:active {
    color: #999 !important;
}



/* Music Search Field */

.ui_search_new.ui_search_field_empty .ui_search_button_search {
    background-color: var(--background_light) !important;
    border: 1px solid var(--separator_common) !important;
    height: 35.5px !important;
    border-left: none !important;
}
.ui_search_new.ui_search_field_empty .ui_search_button_search:hover {
    background-color: #535672 !important;
}
input.ui_search_field {
    background-color: var(--background_light) !important;
    color: var(--text_primary) !important;
}
.ui_search_new .ui_search_input_inner {
    border: 1px solid var(--separator_common) !important;
}



/* Profile Editor */

#profile_editor input.dark[disabled] {
    background: var(--background_light) !important;
    border-color: var(--separator_common) !important;
}
.pedit_separator {
    border-color: var(--separator_common) !important;
}
.result_list {
    border: 1px solid var(--separator_common) !important;
}
.result_list ul li.active {
    background: var(--background_light) !important;
}
.result_list ul li {
    background: var(--background_content) !important;
}
.result_list ul li, .result_list ul li.active {
    color: var(--text_primary) !important;
}
.selector_focused table.selector_table {
    border-color: var(--separator_common) !important;
}
.selector_container table.selector_table {
    border: 1px solid var(--separator_common) !important;
}
.selector_container {
    background: var(--background_light) !important;
    color: var(--text_primary) !important;
}
.selector_input {
    color: var(--text_primary) !important;
}
[dir] .im-chat-input.im-chat-input_error {
    background: var(--background_light) !important;;
}
input.text, input.text ~ .placeholder .ph_input,
input.search, input.search ~ .placeholder .ph_input, input.big_text,
input.big_text ~ .placeholder .ph_input, input.dark, input.dark ~ .placeholder .ph_input,
.fakeinput, .fakeinput ~ .placeholder .ph_input,
div[contenteditable="true"], div[contenteditable="true"] ~ .placeholder .ph_input,
textarea, textarea ~ .placeholder .ph_input {
    border: 1px solid var(--separator_common) !important;
}
[dir] div.submit_post_field, [dir] div.submit_post_field[contenteditable="true"],
[dir] div.submit_post_field ~ .placeholder .ph_input,
[dir] div.submit_post_field[contenteditable="true"] ~ .placeholder .ph_input {
    border: none !important;   
}
[dir] div[contenteditable="true"] ~ .placeholder .ph_input {
    border-color: transparent !important;
}
[dir] .im-chat-input .im-chat-input--txt-wrap {
    border: none !important;
}
[dir] .im-page_classic.im-page .im-page--chat-body-wrap-inner {
    border-color: transparent !important;
}
[dir] .im-navigation--to-end .im-navigation__button {
    background-color: #4d5467 !important;
}
[dir] .im-navigation--to-end .im-navigation__button:hover {
    background-color: #434558 !important;
}
[dir] .im-navigation__button {
    border: 1px solid var(--separator_alternate) !important;
}
[dir] .SitpostingEntrypoint::before {
    background-color: var(--steel_gray_500) !important;
}

.ProfileEditorVkconnect__notice {
    background: var(--background_light) !important;
}
.trg-b-banner .trg-b-text-c {
    filter: invert(0.76);
}
.ads_ad_box.redesign .ads_ad_description {
    color: var(--text_primary) !important;
}

[dir] .box_title_wrap {
    background: var(--background_lighter) !important;
}
.box_title {
    color: var(--text_primary) !important;
}




/* Audio Page Player */

.audio_page_player_wrap {
    background-color: var(--background_light) !important;
}
[dir] .audio_page_player2.audio_page_player_fixed {
    box-shadow: 0 1px 0 0 var(--separator_common) !important;
    background-color: var(--background_light) !important;
}
[dir] .slider .slider_amount, [dir] .slider .slider_back {
    background-color: #5c8ebc !important;
}
[dir] .audio_page_player2 .audio_page_player_track_slider.slider.slider_size_1 .slider_back, [dir] .audio_page_player2 .audio_page_player_volume_slider.slider.slider_size_1 .slider_back {
    background-color: #636878 !important;
}
[dir] .audio_page_player2 .audio_page_player_prev .icon {
    background-image: url("data:image/svg+xml;charset=utf-8,%3Csvg%20xmlns%3D%22http%3A%2F%2Fwww.w3.org%2F2000%2Fsvg%22%20width%3D%229%22%20height%3D%2210%22%20viewBox%3D%220%200%209%2010%22%3E%3Cpath%20fill%3D%22%23567CB5%22%20fill-rule%3D%22evenodd%22%20d%3D%22M2%204.709V.596A.604.604%200%200%200%201.4%200H.6C.27%200%200%20.267%200%20.596v8.808c0%20.33.278.596.6.596h.8c.331%200%20.6-.267.6-.596V5.291c.033.04.074.079.123.114L8.19%209.803c.447.324.81.138.81-.408V.604c0-.55-.359-.735-.81-.408L2.123%204.594A.65.65%200%200%200%202%204.71z%22%2F%3E%3C%2Fsvg%3E") !important;
}
[dir] .audio_page_player2 .audio_page_player_next .icon {
    background-image: url("data:image/svg+xml;charset=utf-8,%3Csvg%20xmlns%3D%22http%3A%2F%2Fwww.w3.org%2F2000%2Fsvg%22%20width%3D%229%22%20height%3D%2210%22%20viewBox%3D%220%200%209%2010%22%3E%3Cpath%20fill%3D%22%23567CB5%22%20fill-rule%3D%22evenodd%22%20d%3D%22M7%204.709V.596C7%20.266%207.278%200%207.6%200h.8c.331%200%20.6.267.6.596v8.808a.604.604%200%200%201-.6.596h-.8a.598.598%200%200%201-.6-.596V5.291a.653.653%200%200%201-.123.114L.81%209.803c-.447.324-.81.138-.81-.408V.604c0-.55.359-.735.81-.408l6.067%204.398A.65.65%200%200%201%207%204.71z%22%2F%3E%3C%2Fsvg%3E") !important;
}
[dir] .audio_page_player2 .slider_hint.audio_player_hint {
    background-color: #4d5467 !important;
}
[dir] .audio_page_player2 .slider_hint.audio_player_hint::after {
    border-top-color: #4d5467 !important;
}
.audio_page_player2 .slider_hint.audio_player_hint {
    color: #a0a3ac !important;
}



/* Audio Popup */

[dir] .audio_layer_container .audio_page_player_wrap {
    background-color: var(--background_light) !important;
    border-bottom: 1px solid var(--separator_common) !important;
}
.page_block_h2 {
    background-color: var(--background_light) !important;
}
.PlaceholderSmall__title {
    color: var(--text_primary) !important;
}
.PlaceholderSmall__text {
    color: var(--text_secondary) !important;
}
.audio_page__shuffle_all .audio_page__shuffle_all_button {
    color: var(--text_primary) !important;
