css:
/*
Author      : lrjff
Updated     : August 24, 2009
Website     : http://armatoste.net/
Description : More legible version of Opera Portal Beta
*/

@-moz-document domain("portal.opera.com") {

html{background:#ddd !important;}
body{font-family:"Lucida Grande", "Lucida Sans Unicode", Verdana, Tahoma !important;}
h4,.twitter-user,#main{font-size:16px !important;}
#main,.feed-content{line-height:1.625 !important;}

.last a,#tabs a,.pub,.feed-pub,.feed-content a,#ticker{font-weight:700;}

#beta-ticker{display:none !important;}

.game-list{font-size:13px !important;line-height:1.23 !important;}

.box a.min {background-position: -167px -543px !important;}
.box a.configure{background-position: -170px -582px !important;}
.box a.close{background-position: -169px -619px !important;}

.pub,.feed-pub{font-size:80% !important;}

.feed-title:hover,.feed-content a{text-decoration:underline !important;}
.feed-content a:hover{text-decoration:none !important;}

.feed .inner li.odd:hover,.feed .inner li.even:hover{background-color:#e3e3e3 !important;}

}
