css:
@-moz-document domain("angband.readthedocs.io") {
html, body, div, p, table, td, li, h1, h2, h3, h4, h5, h6 {
    font-family: Bookerly, Charter, "Noto Serif", Georgia, serif;
    line-height: 130%;
}

html, body, div, p, table, td, li {
    font-size: 1.1rem;
}

td, tr {
    padding: .3em 0 !important;
    margin: 0 !important;
}
.body {
    background-color: #f4ecd8;
    color: #5b4636;
}

h1, h2, h3, h4, h5, h6 {
    color: #5b4636;
    font-weight: bold;
}

.body a {
    color: #1d2a32;
}

pre, code, .pre {
    color: #ffffff;
    font-weight: 400;
    font-family: "Cascadia Mono", "Source Code Pro", Consolas, monotype;
}

}
