@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("telerama.fr") {

/* ==== 0- Telerama - Voir Notes (Sans Inscription) v.3 - SUPP PUB  ==== */

/* SUPP */
.overlay-grid--show-abtest ,
.abtest-toaster.abtest-toaster--active ,
.selection__container ,
.abtest-bandeau__container ,
.tv-grid--pub ,
.bouquet__container {
    display: none !important;
}

/* TOP HEADER */
.tv-grid--header_gridchoice {
    display: flex;
    justify-content: space-between;
    margin-top: -31px !important;
    padding: 0.5rem 0 0.5rem;
}
/* (new18) SHOW HIDDEN NOTES */
.tv-grid--channel {
    display: flex;
    height: 95px !important;
}
.tv-grid--title .hidden {
    display: block !important;
    float: left !important;
    margin-top: 2px !important;
    width: 7.4rem;
}
.tv-grid__lock-container {
    display: none !important;
}

/* ==== END  ==== */

}
