css:
@-moz-document domain("whatsapp.com")
{

    :root
    {
        --main: #345859;
        --accent: #4b7f7d;
        --mainO: #3458598f;
        --accentO: #4b7f7d8f;
        --accent2: #589593;
        --icons: #c4cad3;
        --text1: #c4cad3;
        --text2: #abb1b9;
        --text3: #e4eefd;
        --bg: #2E3440;
    }

      .dark
    {
        --icon: #c4cad3 !important;
        --panel-header-icon: #c4cad3 !important;
        --dropdown-background: #4b7f7d8f !important;
        --dropdown-background-hover: #3458598f !important;
        --outgoing-background: #4b7f7d !important;
        --outgoing-background-deeper: #345859 !important;
        --outgoing-background-highlight: #c4cad3 !important;
        --incoming-background: #2E3440 !important;
        --incoming-background-deeper: #2E3440 !important;
        --incoming-background-highlight: #c4cad3 !important;
        --primary-muted: grey !important;
        --primary: #c4cad3 !important;
        --primary-strong: #c4cad3 !important;
        --primary-title: #c4cad3 !important;
        --background-default: transparent !important;
        --background-default-active: #4b7f7d !important;
        --background-default-hover: #3458598f !important;
        --modal-backdrop: rgba(47, 47, 47, .44) !important;
        --button-primary: #c4cad3 !important;
        --button-primary-background: #4b7f7d !important;
        --button-primary-background-hover: #589593 !important;
        --button-round-background: #f00!important;
        --button-secondary: #c4cad3 !important;
        --button-secondary-border: #4b7f7d !important;
        --button-secondary-background: transparent;
        --button-secondary-hover: #589593 !important;
        --overlay: rgba(47, 47, 47, .44) !important;
        --panel-background: #3458598f !important;
        --panel-header-background: transparent !important;
        --typing: #abb1b9 !important;
        --teal: #e4eefd !important;
        --message-primary: #c4cad3 !important;
        --panel-primary: #3458598f !important;
        --rich-text-panel-background: #3458598f !important;
        --butterbar-connection-background: transparent !important;
        --startup-background: #131c21;
        --startup-icon: #6c7175;
        --status-background: #345859 !important;
        --status-background-hover: #242424;
        --status-primary: #c4cad3 !important;
        --status-secondary: #abb1b9 !important;
        --active-tab-marker: #4b7f7d !important;
        --panel-input-background: #4b7f7d8f !important;
    }


    /*intro*/
    ._2zkCi
    {
        padding-top: 28px;
        padding-bottom: 28px;
        cursor: default;
        background-color: var(--mainO) !important;
    }

    /*header-right*/
    .dark ._1-qgF
    {
        background-color: var(--mainO) !important;
        backdrop-filter: blur(4px) !important;
        -webkit-backdrop-filter: blur(4px) !important;
        border: 0px !important;
        border-radius: 0 0 50px 50px !important;
    }

    ._1-qgF
    {
        position: fixed;
        box-sizing: border-box;
        display: flex;
        align-items: center;
        width: 100%;
        height: 59px;
    }

    /*header-left*/
    ._1R3Un
    {
        background-color: transparent !important;
        top: 0px !important;
        border: 0px !important;
    }


    /*chats*/
    ._3U29Q,
    .F-0gY
    {
        background-color: transparent !important;
    }

    ._3QfZd,
    ._3-dtC,
    ._1Flk2,
    ._2DPZK,
    ._1pQBW
    {
        background-color: transparent !important;
    }

    ._3QfZd:after
    {
        background: url("/*[[https:null]]*/") no-repeat center center fixed !important;
        background-color: transparent !important;
    }

    .dark
    {
        background: url("/*[[https:null]]*/") no-repeat center center fixed !important;
        --app-background: transparent;
    }

    /*chats*/
    /*drawer profile*/
    ._2heqZ
    {
        background-color: transparent !important;
    }

    .OMoBQ
    {
        background-color: var(--mainO) !important;
        backdrop-filter: blur(8px) !important;
        -webkit-backdrop-filter: blur(8px) !important;
    }

    ._36Jt6
    {
        background-color: transparent !important;
    }

    ._3ZEdX
    {
        background-color: transparent !important;
    }



    /*search bg*/
    div.SgIJV
    {
        background-color: transparent !important;
    }

    .dark ._3LX7r
    {
        width: 260px !important;
        border: 0px !important;
        left: 50px !important;
    }

    /*search bg 1*/
    .RPX_m
    {
        background-color: var(--accentO) !important;
        border-radius: 18px;
    }

    ._3LX7r._1brJQ
    {
        background-color: transparent !important;
        box-shadow: 0 2px 3px transparent;
    }

    .dark ._3LX7r._1brJQ
    {
        border-bottom: 0px !important;
    }

    .-lcoh
    {
        background-color: transparent !important;
    }

    /*convos bg */
    ._1Flk2._2DPZK._1pQBW
    {
        background-color: var(--mainO) !important;
        backdrop-filter: blur(8px) !important;
        -webkit-backdrop-filter: blur(8px) !important;
    }

    /*convos bg transparent */
    ._1C2Q3.F-0gY
    {
        background-color: transparent !important;
    }

    ._2Z4DV
    {
        background-color: transparent !important;
    }

    .JnmQF._3QmOg
    {
        background-color: transparent !important;
    }

    /* input bg */
    .vR1LG._3wXwX
    {
        background-color: var(--mainO) !important;
    }

    ._2A8P4
    {
        background-color: var(--accentO) !important;
        border: 0px solid var(--bg) !important;
    }
    .vR1LG
    {
        background-color: transparent!important;
        border-radius: 30px 30px 0px 0px !important;
    }
    ._3uxr9 {
        background-color: rgba(224, 1, 1, 0)!important;
}


    /* dropdowns */
    ._1qAEq
    {
        border-radius: 30px 30px 30px 30px!important;
        backdrop-filter: blur(4px) !important;
        -webkit-backdrop-filter: blur(4px) !important;
    }





    /*messages*/
._1bUzr {
        display: none !important;
    }

    .message-out ._1bUzr
    {
        display: none !important;
    }

    .message-in,
    ._1-U5A,
    ._3XpKm
    {
        border-radius: 10px !important;
    }

    /*dropdown hover bg*/
    ._3UHfW.H774S
    {
        border-radius: 10px !important;
    }







    /*date*/
    ._2kR4B
    {
        position: relative !important;
        text-align: center;
        background-color: VAR(--accentO) !important;
        border-radius: 10px !important;
        backdrop-filter: blur(4px) !important;
        -webkit-backdrop-filter: blur(4px) !important;
    }

    ._24wtQ
    {
        flex: none;
        font-size: 14.2px;
        line-height: 19px;
        color: var(--message-primary);
        bottom: -50px;
    }

    /*chat bg image*/
    .IeYBo
    {
        display: none !important;
        background-color: transparent !important;
        color: transparent !important;
    }

    /*chat bg */
    ._3AUV4
    {
        background: url("/*[[https:null]]*/") no-repeat center center fixed !important;
        background-size: cover !important;
        background-color: var(--bg) !important;
    }

    /*mute modal */
    ._3NCh_
    {
        padding: 22px 24px 20px;
        background-color: var(--mainO) !important;
        border-radius: 30px !important;
    }

    /*emoji-picker*/
    ._12FEz
    {
        border-radius: 30px 30px 0px 0px !important;
        width: 94% !important;
        margin-left: 20px !important;
    }
}
