css:
@-moz-document regexp("^(.+?)\\.salesforce\\.com\\/\\_ui\\/common\\/apex\\/debug\\/ApexCSIPage$") {
.CodeMirror-code {
    background-color: #1b1e2b;
    font-family: 'Source Code Pro', 'Hack', 'Monospace', 'Consolas', 'Menlo-Regular', 'Segoe UI', monospace;
}

.cm-s-default span.cm-keyword {
    color: #5f0;
}
.cm-def{
    color: #d87d1f;
}

.cm-s-default pre > span.cm-property {
    color: #a866c1;
    font-style: italic;
}

.CodeMirror-code pre {
    color: #ff8e00;
}

.cm-s-default span.cm-string {
    color: #dfc141;
}

.cm-s-default span.cm-variable {
    color: #a6e22e;
}

.cm-s-default span.cm-def {
    color: #37ffb5;
}

.cm-s-default span.cm-variable-2 {
    color: #59ff3f;
}

.DevConsole-gutter-breakpoint {
    width: 8px;
    color: #37ffb5;
    cursor: pointer;
}

.cm-s-default span.cm-tag {
    color: #ff770f;
}

.cm-s-default span.cm-attribute {
    color: #a6e22e;
}

.cm-s-default span.cm-comment {
    color: #75715e;
}

.cm-s-default span.cm-qualifier {
    color: #a6e22e;
}

.cm-s-default span.cm-operator {
    color: #fff;
}

.cm-s-default span.cm-number {
    color: #49ff00;
}

.cm-s-default span.cm-atom {
    color: #00ffb7;
}

.cm-s-default span.cm-string-2 {
    color: #f92672;
}


div.CodeMirror span.CodeMirror-matchingbracket {
    color: #91ff00;
    border-bottom: 1px dotted #848484;
    font-weight: bold;
}

.CodeMirror-matchingtag {
    background-color: #383830;
}

.cm-tag.CodeMirror-matchingtag {
    border-bottom: 1px dotted #fff;
}

.cm-matchhighlight,
.CodeMirror-selected,
.CodeMirror-focused .CodeMirror-selected {
    background-color: #4a4a4a;
}

.CodeMirror-scroll {
    background-color: #1b1e2b;
}

.CodeMirror div.CodeMirror-cursor {
    border-left: 1px groove white;
}

.CodeMirror-gutters {
    background-color: #1b1e2b;
    border-right: 1px solid #666;
}

.CodeMirror-linenumber {
    color: white;
}

.VARIABLE_DECLARATION {
    color: #A866C1;
}

.VARIABLE_REFERENCE {
    color: #3dcab4;
}

.USER_DEFINED:hover {
    color: #fffb0b;
}

.cm-s-default span.cm-builtin {
    color: #a6e22e;
}


}
