css:
@-moz-document domain("ticktick.com") {
    
    [class*=-dark-theme] .g-center, [class*=-dark-theme] .g-full-right, [class*=-dark-theme] .g-right {
    background-color: #2D2E39;
}

    [class*=-dark-theme] .td-body, [class*=-dark-theme] .td-footer, [class*=-dark-theme] .td-header {
    background-color: #272931;
}
    
    [class*=-dark-theme] .container-fluid, [class*=-dark-theme] .g-left {
    background-color: #272931;
}
    
    .night-dark-theme .column-list-view .column .column-main, .night-dark-theme .column-list-view .column.new-column {
    background-color: #272931;
}
    
    [class*=-dark-theme] .dropdown-menu, [class*=-dark-theme] .dropdown-menu-menu-submenu {
    background-color: #272931;
}
    
    .lists .active>a .l-title {
    font-weight: normal;
}
    
    .active .i-active-theme, .option-item-selected .i-active-theme, [class*=-dark-theme] .dropdown-menu-menu-submenu .dropdown-menu-menu-item-selected>a, [class*=-dark-theme] .dropdown-menu-menu-submenu .dropdown-menu-menu-item-selected>a .text-primary, [class*=-dark-theme] .dropdown-menu-menu-submenu .dropdown-menu-menu-item-selected>a .text-secondary, [class*=-dark-theme] .dropdown-menu-menu-submenu .dropdown-menu-menu-item-selected>a strong, [class*=-dark-theme] .dropdown-menu-menu-submenu li.active>a, [class*=-dark-theme] .dropdown-menu-menu-submenu li.active>a .text-primary, [class*=-dark-theme] .dropdown-menu-menu-submenu li.active>a .text-secondary, [class*=-dark-theme] .dropdown-menu-menu-submenu li.active>a strong, [class*=-dark-theme] .dropdown-menu .dropdown-menu-menu-item-selected>a, [class*=-dark-theme] .dropdown-menu .dropdown-menu-menu-item-selected>a .text-primary, [class*=-dark-theme] .dropdown-menu .dropdown-menu-menu-item-selected>a .text-secondary, [class*=-dark-theme] .dropdown-menu .dropdown-menu-menu-item-selected>a strong, [class*=-dark-theme] .dropdown-menu li.active>a, [class*=-dark-theme] .dropdown-menu li.active>a .text-primary, [class*=-dark-theme] .dropdown-menu li.active>a .text-secondary, [class*=-dark-theme] .dropdown-menu li.active>a strong {
    color: #0CABC3;
    fill: #0CABC3;
}
    
    .night-dark-theme .lists .f-header>a svg, .night-dark-theme .lists .fake-li>a svg, .night-dark-theme .lists .l-new>a svg, .night-dark-theme .lists div.p-sort-help>a svg, .night-dark-theme .lists li>a svg {
    	fill: #0CABC3; 
}
    
    [class*=-dark-theme] .lists li.active>a svg {
    	fill: #0CABC3; 
}
    
    [class*=-dark-theme] .text-info, [class*=-dark-theme] .text-info-title {
    color: #0CABC3;
                                            font-size: 125%;
                                            font-weight: 400;
}
    
    [class*=-dark-theme] svg {
    fill: #76899b;
}
    .i-o-36, .i-o-24 {
     opacity: 1; 
}
    
    [class*=-dark-theme] .i-4 {
     fill: #76899b;
     color: #76899b;
}
    
    [class*=-dark-theme] .left-menu-t .icon-left-menu {
    fill: #76899b;
}
    
    [class*=-dark-theme] .g-left .tool-bar .t-notify .i-1, [class*=-dark-theme] .g-left .tool-bar .t-search .i-1 {
    opacity: 1;
}
    
    [class*=-dark-theme] .g-left .lists .active>a:hover, [class*=-dark-theme] .g-left .lists .active>a:not(.l-tab) {
    background-color: #394a61;
}
    
    .night-dark-theme .real-color-theme {
    color: #5abfe6;
}
    
    [class*=-dark-theme] .column-list-view .column .task .l-task-bg {
    background-color: #394a61;
}
    
     [class*=-dark-theme] .text-def {
    color: #ffffff;
                                                      font-size: 13px;
}
    
    [class*=-dark-theme] .text-primary {
    font-size: 22px;
} 
    
    .rc-menu-item-selected .text-primary {
    color: #5abfe6 !important;
    }
    
    .background-theme {
    background-color: #5abfe6;
}
    
    .tl-quick .task-input {
    color: hsla(0,0%,100%,.54);
}
    
    .sub-task .title.editor-with-link [contenteditable], .sub-task .title>.input-placeholder [contenteditable] {
    color: hsla(0,0%,100%,.54);
}
    
    .cm-s-markdown-default pre.CodeMirror-line {
    color: hsla(0,0%,100%,.54);
}
    
    .task .desc-content {
    color: hsla(0,0%,100%,.54);
    font-size: 12px !important;
}
    
    [class*=-dark-theme] .select-menu .select-title, p {
    color: #ffffff;
}
}
