css:
@-moz-document domain("googlefeud.com") {
button {
    cursor: pointer;
    display: inline-block;
    line-height: 22pt;
    margin-right: 15px;
    margin-bottom: 15px;
    font-weight: bold;
    font-size: 10pt;
    text-transform: capitalize;
    border-radius: 2px;
    -moz-border-radius: 2px;
    -webkit-border-radius: 2px;
    border: 1px solid #dbdbdb;
    background-color: #f5f5f5;
    width: 124px;
    height: 30px;
}
.oval {
    height: 36px;
    width: 60px;
    border: none;
    border-radius: 30px / 18px;
    background-color: #f5f5f5;
    text-align: center;
    margin: auto;
    line-height: 35px;
    font-weight: bold;
    color: black;
    font-size: 1.3em;
}
#queryspan, #queryspan_touch, #rebox, #table1, #table2 {
    text-transform: lowercase;
    background-color: #fff;
}
#email, #querydiv {
    border: thin solid #CCCCCC;
    padding: 0.3rem 8rem 0.4rem 1rem;
    overflow: hidden;
    border-radius: 0rem;
    position: relative;
}
.search {
    width: 87px;
    height: 36px;
    background: url(https://web.archive.org/web/20160702064213im_/http://www.googlefeud.com/search.gif.pagespeed.ce.siNnj6hUm7.gif) 0 0;
    margin-top: -13px;
    right: -1rem;
    float: left;
    cursor: pointer;
    fill: #ffffff00;
}
#wrapper {
    width: 800px;
    margin: 0 auto;
    margin-right: auto;
    text-align: center;
    padding: 0;
}
tbody {
    display: table-row-group;
    vertical-align: middle;
    border-color: inherit;
}
table {
    display: table;
    border-collapse: separate;
    box-sizing: border-box;
    text-indent: initial;
    border-spacing: 2px;
    border-color: grey;
}
h3 {
    font-size: 2rem;
    border: .1rem #d0d2d3 solid;
    padding: 0.7rem 2rem;
    border-radius: 0rem;
    margin-bottom: 2rem;
    font-weight: 100;
    -webkit-touch-callout: none;
    -webkit-user-select: none;
    -khtml-user-select: none;
    -moz-user-select: none;
    -ms-user-select: none;
    user-select: none;
}
}
