/* ==UserStyle==
@name           QZone-TwitterStyle
@namespace      github.com/IcedOtaku/qzone-stylus
@version        0.0.1
@description    Brower your QQ zone like twitter. Github https://github.com/IcedOtaku/qzone-stylus.
@author         IcedOtaku
==/UserStyle== */

@-moz-document url-prefix("https://user.qzone.qq.com") {
	/* 
	 * relayout
	 * change ui components size
	 * remove unwanted conponents
	 * adjust layout
	 */
	.background-container {
		background-image: none;
		background-color: #000;
	}
	.layout-body {
		padding: 0px;
	}
	.col-main-feed {
		padding: 0px;
	}
	.layout-page .col-main .col-main-feed {
		padding: 0px;
	}
	.head-detail {
		visibility: hidden;
	}
	.weather-module {
		visibility: hidden;
	}
	.col-menu {
		visibility: hidden;
	}
	.col-main-sidebar {
		visibility: hidden;
	}
	.layout-page .col-main .col-main-sidebar {
		width: 0px;
	}
	.fn-feed-control-v2 {
		visibility: hidden;
	}
	.qz-poster-editor-cont {
		margin-top: 20px;
	}
	.layout-head {
		height: 0px;
		visibility: hidden;
	}
	.top-fix-bar {
		visibility: hidden;
		height: 0px;
	}
	.layout-nav {
		height: 0px;
	}
	.layout-nav-inner {
		height: 0px;
	}
	.layout-nav .head-avatar {
		bottom: 0px;
		top: 20px;
		left: -20px;
		border: none;
	}
	.layout-nav .head-avatar .user-avatar {
		width: 160px;
		height: 160px;
		border-radius: 80px;
		border: none;
	}

	/* 
	 * rerender
	 * font colors change
	 */
	.feed-v9 .f-single .fui-txtimg-bg {
		background-color: #000;
	}
	.feed-v9 .f-single .fui-txtimg-bg .txt-box {
		color: #fff;
	}
	.feed-v9 a {
		color: #00acee;
	}
	.feed-v9 .f-single {
		background-color: #000;
	}
	.feed-v9 .f-single .f-single-head .user-info .f-name {
		font-weight: 900;
	}
	.feed-v9 .f-single .f-single-head .user-info .info-detail .state {
		color: #fff;
	}
	.feed-v9 .f-single .f-info,
	.feed-v9 .f-single .f-info .state,
	.feed-v9 .f-single .f-single-head .user-info .f-name {
		color: #fff;
	}
	.feed-v9 .f-single .f-single-foot .f-op-detail:after {
		content: none;
	}
	.qz-poster .qz-inputer {
		border-color: #fff;
		background-color: #000;
	}
	.qz-poster .qz-poster-attach-side .item {
		background-color: #fff;
	}
}
