css:
@-moz-document domain(www.youtube.com) {

:root{
    --buttonFace:#E6E4E3;
    --buttonFaceShadow:#D6D4D3;
    --buttonSkeuBorder:#F8F6F4;
}
*{
    font-family         : "MS PGothic"!important;
    shape-rendering     : geometricPrecision !important;
    transition-duration : 0s !important;
    transition-timing-function:step-end!important;
    border-radius       : 0px!important;
    image-rendering     : auto !important
}
ytd-guide-collapsible-section-entry-renderer, #guide-wrapper, ytd-guide-section-renderer{
    color:#000000!important;
    background-color:var( --buttonFace )!important;
}
#primary-links, #secondary-links{
    background-color:#1256B2!important;
    border:outset 2px #2292DA!important;
}
ytd-player{
    box-sizing:border-box!important;
    border:solid 1px #AAAAAA;
}
yt-live-chat-header-renderer{
    background-color:#00000000!important;
    margin:-9px 0px -8px 0px!important;
}
#sponsor-button, #voice-search-button, yt-formatted-string#subscriber-count, ytd-sentiment-bar-renderer, yt-live-chat-viewer-engagement-message-renderer,iron-pages yt-img-shadow, #owner-sub-count, #input-panel,#guide-inner-content #footer, ytd-playlist-panel-renderer #menu.style-scope.ytd-playlist-panel-video-renderer{
    display:none!important;
}
#tabsContainer, yt-icon-button .style-scope .ytd-masthead, #notification-preference-button, #endpoint, yt-dropdown-menu, #left, #right, #play-all,ytd-topbar-menu-button-renderer, ytd-notification-topbar-button-renderer, #search-icon-legacy,yt-icon-button#button.style-scope.ytd-button-renderer.style-default.size-default, #button.style-scope.yt-icon-button>yt-icon.style-scope.ytd-menu-renderer,ytd-toggle-button-renderer, ytd-toggle-theme-compact-link-renderer, #send-button, yt-live-chat-icon-toggle-button-renderer, tp-yt-paper-dialog button{
    background-color:var( --buttonFace )!important;
    border:outset 2px var( --buttonSkeuBorder )!important;
    color:#000000!important;
    font-weight:bold!important;
    text-decoration:underline!important;
}
yt-icon-button .style-scope .ytd-masthead:hover, #endpoint:hover, #notification-preference-button:hover, #endpoint:hover, yt-dropdown-menu:hover, #left:hover, #right:hover, #play-all:hover, ytd-topbar-menu-button-renderer:hover, ytd-notification-topbar-button-renderer:hover, #search-icon-legacy:hover,#button.style-scope.yt-icon-button>yt-icon.style-scope.ytd-menu-renderer:hover, ytd-toggle-theme-compact-link-renderer:hover,#info-contents ytd-toggle-button-renderer:hover, yt-icon-button#button.style-scope.ytd-button-renderer.style-default.size-default:hover, #send-button:hover, yt-live-chat-icon-toggle-button-renderer:hover, tp-yt-paper-dialog button:hover{
    background-color:var( --buttonFaceShadow )!important;
    border:inset 2px var( --buttonSkeuBorder )!important;
}
#search-input{
    background-color:#FFFFFF!important;
    border:inset 2px #AAAAAA!important;
}
ytd-comment-thread-renderer ytd-toggle-button-renderer{
    border:0px!important;
    background-color:#00000000!important;
}
ytd-comment-thread-renderer .toolbar a{
    font-weight:bold!important;
    text-decoration:underline!important;
    color:#0000FF!important;
}
ytd-comment-thread-renderer #body{
    margin:0px 0px -6px 0px!important;
    padding-top:3px!important;
    border-top:solid 1px #CCCCCC!important;
}
#author-name, ytd-comment-thread-renderer #author-text{
    color:#008800!important;
    font-weight:bold!important;
}
ytd-comment-replies-renderer{
    padding:0px!important;
    margin-top:-6px!important;
    margin-bottom:-8px!important;
}
ytd-mini-guide-renderer, #container.style-scope.ytd-masthead, #container.style-scope.ytd-playlist-panel-renderer, ytd-watch-next-secondary-results-renderer{
    background-color:var( --buttonFace )!important;
    border:solid 1px #BBBBBB!important;
}
yt-live-chat-text-message-renderer{
    box-sizing:border-box!important;
    background-color:#FFFFFF!important;
    margin:1px!important;
    padding:0px!important;
    border:solid #CCCCCC!important;
    border-width:0px 1px 1px 0px!important;
}
yt-live-chat-paid-message-renderer #author-name{
    color:#FFFFFF!important;
    text-shadow:-1px  0px 0px #000000,
                 1px  0px 0px #000000,
                 0px -1px 0px #000000,
                 0px  1px 0px #000000;
}
#selectionBar{
    transition-duration : 0.000001s !important;
    border-bottom:ridge 8px #44B2FF!important;
}
tp-yt-paper-button.style-scope.ytd-subscribe-button-renderer{
    background-color:#FD4422!important;
    border:outset 3px #FFA873!important;
}
tp-yt-paper-button.style-scope.ytd-subscribe-button-renderer:hover{
    background-color:#FF7422!important;
    border:inset 3px #FFA873!important;
}
tp-yt-paper-button.style-scope.ytd-subscribe-button-renderer[subscribed]{
    color:#FFFFFF!important;
    background-color:#DD3420!important;
    border:inset 3px #E29263!important;
    opacity:0.5!important;
}
ytd-thumbnail{
    border:solid 1px #0000FF!important;
    margin-left:-1px!important;
    height:100px!important;
    max-width:200px!important;
    box-sizing:border-box!important;
}
ytd-video-renderer{
    background-color:#F0F2FF!important;
    margin:2px 0px 2px 2px!important;
    border-bottom:solid 1px #CCCCCC!important;
}
ytd-video-renderer #video-title, ytd-radio-renderer #video-title{
    color:#0000FF!important;
    font-weight:bold!important;
    text-decoration:underline!important;
}
ytd-grid-video-renderer, ytd-radio-renderer, ytd-compact-video-renderer, ytd-playlist-panel-video-renderer{
    background-color:#F0F2FF!important;
    border:outset 1px #F8F9FF!important;
    margin:0px!important;
}
ytd-grid-video-renderer{
    width:208px!important;
    height:150px!important;
    margin:0px 2px 8px 0px!important;
    padding-bottom:24px!important;
}
ytd-rich-item-renderer{
    width:240px!important;
    height:162px!important;
    margin:0px 2px 40px 0px!important;
}
#details.style-scope.ytd-rich-grid-media, ytd-playlist-video-renderer #content{
    padding-bottom:2px!important;
    background-color:#F0F2FF!important;
    border:outset 1px #F8F9FF!important;
}
ytd-grid-video-renderer:hover{
    background-color:#E0FFFF!important;
    border:inset 1px #F8F9FF!important;
}
#meta .style-scope.ytd-grid-video-renderer{
    margin:0px!important;
    padding:0px!important;
    line-height:115%!important;
}
ytd-grid-video-renderer #items{
    background-color:#00000000!important;
    border:0px!important;
}
ytd-grid-playlist-renderer, ytd-compact-radio-renderer, ytd-compact-playlist-renderer{
    background-color:#FFFAF0!important;
    border:outset 1px #FFFFF5!important;
    margin:0px!important;
    overflow  : hidden!important;
}
ytd-grid-playlist-renderer{
    width:209px!important;
    height:165px!important;
    margin:0px 2px 8px 0px!important;
    padding-bottom:24px!important;
}
ytd-two-column-browse-results-renderer, #items{
    margin:0px!important;
    padding-top:0px!important;
    padding-bottom:0px!important;
    padding-right:0px!important;
}
#video-title.yt-simple-endpoint.style-scope.ytd-grid-video-renderer{
    color:#0000FF!important;
    font-weight:bold!important;
    
}
ytd-item-section-renderer{
    background-color:#FCFCFC!important;
    border:solid 1px #AAAAAA!important;
    padding:2px!important;
    margin-bottom:4px!important;
}
ytd-playlist-video-list-renderer #sort-filter-menu{
    padding:0px!important;
}
ytd-playlist-video-renderer #content{
    padding:2px 0px 2px 0px!important;
}
ytd-playlist-video-renderer #container, ytd-playlist-video-renderer ytd-thumbnail{
    height:70px!important;
}
.style-scope.ytd-shelf-renderer{
    margin-top:2px!important;
    margin-bottom:2px!important;
}
#channel-header-container, ytd-video-secondary-info-renderer{
    box-sizing:border-box!important;
    background-color:#FDFDE3!important;
    padding:4px!important;
    border:ridge 1px #EAAF66!important;
}
ytd-expander a{
    font-weight:bold!important;
    text-decoration:underline!important;
    color:#0000FF!important;
}
ytd-video-primary-info-renderer{
    box-sizing:border-box!important;
    padding:4px!important;
    background-color:#DFFFD5!important;
    border:solid 1px #8ACF46!important;
}
.ytp-gradient-bottom
{
    height:10px!important;
    opacity:0.8!important;
}
#top-row{
    margin:0px!important;
    padding:0px!important;
}
#endpoint-link{
    max-height:70px!important;
    overflow:hidden!important;
    background-color:#FFFFFF!important;
    border:solid 1px #A1CFFF!important;
}
ytd-rich-metadata-row-renderer{
    margin-top:-6px!important;
    margin-bottom:-12px!important;
}
ytd-comment-thread-renderer{
    margin:0px!important;
    padding-bottom:2px!important;
}
#columns #primary{
    padding:8px 16px 0px 0px!important;
    margin:0px!important;
    margin-left:8px!important;
}
#columns #secondary{
    padding-top:8px!important;
    padding-right:8px!important;
    margin:0px!important;
    margin-right:0px!important;
    margin-left:-16px!important;
}
#columns{
    padding:0px!important;
    margin:0px!important;
    width:100%!important;
}
ytd-comments{
    margin-top:0px!important;
    margin-right:12px!important;
}
ytd-searchbox #container{
    background-color:#00000000!important;
    border:0px!important;
}
ytd-video-primary-info-renderer h1.title.style-scope.ytd-video-primary-info-renderer{ margin-bottom : -4px !important; }
ytd-video-primary-info-renderer #info           { margin-bottom : 0px !important;  }
ytd-video-primary-info-renderer #menu-container { margin-top    : -2px !important; }
ytd-video-secondary-info-renderer #content      { margin-top    : -8px !important; }
ytd-watch-next-secondary-results-renderer, ytd-playlist-panel-renderer{ margin-left : -1px !important; }
.grid-subheader.style-scope.ytd-shelf-renderer  { margin        : 0px !important;  }
ytd-rich-grid-media ytd-thumbnail               { max-width     : 240px !important;}
ytd-playlist-panel-renderer .header.style-scope.ytd-playlist-panel-renderer{ padding : 4px 0px 0px 4px !important; }
ytd-playlist-panel-renderer #publisher-container{ margin        : 0px !important;  }
ytd-playlist-panel-renderer tp-yt-paper-button  { padding       : 0px !important;  }
yt-live-chat-app #message button.style-scope.yt-icon-button.fyc_button{opacity:0!important;}
yt-live-chat-app #message button.style-scope.yt-icon-button.fyc_button:hover{opacity:1!important;}
ytd-active-account-header-renderer, yt-multi-page-menu-section-renderer{ background-color : #F5F7F9 !important; }
ytd-comment-thread-renderer #toolbar            { margin        : -14px 0px -8px 0px !important; }
ytd-comment-thread-renderer #content            { margin-top    : -4px !important; }
ytd-watch-flexy                                 { background-color:#EFEFEF !important; }
yt-live-chat-author-chip                        { min-width     : 100px !important;}
ytd-live-chat-frame                             { height        : calc( 100vh - 70px ) !important; }
ytd-comment-replies-renderer ytd-button-renderer{ color         : #0000FF !important; }
iron-pages, yt-live-chat-message-input-renderer, yt-live-chat-ticker-renderer{ background-color : #FAF3F3 !important; }
iron-pages#content-pages                        { border        : solid 0px #F1D5DA !important; }
#search                                         { font-family   : "MS Gothic" !important; }
#container.style-scope.ytd-playlist-panel-renderer{ border      : solid 1px #BBBBBB !important; }
.header.style-scope.ytd-playlist-panel-renderer { background-color: #F8F8F8 !important; }
ytd-mini-guide-renderer                         { border-width  : 0px 2px 0px 2px !important; }
#container.style-scope.ytd-masthead             { border-width  : 2px 0px 2px 0px !important; }
#items                                          { border-width  : 2px  !important;  }
.tab-content:hover,ytd-comment-thread-renderer ytd-toggle-button-renderer:hover{ background-color : #87DFFF88 !important; }
#tabsContainer                                  { box-sizing    : border-box !important; }
ytd-video-renderer #channel-info                { padding       : 0px  !important;  }
ytd-grid-playlist-renderer:hover                { border        : inset 1px #FFFFF5 !important; }
#channel-header                                 { border-top    : dashed 1px #444466 !important; }
ytd-two-column-browse-results-renderer, ytd-section-list-renderer, ytd-item-section-renderer{ width : 100% !important; }
ytd-video-secondary-info-renderer               { margin-bottom : 0px  !important;  }
ytd-expander                                    { line-height   : 115% !important;  }
#player-theater-container                       { background-color: #00000000 !important; }
ytd-item-section-renderer                       { padding-left  : 8px  !important;  }
video.video-stream.html5-main-video, .html5-video-container{margin:auto !important; }
tp-yt-iron-overlay-backdrop                     { opacity       : 0.2  !important;  }
ytd-masthead>#container                         { height        : 46px !important;  }
tp-yt-paper-dialog                              { box-shadow    : 10px 10px 4px #00003344 !important;     }
ytd-page-manager                                { margin-top    : 48px !important;  }
ytd-comments-header-renderer                    { margin        : 2px 0px 2px 0px !important;   }
ytd-comments-header-renderer #title             { margin        : 0px  !important;  }
ytd-live-chat-frame, ytd-playlist-panel-renderer{ margin-bottom : 0px  !important;  }
ytd-search ytd-shelf-renderer, ytd-horizontal-card-list-renderer{
    display:none!important;
}
ytd-search ytd-item-section-renderer, ytd-video-renderer{
    line-height:80%!important;
}
ytd-search ytd-video-renderer .metadata-snippet-container.style-scope.ytd-video-renderer
{
    margin:0px!important;
}
ytd-search ytd-section-list-renderer #header-container #label{
    padding:4px!important;
}
ytd-search ytd-search-filter-renderer{
    padding:0px 0px 4px 0px!important;
}

}
