css:
@-moz-document domain("studip.uni-hannover.de") {
#flowcastsplayer > .flowcastsplayer > div,
#flowcastsplayer #main-container, #flowcastsplayer #player_with_controls {
    min-width: 592px !important;
    width: auto !important;
    max-width: 100% !important;
}

#flowcastsplayer #player-panel {
    min-width: 592px !important;
    height: auto !important;
    max-width: 100% !important;
}

#flowcastsplayer #player-panel video {
    display: block !important; /* Removes annoying whitespace below the player after unsetting height of #player-panel */
    margin: 0px auto !important;
    min-width: 592px !important;
    width: auto !important;
    max-width: 100% !important;
    min-height: 333px !important;
    max-height: calc(100vh - 100px) !important;
}

.is-fullscreen #flowcastsplayer #player-panel video {
    max-height: calc(100vh - 55px) !important;
}

#flowcastsplayer > #player-panel video.detached {
    min-width: unset !important;
    max-width: 40% !important;
    min-height: 250px !important;
    max-height: calc(100vh - 74px) !important;
}

.is-fullscreen #flowcastsplayer #player-panel video.detached {
    top: 0 !important;
    max-width: 50% !important;
    max-height: calc(100vh - 100px) !important;
}

#flowcastsplayer #toolbar-panel {
    height: 54px !important;
    padding-left: 10px !important;
    padding-right: 15px !important;
    padding-top: 1px !important;
    width: auto !important;
}

#flowcastsplayer #toolbar-panel #toolbar-play {
    display: block !important;
    margin-right: 0px !important;
    margin-left: 0px !important;
    width: 36px !important;
}

#flowcastsplayer #toolbar-panel a > img {
    display: block !important;
    padding: 3px !important;
}

#flowcastsplayer #toolbar-panel #toolbar-time {
    margin-right: 10px !important;
    margin-left: 10px !important;
}

#flowcastsplayer #toolbar-panel #toolbar-options {
    min-width: unset !important;
    justify-content: end !important;
    height: 100% !important;
}

#flowcastsplayer #toolbar-panel #toolbar-options .source_selector {
    margin-bottom: calc(100% - 13px) !important;
}

#flowcastsplayer #toolbar-panel #toolbar-options .source_selector_area {
    display: block !important;
    box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.6) !important;
    margin-left: -160px !important;
    border: 0px none !important;
    position: relative !important;
    border-radius: 10px !important;
    padding-top: 8px !important;
    padding-bottom: 5px !important;
}

#flowcastsplayer #toolbar-panel #toolbar-options .source_selector_area:after {
    display: block !important;
    content: "" !important;
    color: white !important;
    background: black no-repeat url("https://studip.uni-hannover.de/assets/images/icons/blue/admin.svg") !important;
    background-size: 30px !important;
    background-position: center 14px !important;
    position: absolute !important;
    right: 42px !important;
    padding: 15px 8px 8px 8px !important;
    width: 30px !important;
    height: 30px !important;
    box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.6) !important;
    border: 0px none !important;
    border-radius: 0 0 10px 10px !important;
    margin-top: -6px !important;
}

#flowcastsplayer #toolbar-panel #toolbar-options .source_selector_area label::before {
    content: "Lautst√§rke: " !important;
    font-size: 14px !important;
}

#flowcastsplayer #toolbar-panel #toolbar-options .source_selector_area label {
    font-size: 0px !important;
}

#flowcastsplayer #toolbar-panel #toolbar-options .source_selector_area label span {
    font-size: 14px !important;
}

#flowcastsplayer #toolbar-panel #toolbar-options .source_selector_area label input[type=range] {
    position: relative !important;
    top: -4px !important;
}

#flowcastsplayer #toolbar-panel #toolbar-options .source_selector_area ul li {
    display: inline-block !important;
    margin: 0 !important;
    margin-left: 10px !important;
}

#flowcastsplayer #toolbar-panel #toolbar-options .source_selector_area ul li a {
    display: inline-block !important;
    position: relative !important;
    top: -4px !important;
}

#flowcastsplayer #toolbar-panel #toolbar-options .source_selector_area ul li a > img {
    display: inline !important;
    position: relative !important;
    top: 4px !important;
}

#flowcastsplayer #toolbar-panel #toolbar-options .source_selector_area ul li:first-child {
    margin-left: 0 !important;
}

#flowcastsplayer #toolbar-panel #toolbar-options .source_selector_area .playbackRates {
    margin: 0px 3px 0px 2px !important;
}

#flowcastsplayer #toolbar-panel #toolbar-options > a {
    min-width: 36px !important;
    text-align: center !important;
    margin-left: 10px !important;
}

#flowcastsplayer #toolbar-panel #toolbar-options .duration {
    min-width: 130px !important;
}

#flowcastsplayer > .flowcastsplayer > div .blubber_panel {
    height: auto !important;
    min-height: 50px !important;
    max-height: 300px !important;
    max-width: 100% !important;
}

#flowcastsplayer > .flowcastsplayer > div .blubber_panel #blubber_stream_container {
    max-width: 100% !important;
}

#flowcastsplayer > .flowcastsplayer > div .blubber_panel .start_blubber {
    height: auto !important;
    min-height: 50px !important;
    border-color: #e7ebf1 !important;
    padding-top: 2px !important;
}

#flowcastsplayer > .flowcastsplayer > div .blubber_panel .blubber_thread {
    height: auto !important;
    min-height: 100px !important;
    border-color: #e7ebf1 !important;
    border-top-width: 0px !important;
}

#flowcastsplayer > .flowcastsplayer > div .blubber_panel .blubber_thread  .writer {
    border-color: #e7ebf1 !important;
}

}
