css:
@-moz-document domain("gamefaqs.gamespot.com") {

/*Removes the italicized topic names*/
.game_content .item .new {
	opacity: 0.8;
	font-style: normal !important;
}

/*Compacts the topic list*/
.oldschool.forum_topics .item .long_name {
	padding: 3px !important;
}

/*Makes signatures become closer to the post*/
.oldschool .item.converse .signature {
	
    margin-top: -20px !important;
    margin-right: 10px !important;
    margin-left: 10px !important;
}

/*Decreases the size of the bar at the bottom of each post with options*/
.oldschool .item.converse .block.w100 {
    margin-top: -11px !important;
    Height: 14px !important;
    font-size: 12px !important;  
    position:relative !important;                                          
}

}
