@keyframes rainbow {
    0% {background-color: #d9c59e;border-color: #d9c59e;}
    20% {background-color: #ba9f7b;border-color: #ba9f7b;}
    40% {background-color: #a38464;border-color: #a38464;}
    60% {background-color: #825e3e;border-color: #825e3e;}
    80% {background-color: #5e4025;border-color: #5e4025;}
    100% {background-color: #d9c59e;border-color: #d9c59e;}
}

.btn-common-play-game-lg {
    animation: rainbow 10s infinite;
}

@keyframes spin
{
  from
  {
    transform: rotate(0deg);
  }

  to
  {
    transform: rotate(360deg);
  }
}

