css:
@-moz-document domain(tokyotosho.info) {
	body {
    font-size:16px;
    position:relative;
    font-family: Arial, sans-serif;
  
}
#main h1:first-child {
    font-size:18px;
    text-align:left;
    float:left;
    margin-right:5px
}
h1 +.centertext {
    display:none;
}
h2 {
    display:none;
}
h3 {
    margin:0;
    text-align:left;
    padding-top:1px;
    float:left;
}
a {
   
    text-decoration:underline;
    line-height:1.65em;
    transition:.3s
}
a:hover {
    color:black!important;
    font-weight:bold;
}
#main {
    border:0;
    margin:0 auto;
    width:auto;
    padding:15px;

   max-width: 100%;
}
table.listing {
}
.news {
    position:absolute;
    bottom:0;
    display:none;
}
p.centertext > a {
    display:none
}
.listing td {
    padding:7px 10px;
}
.listing tr {
    background-color:rgba(180,180,180,.1);transition:.3s;
}
.listing td a {
    font-size:16px;
    
}
.desc-top {
    font-size:13pt;
    font-weight:700;
    padding-bottom:0!important
}
.listing tr:hover , .listing tr.shade:hover  { background-color:rgba(160,160,160,.2)!important}

form table td {
    text-align:left
}
table td {
    vertical-align:middle !important
}
form input, form select {
    font-family:'Jost';
    font-size:12pt!important;
    padding:8px;
    margin:8px;
    line-height:18px;
    max-width:220px
}
form br {
    display:none
}
form select {
    padding:7px;
    margin:8px 8px 8px -14px;
    line-height:16px;
}
form {
    text-align:left;
}
input#searchName, input#searchComment {
    height:20px;
    width:20px;
    margin:0 8px 0 8px;
    vertical-align:middle;
}
input[value="Search"] + br {
    display:block
}
ul.menuwrapper + * {
    display:block;clear:both;padding-top:30px;
}
.desc-bot, .stats {
    font-size:10pt;
    padding-top:0!important
}
.listing tr.shade {
    background-color: transparent !important
}
p.footer {
    display:none;
}
ul.menuwrapper {

    padding:0;
    margin:0 auto;
    width:auto;
    text-align:left;
    display:flex;                                  
	flex-wrap:wrap;
    float:right;
}

ul.menuwrapper:after {display:block;
clear:both;content:'';}
ul.menuwrapper li.menu {
    display:inline-block;
    text-indent:-99999px;
    line-height:0;
    margin:0 2px;
    color:transparent;
}
ul.menuwrapper li.menu:after {
    content:'_';
    text-indent:-999px;
    color:transparent;
    visibility:hidden;

   
                                            
}
ul.menuwrapper li.menu a {
    float:left;
    display:block;
    padding:6px 12px;
    font-size:13px;
    line-height: 30px;
    text-decoration:none;
    background-color:#2f2f77;
    margin:0;
    text-indent:0;
    font-weight:500;
    top:0;
    color:white!important
}
ul.menuwrapper li.menu a:hover {
    color:black!important;
    transition:.3s;
    box-shadow: 0 1px 4px 0px rgba(0, 0, 0, .2);
    background-color:white
}
#tsearch {
    display:none;
}
ul.menuwrapper li.menu a[title="Lost?"] {
    display:none
}
}
