css:
@namespace url(http://www.w3.org/1999/xhtml);
/*
GIGAZINE
http://gigazine.net/
*/

@-moz-document domain("gigazine.net") {
/* template v4 beta update 2008/04/14 r2 */
* {
background-image : none !important;
float : none !important;
text-align : left !important;
letter-spacing : 1px !important;
line-height : 1.5 !important;
min-width : 0 !important;
text-indent : 0 !important;
white-space : normal !important;
list-style-image : none !important;
}
html {
font-size : 100% !important;
background-color : #dcdcd4 !important;
}
body {
font-size : 112.5% !important;
margin : 0 auto !important;
padding : 0 !important;
background-color : #dcdcd4 !important;
color : #000 !important;
width : 40em !important;
max-width : 100% !important;
border : 0 !important;
}
table[width]
, tbody[width]
, thead[width]
, tr[width]
, td[width]
{ width : auto !important; }
noscript
, iframe
, hr
, br[clear="all"]
, #footer
, #banner
, .footer
, .banner
, img[width="1"]
, img[width="0"]
, img[src^="http://ad"]
, *[id^="ad"]
, *[class^="ad"]
, *[id*="calendar"]
, *[class*="calendar"]
, *[id*="track"]
, *[class*="track"]
{ display : none !important; }
u { text-decoration : none !important; }
a { text-decoration : underline !important; }
font[size] { font-size : 100% !important; }
table[width] { max-width : 40em !important; }
/* /template */

/* destroy div layout */
#pagewidth
, #pagewidth div {
font-size : 100% !important;
position : static !important;
background-color : transparent !important;
width : auto !important;
height : auto !important;
border : 0 !important;
margin : 0 !important;
padding : 0 !important;
}
#pagewidth * {
font-size : 100% !important;
position : static !important;
background-color : transparent !important;
width : auto !important;
height : auto !important;
color : #000 !important;
}
#pagewidth a:link { color : #0000ee !important; }
#pagewidth a:visited { color : #551a8b !important; }
#pagewidth a:hover { color : #ee0000 !important; }
#pagewidth a:hover { background-color : transparent !important; }
/* /destroy div layout */

#rightcol
, #leftcol
, a[href="http://gigazine.net/"] img
, .blogtitle table tbody tr > th:last-child
, .content > noscript + br
, .content > noscript + br + br
{ display : none !important; }

a[href="http://gigazine.net/"]:before {
content : "GIGAZINE";
}

}
