css:
@-moz-document domain("amazon.de") {
#submit\.buy-now-announce {
    font-size: 0;
}

#submit\.buy-now-announce::after {
    content: 'Rausballern';
    font-size: 13px;
}


#a-autoid-0-announce {
    font-size: 0;
}

#a-autoid-0-announce::after {
    content: 'Rausballern';
    font-size: 13px;
}

}

@-moz-document domain("youtube.com") {
ytd-commentbox ytd-button-renderer#submit-button yt-formatted-string {
    display: none;
}

ytd-commentbox ytd-button-renderer#submit-button tp-yt-paper-button#button::after {
    content: 'Rausballern';
}

}

@-moz-document domain("twitch.tv") {
[data-a-target='chat-send-button'] > div > div {
    display: none !important;
}

[data-a-target='chat-send-button'] > div::after {
    content: 'Rausballern';
}

}

@-moz-document domain("ebay.de") {
a.btn-prim:not([data-action]) {
    font-size: 0 !important;
}

a.btn-prim:not([data-action])::after {
    font-size: 16px !important;
    content: 'Rausballern';
}

}

@-moz-document domain("twitter.com") {
div[data-testid='tweetButtonInline'] span > span, div[data-testid='tweetButton'] span > span {
    display: none;
}

div[data-testid='tweetButtonInline'] span::after, div[data-testid='tweetButton'] span::after {
    content: 'Rausballern';
    font-size: 15px;
}

}
