css:
@-moz-document domain("mangadex.org") {
.theme--light.v-application { background: #222 !important; color: #eee !important; }
.theme--light.v-navigation-drawer { background-color: #333 !important; }
.theme--light.v-list-item:not(.v-list-item--active):not(.v-list-item--disabled) { color: #eee !important; }
.v-navigation-drawer__content { background-color: #333 !important; }
.v-toolbar__content, .v-toolbar__extension { background-color: #222 !important; }
.theme--light.v-card { background-color: #444 !important; color: #eee !important; }
.theme--light.v-text-field--solo > .v-input__control > .v-input__slot { background: #555 !important; }
.theme--light.v-input, .theme--light.v-input input, .theme--light.v-input textarea { color: #fff !important; }
div.col-6:nth-child(1) > div:nth-child(1), div.col-6:nth-child(2) > div:nth-child(1) { background-color: #333 !important; }
.theme--light.v-footer { background-color: #222; color: #eee; }
.theme--light.v-btn.v-btn--has-bg { background-color: #333; }
.theme--light.v-btn { color: #eee; }
.simple-tag, .small-btn { background: #333 !important; }
.readmore-link { background: linear-gradient(180deg, hsla(0, 0%, 100%, 0) 0, #222 70%, #222) !important; }
.theme--light.v-list { background: #333; }
.list__container { background-color: #333 !important; }
.avatar__subtext { background-color: #222 !important; }
.v-application .pa-4 { background-color: #333 !important; }
.v-application .pl-1 { background-color: #444 !important; }
.v-application .pa-3 { background-color: #333 !important; color: #eee; }
.theme--light.v-text-field--filled > .v-input__control > .v-input__slot { background: #444; color: #eee; }
.theme--light.v-label { color: #eee; }
.list__item:hover { background-color: #666 !important; }
.box__side { background-color: #333 !important; }
.box__side:hover { background-color: #444 !important; }
.box { background-color: #333 !important; }
.menu { background-color: #333 !important; }
.menu__box, .menu__box-side { background-color: #444 !important; }
.theme--light.v-select .v-select__selections { color: #eee; }
.toggle { background-color: #444 !important; }
.toggle:hover { background-color: #555 !important; }
.reader__menu { background-color: #444 !important; }
.theme--light.v-messages { color: #bbb; }
.theme--light.v-icon { color: #eee; }
.group__menu { background-color: #333 !important; }
.chapter__container { background: #444 !important; }
.group__status { background-color: #333 !important; }
.status__box { background-color: #333 !important; }
.group__actions { background-color: #333 !important; }
.v-application .py-2 { background-color: #333 !important; }
.group__stats .stats__label { color: #eee !important; }
.swiper-pagination-bullet:not(.swiper-pagination-bullet-active) { background: #fff }
.v-toolbar__title { color: #eee; }
.v-application .grey.lighten-4 { background-color: #222 !important; border-color: #222 !important; }
.v-application .py-2 { background-color: #222 !important; }
.theme--light.v-app-bar.v-toolbar.v-sheet { background-color: #222 !important; }
}
