css:
@-moz-document domain("tgfcer.com") {
:root {
    --border-radius: 8px;
}

.mainbox {
    border: none;
}
.ad_footerbanner, .adsbygoogle {
    display: none;
}
body > .wrap, body > #footer, #footer .wrap {
    min-width: auto;
    max-width: 100%;
}
.t_msgfont ~ div {
    display: none;
}

.postactions{
    line-height: 10px;
    height: 10px;
    font-size: 9px;
    background: none;
}

.profile, .postauthor > ul {
    font-size: smaller;
}
.postauthor cite {
    border: none;
}
.blockcode, .quote {
    box-shadow: 0px 1px 3px #aaa;
    border-radius: var(--border-radius);
    font-size: smaller;
    border: none;
    background: #ddd;
    color: #555;
    padding: 6px;
}
.blockcode h5, .quote h5 {
    border: none;
}

.postinfo {
    font-size: smaller;
}
.defaultpost{
    min-height: auto!important;
}
}
