css:
@namespace url(http://www.w3.org/1999/xhtml);
/*
CodeZine
http://codezine.jp/
*/

@-moz-document domain("codezine.jp") {
/* template v4 2008-05-08 */
* {
background-image : none !important;
float : none !important;
text-align : left !important;
letter-spacing : 1px !important;
line-height : 1.5 !important;
min-width : 0 !important;
text-indent : 0 !important;
white-space : normal !important;
list-style-image : none !important;
}
pre {
white-space : pre !important;
overflow : auto;
}
html {
font-size : 100% !important;
background-color : #e4e2d4 !important;
}
body {
font-size : 112.5% !important;
margin : 0 auto !important;
padding : 0 !important;
background-color : #e4e2d4 !important;
color : #000 !important;
width : 40em !important;
max-width : 100% !important;
border : 0 !important;
}
table[width]
, tbody[width]
, thead[width]
, tr[width]
, td[width]
{ width : auto !important; }
noscript
, iframe
, hr
, br[clear="all"]
, #footer
, #banner
, .footer
, .banner
, img[width="1"]
, img[width="0"]
, img[src^="http://ad"]
, *[id^="ad"]
, *[class^="ad"]
, *[id*="calendar"]
, *[class*="calendar"]
, *[id*="track"]
, *[class*="track"]
{ display : none !important; }
u { text-decoration : none !important; }
a { text-decoration : underline !important; }
font[size] { font-size : 100% !important; }
table[width] { max-width : 40em !important; }
/* /template */

/* destroy div layout */
.article
, .article div {
font-size : 100% !important;
position : static !important;
background-color : transparent !important;
width : auto !important;
height : auto !important;
border : 0 !important;
margin : 0 !important;
padding : 0 !important;
}
.article * {
font-size : 100% !important;
position : static !important;
background-color : transparent !important;
width : auto !important;
height : auto !important;
color : #000 !important;
}
.article a:link { color : #0000ee !important; }
.article a:visited { color : #551a8b !important; }
.article a:hover { color : #ee0000 !important; }
.article a:hover { background-color : transparent !important; }
/* /destroy div layout */

#shoeisha_gnavi
, #_ctl1__ctl1_pnlHeadSuper
, #_ctl0__ctl2_pnlHeadSuper
, #_ctl1_dfForm_tdLeftPane
, #_ctl1_dfForm_tdRightPane
, #_ctl1_dfForm_pnlAttention
, #_ctl1__ctl1_pnlHeaderLogin
, #_ctl1_dfForm_pnlCopyright
, #_ctl1_dfForm_pnlRating
, #_ctl1_dfForm_tdTitle img
, #_ctl1_dfForm_pnlPrint img
, #_ctl1_dfForm__ctl26_dlArticleList img
, #cate
, .gm2
, .article_info
, td[width="4"]
, td[width="200"] a img
, .td_top > div:first-child
, .td_top > br
, #searchbox .searchiconword
, .pg_bar_next a img
, .pg_bar_prev a img
{ display : none !important; }

.article td
{ padding : 0 !important; }

#searchbox
, #searchbox form
, .article td
{ margin : 0 !important; }

#searchbox
{ width : auto !important; }

#searchbox .searchsubmit
, #searchbox .searchicon
{ display : inline !important; }

#searchbox
{ margin-left : 1em !important; }

td[width="200"] a:before
{ content : "http://codezine.jp/" }

.pg_bar_next a:before
{ content : "æ¬¡ã®ãƒšãƒ¼ã‚¸ã¸"; }

.pg_bar_prev a:before
{ content : "å‰ã®ãƒšãƒ¼ã‚¸ã¸"; }

}
