@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.linux.org.ru") {
a{
color:#729fcf    !important}

a{
text-decoration: none   !important}

a:visited{
color:#babdb7  !important}

div[class=sign]{
position:relative; left: 0; text-align:justify  !important  }

div[class=sign] a, a:visited{
text-decoration: none !important}

div[class=sign] a{
color:#8ae234 !important}


}

@-moz-document domain("www.linux.org.ru"), url-prefix('http://www.linux.org.ru/group.jsp?group=8404'){

div[class=nav]{visibility:hidden !important}

div[class=forum]{position:relative;top:-88pt !important}

}
