css:
/* i really want this to be global */
/* Vivaldi - Hide panel borderline V5 */
#panels-container
{
  box-shadow: none !important;
}
.panelbtn.uifocusstop.active
{
  border: none !important;
}
.left#panels-container.overlay .panel-group
{
  box-shadow: none !important;
}
.right#panels-container.overlay .panel-group
{
  box-shadow: none !important;
}
.left #switch .addwebpanel-wrapper > button:hover:not(.active),
.left #switch > button:hover:not(.active),
.right #switch .addwebpanel-wrapper > button:hover:not(.active),
.right #switch > button:hover:not(.active)
{
  box-shadow: none !important;
}
#panels input, #panels select
{
  box-shadow: none !important;
}
#panels button, #panels input, #panels select, #panels textarea
{
  border: none !important;
}
.sortselector
{
  border: none !important;
}
#panels .SlideBar--FullWidth.alternate:after
{
  display: none !important;
}
.NotesEditor-Editor-Background:after
{
  border: none !important;
  box-shadow: none !important;
}
.addwebpanel-wrapper .addwebpanelcallout
{
  box-shadow: none !important;
}
.addwebpanel-wrapper .addwebpanelcallout:before
{
  border: none !important;
}
#panels .webpanel-stack .webpanel .webpanel-header
{
  border: none !important;
}
