css:
@-moz-document url-prefix("https://www.roblox.com/games/"), url-prefix("https://web.roblox.com/games/") {
.light-theme .btn-common-play-game-lg, .dark-theme .btn-common-play-game-lg {
    border-color: #007001;
    background: url(http://www.roblox.com/images/Buttons/StyleGuide/bg-lg-green-play.png) no-repeat left top;
    height: 50px;
    width: 118px!important;
    border-radius: 0px;
    font-family: 'Arial',sans-serif;
    font-weight: bold;
}
.light-theme .btn-common-play-game-lg:hover, .dark-theme .btn-common-play-game-lg:hover {
    background-color: transparent;
    background-position: left -48px;
    border-color: #007001;
}
.game-main-content .game-play-buttons .btn-common-play-game-lg {
    min-width: 0px;
    padding: 0px;
}
.icon-common-play::before {
    content: "Play";
    font-size: 23px;
    text-shadow: 0px -1px #013401;
    letter-spacing: -1px;
}
.icon-common-play {
    background-image: none!important;
    margin-left: 36px!important;
    margin-top: 2px!important;
}
}
