css:
@-moz-document domain("twitter.com")
{
div[data-testid="sidebarColumn"] { display: none; }
div[data-testid="retweet"] { display: none; }

div[data-testid="like"] span,
div[data-testid="unlike"] span,
div[data-testid="reply"] span
{
    display: none;
}

div[aria-label="Pause"] + div span,
div[aria-label="Play"] + div span
{ display: none; }

a[aria-label="Lists"],
a[aria-label="Bookmarks"],
a[aria-label="Search and explore"] {
    display: none;
}

a[href*="/likes"],
a[href*="/retweets"]
{ display: none; }

a[href*="followers"]:nth-child(1),
a[href*="following"]:nth-child(1)
{ display: none; }
  
}
